// Obtener todos los datos de entrada
const datosEntrada = $("Recupera todos los mensajes").all();

// Filtrar solo los objetos que contienen un mensaje, descartando estadísticas
const mensajes = datosEntrada.filter(item => item.json.message_id);

// Función para convertir la fecha a formato Colombia (UTC-5) en formato de 12 horas
function convertirHoraColombia(fechaUTC) {
    if (!fechaUTC) return "[Hora no encontrada]";
    let fecha = new Date(fechaUTC);
    fecha.setHours(fecha.getHours()); // Convertir a UTC-5
    return fecha.toLocaleTimeString('es-CO', { hour: '2-digit', minute: '2-digit', hour12: true });
}

// Función para dar formato de fecha en español sin hora
function formatearFecha(fechaUTC) {
    if (!fechaUTC) return "[Fecha no encontrada]";
    let fecha = new Date(fechaUTC);
    return fecha.toLocaleDateString('es-CO', { year: 'numeric', month: 'long', day: 'numeric' });
}

// Estructurar los mensajes en un formato claro para el agente IA
const mensajesEstructurados = mensajes.map((mensaje, index) => {
    let fecha = mensaje.json.fecha ? formatearFecha(mensaje.json.fecha) : "[Fecha no encontrada]";
    let hora = mensaje.json.fecha ? convertirHoraColombia(mensaje.json.fecha) : "[Hora no encontrada]";
    let contenido = mensaje.json.contenido || "[Sin contenido]";

    // Determinar si el mensaje es del usuario o del agente IA
    let remitente = mensaje.json.chat_id === 'agenteAI' ? 'El Agente ChatBot IA dice:' : 'El usuario dice:';

    return `Mensaje ${index + 1} enviado el ${fecha} a las ${hora}.\n${remitente} "${contenido}".`;
});

// Unir todos los mensajes en un solo texto estructurado
const historialMensajes = mensajesEstructurados.join('\n\n');

// Retornar los datos estructurados
return [{ historialMensajes }];

// Obtener todos los elementos del historial de citas
const historialCitas = $input.all();

// Verificar si hay datos
if (!historialCitas || historialCitas.length === 0) {
  return [{ historialCitasFormateado: "No hay historial de citas disponible" }];
}

// Función para obtener valor seguro
function getValorSeguro(objeto, propiedad) {
  try {
    // Intenta acceder directamente
    if (objeto && objeto[propiedad] !== undefined && objeto[propiedad] !== null) {
      return objeto[propiedad];
    }

    // Intenta acceder a través de json
    if (objeto && objeto.json && objeto.json[propiedad] !== undefined && objeto.json[propiedad] !== null) {
      return objeto.json[propiedad];
    }

    return 'N/A';
  } catch (error) {
    return 'N/A';
  }
}

// Función para formatear fecha
function formatearFecha(fechaStr) {
  if (!fechaStr || fechaStr === 'N/A') return 'N/A';

  try {
    const fecha = new Date(fechaStr);
    return fecha.toLocaleDateString('es-CO', { year: 'numeric', month: 'long', day: 'numeric' });
  } catch (error) {
    return fechaStr;
  }
}

// Estructurar las citas en un formato claro
const citasEstructuradas = historialCitas.map((cita, index) => {
  // Obtener valores de forma segura
  const id = getValorSeguro(cita, 'id');
  const chatId = getValorSeguro(cita, 'chat_id');
  const nombre = getValorSeguro(cita, 'nombre');
  const ordenCompra = getValorSeguro(cita, 'orden_compra');
  const peso = getValorSeguro(cita, 'peso');
  const unidades = getValorSeguro(cita, 'unidades');
  const fechaCita = formatearFecha(getValorSeguro(cita, 'fecha_cita'));
  const estado = getValorSeguro(cita, 'estado');
  const fechaCreacion = formatearFecha(getValorSeguro(cita, 'fecha_creacion'));
  const chatHistorial = getValorSeguro(cita, 'chat_historial');

  // Crear texto estructurado para esta cita
  let citaTexto = `CITA #${index + 1}\n`;
  citaTexto += `ID: ${id}\n`;
  citaTexto += `Chat ID: ${chatId}\n`;
  citaTexto += `Nombre: ${nombre}\n`;
  citaTexto += `Orden de compra: ${ordenCompra}\n`;
  citaTexto += `Peso: ${peso}\n`;
  citaTexto += `Unidades: ${unidades}\n`;
  citaTexto += `Fecha de cita: ${fechaCita}\n`;
  citaTexto += `Estado: ${estado}\n`;
  citaTexto += `Fecha de creación: ${fechaCreacion}\n`;

  // Agregar historial de chat si existe
  if (chatHistorial && chatHistorial !== 'N/A') {
    citaTexto += `Chat: ${chatHistorial}\n`;
  }

  return citaTexto;
});

// Unir todas las citas en un solo texto estructurado
const historialCitasFormateado = citasEstructuradas.join('\n\n');

// Retornar un objeto simple con el texto formateado
return [{ historialCitasFormateado }];

// ====================================================================
// UNIFICACIÓN Y CONSISTENCIA DE DATOS - VERSIÓN CORREGIDA
// ====================================================================

const output = {};

// Inicializar estructuras con valores por defecto
output.infoMensajecita = { 
    chat_id: "", 
    historialCitas: "" 
};

output.infoMensaje = { 
    chat_id: null, 
    historialMensajes: "", 
    count: null, 
    tiempoUltimoMensaje: null, 
    mensajeActual: "", 
    fechaMensajeActual: "", 
    horaMensajeActual: "" 
};

// ====================================================================
// SOLUCIÓN 1: OBTENER DATOS DEL NODO "Organiza el Mensaje"
// ====================================================================
try {
    const nodoMensaje = $node["Organiza el Mensaje"];

    if (nodoMensaje && nodoMensaje.json) {
        console.log('Datos disponibles en Organiza el Mensaje:', Object.keys(nodoMensaje.json));

        // Extraer datos usando las claves correctas con fallbacks múltiples
        output.infoMensaje = {
            chat_id: nodoMensaje.json["infoMensaje.chat_id"] || 
                     $('Parametrización').first().json["message"]["chat"]["id"] || 
                     null,
            historialMensajes: nodoMensaje.json["infoMensaje.historialMensajes"] || 
                              $('Organización y estructuracion de mensajes').first().json.historialMensajes || 
                              "",
            count: nodoMensaje.json["infoMensaje.count"] || 
                   $('If (Salida 2 - Seguir)').first().json.mensajeCount || 
                   null,
            tiempoUltimoMensaje: nodoMensaje.json["infoMensaje.tiempoUltimoMensaje"] || 
                                $('If (Salida 2 - Seguir)').first().json.tiempoUltimoMensaje || 
                                null,
            mensajeActual: nodoMensaje.json["infoMensaje.mensajeActual"] || 
                          $('Mensaje Entrada').first().json.body.data.message.conversation || 
                          "",
            fechaMensajeActual: nodoMensaje.json["infoMensaje.fechaMensajeActual"] || 
                               ($('Parametrización').first().json.message.date ? 
                                $('Parametrización').first().json.message.date.split('T')[0] : "") ||
                               "",
            horaMensajeActual: nodoMensaje.json["infoMensaje.horaMensajeActual"] || 
                              ($('Parametrización').first().json.message.date ? 
                               new Date($('Parametrización').first().json.message.date)
                                 .toLocaleTimeString("es-CO", { 
                                   hour: '2-digit', 
                                   minute: '2-digit', 
                                   hour12: true,
                                   timeZone: 'UTC' 
                                 }) : "") ||
                              ""
        };
    }
} catch (error) {
    console.log('Error al obtener datos del nodo "Organiza el Mensaje":', error.message);

    // FALLBACK DIRECTO: Si el nodo falla, tomar datos directamente de fuentes originales
    try {
        output.infoMensaje = {
            chat_id: $('Parametrización').first().json.message.chat.id || null,
            historialMensajes: $('Organización y estructuracion de mensajes').first().json.historialMensajes || "",
            count: $('If (Salida 2 - Seguir)').first().json.mensajeCount || null,
            tiempoUltimoMensaje: $('If (Salida 2 - Seguir)').first().json.tiempoUltimoMensaje || null,
            mensajeActual: $('Mensaje Entrada').first().json.body.data.message.conversation || "",
            fechaMensajeActual: $('Parametrización').first().json.message.date.split('T')[0] || "",
            horaMensajeActual: new Date($('Parametrización').first().json.message.date)
                .toLocaleTimeString("es-CO", { 
                    hour: '2-digit', 
                    minute: '2-digit', 
                    hour12: true,
                    timeZone: 'UTC' 
                }) || ""
        };
    } catch (fallbackError) {
        console.log('Error en fallback directo para infoMensaje:', fallbackError.message);
    }
}

// ====================================================================
// SOLUCIÓN 2: OBTENER DATOS DEL NODO "Organiza el historial de citas"
// ====================================================================
try {
    const nodoCitas = $node["Organiza el historial de citas"];

    if (nodoCitas && nodoCitas.json) {
        console.log('Datos disponibles en Organiza el historial de citas:', Object.keys(nodoCitas.json));

        // Extraer datos usando las claves correctas con fallbacks múltiples
        output.infoMensajecita = {
            chat_id: nodoCitas.json["infoMensajecita.chat_id"] || 
                     $('Parametrización').first().json.message.chat.id || 
                     "",
            historialCitas: nodoCitas.json["infoMensajecita.historialCitas"] || 
                           $('Organización y estructuracion historial citas').first().json.historialCitasFormateado || 
                           ""
        };
    }
} catch (error) {
    console.log('Error al obtener datos del nodo "Organiza el historial de citas":', error.message);

    // FALLBACK DIRECTO: Si el nodo falla, tomar datos directamente
    try {
        output.infoMensajecita = {
            chat_id: $('Parametrización').first().json.message.chat.id || "",
            historialCitas: $('Organización y estructuracion historial citas').first().json.historialCitasFormateado || ""
        };
    } catch (fallbackError) {
        console.log('Error en fallback directo para infoMensajecita:', fallbackError.message);
    }
}

// ====================================================================
// GENERAR SESSION ID Y DATOS COMPLEMENTARIOS
// ====================================================================
// Priorizar chat_id de infoMensajecita, luego infoMensaje
output.sessionId = output.infoMensajecita.chat_id || output.infoMensaje.chat_id || null;

// Calcular la semana actual
const fechaActual = new Date();
const inicioAno = new Date(fechaActual.getFullYear(), 0, 1);
const diff = fechaActual - inicioAno;
const diasDesdeInicio = Math.floor(diff / (1000 * 60 * 60 * 24));
const numeroSemana = Math.ceil((diasDesdeInicio + inicioAno.getDay()) / 7);
output.hojaSeleccionada = `SEMANA ${numeroSemana}`;

// ====================================================================
// LOGGING PARA DEBUGGING
// ====================================================================
console.log('=== DEBUGGING UNIFICACIÓN DE DATOS ===');
console.log('sessionId:', output.sessionId);
console.log('hojaSeleccionada:', output.hojaSeleccionada);
console.log('infoMensaje.chat_id:', output.infoMensaje.chat_id);
console.log('infoMensaje.historialMensajes length:', output.infoMensaje.historialMensajes.length);
console.log('infoMensajecita.chat_id:', output.infoMensajecita.chat_id);
console.log('infoMensajecita.historialCitas length:', output.infoMensajecita.historialCitas.length);
console.log('======================================');

// Retornar el resultado unificado
return [{ json: output }];

// Obtener los datos del Excel
const excelData = $input.all();
const hojaSeleccionada = $('Unificación y consistencia de datos').first().json.hojaSeleccionada;

// Obtener hora local en Bogotá
const ahora = new Date(new Date().toLocaleString("en-US", { timeZone: "America/Bogota" }));
const diaSemanaActual = ahora.getDay(); // 0=domingo, 1=lunes, ..., 5=viernes
const horaActual = ahora.getHours();
const minutosActual = ahora.getMinutes();
// Determinar si es después de las 16:30
const pasoLimiteDia = horaActual > 16 || (horaActual === 16 && minutosActual >= 30);

// Definir estructura de la semana y los días
const diasSemana = ['lunes', 'martes', 'miércoles', 'jueves', 'viernes'];

// Mapeo para saber qué días incluir si hoy es x día y pasó el límite
/**
 * Dado el día de la semana actual (0=domingo ... 6=sábado),
 * devuelve qué días de la semana actual deben incluirse según si pasó el límite.
 */
function diasValidosSemanaActual(diaSemanaActual, pasoLimite) {
  const mapa = {
    1: ['martes', 'miércoles', 'jueves', 'viernes'],   // lunes
    2: ['miércoles', 'jueves', 'viernes'],             // martes
    3: ['jueves', 'viernes'],                          // miércoles
    4: ['viernes'],                                     // jueves
    5: []                                               // viernes (muy tarde para sábado)
  };
  if (diaSemanaActual < 1 || diaSemanaActual > 5) {
    return []; // sábado o domingo no se agendan en semana actual
  }
  if (!pasoLimite) {
    // aún está antes de las 4:30 p.m., se puede incluir el día siguiente
    const siguiente = diasSemana[diaSemanaActual];
    return [siguiente, ...mapa[diaSemanaActual]];
  }
  return mapa[diaSemanaActual];
}

// Objeto para almacenar la malla estructurada
const mallaEstructurada = {
  titulo: "",
  año: "",
  semana: "",
  rangoDias: "",
  franjas: [],
  dias: {}
};

// Tabla de meses y días. Ajusta si necesitas contemplar bisiestos, etc.
const meses = {
  "enero": 1,
  "febrero": 2,
  "marzo": 3,
  "abril": 4,
  "mayo": 5,
  "junio": 6,
  "julio": 7,
  "agosto": 8,
  "septiembre": 9,
  "octubre": 10,
  "noviembre": 11,
  "diciembre": 12
};

const nombreMes = {
  1: "enero",
  2: "febrero",
  3: "marzo",
  4: "abril",
  5: "mayo",
  6: "junio",
  7: "julio",
  8: "agosto",
  9: "septiembre",
  10: "octubre",
  11: "noviembre",
  12: "diciembre"
};

/**
 * Función para determinar si un año es bisiesto
 * Un año es bisiesto si es divisible por 4, excepto aquellos divisibles por 100 
 * que no son divisibles por 400
 */
function esBisiesto(year) {
  return (year % 4 === 0 && year % 100 !== 0) || (year % 400 === 0);
}

/**
 * Función para obtener el número de días en un mes, considerando años bisiestos
 */
function getDiasMes(mes, año) {
  const diasPorMes = {
    1: 31, // enero
    2: esBisiesto(año) ? 29 : 28, // febrero - ajustado para año bisiesto
    3: 31, // marzo
    4: 30, // abril
    5: 31, // mayo
    6: 30, // junio
    7: 31, // julio
    8: 31, // agosto
    9: 30, // septiembre
    10: 31, // octubre
    11: 30, // noviembre
    12: 31  // diciembre
  };

  return diasPorMes[mes];
}

/**
 * Función para eliminar acentos y pasar a minúsculas (para buscar palabras clave sin importar tildes ni mayúsculas).
 */
function normalizarTexto(texto) {
  if (typeof texto !== 'string') return "";
  // Normaliza (NFD) y elimina diacríticos, luego pasa a minúsculas.
  return texto
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, "") // quita acentos
    .toLowerCase();
}

// (Malla semana actual)

// 1. REEMPLAZAR la función determinarEstadoOcupado con:
function determinarEstadoYValores(proveedor, ordenCompra, peso, categoria, unidades) {
  const conjunto = `${proveedor} ${ordenCompra} ${peso} ${categoria} ${unidades}`;
  const texto = normalizarTexto(conjunto);

  // Si cualquier campo contiene "fuera de servicio"
  if (texto.includes("fuera de servicio")) {
    return {
      estado: "fuera de servicio",
      proveedor: "fuera de servicio",
      ordenCompra: "fuera de servicio", 
      peso: "fuera de servicio",
      categoria: "fuera de servicio",
      unidades: "fuera de servicio"
    };
  }

  // Si cualquier campo contiene "cancelado"
  if (texto.includes("cancelado")) {
    return {
      estado: "cancelado",
      proveedor: "cancelado",
      ordenCompra: "cancelado",
      peso: "cancelado", 
      categoria: "cancelado",
      unidades: "cancelado"
    };
  }

  // Si cualquier campo contiene "reagendado"
  if (texto.includes("reagendado")) {
    return {
      estado: "reagendado",
      proveedor: "reagendado",
      ordenCompra: "reagendado",
      peso: "reagendado",
      categoria: "reagendado", 
      unidades: "reagendado"
    };
  }

  // Estado ocupado normal - mantener valores originales
  return {
    estado: "ocupado",
    proveedor: proveedor,
    ordenCompra: ordenCompra,
    peso: peso,
    categoria: categoria,
    unidades: unidades
  };
}

// -------------------------------------------------------------
// 1) PROCESAR DATOS (Cabecera, Horas, Muelles, etc.)
// -------------------------------------------------------------
function procesarDatos(excelData) {
  const datos = excelData.map(item => item.json);

  // A) Cabecera (Título, Año, Rango)
  datos.forEach((dato, index) => {
    // Buscar título
    if (dato.__EMPTY_2 === "MALLA DE RECIBO CEDI KONFIE IA") {
      mallaEstructurada.titulo = dato.__EMPTY_2;

      // En la siguiente fila puede estar año, semana, rango
      if (index + 1 < datos.length) {
        const datoSiguiente = datos[index + 1];
        if (datoSiguiente) {
          // Año
          if (datoSiguiente.__EMPTY_2 && !isNaN(datoSiguiente.__EMPTY_2)) {
            mallaEstructurada.año = datoSiguiente.__EMPTY_2;
          }
          // Semana
          Object.entries(datoSiguiente).forEach(([k, v]) => {
            if (typeof v === 'string' && v.includes("SEMANA")) {
              mallaEstructurada.semana = v;
            }
          });
          // Rango: p.ej. "LUNES 31 AL VIERNES 4 DE ABRIL"
          Object.entries(datoSiguiente).forEach(([k, v]) => {
            if (typeof v === 'string' && v.includes("LUNES") && v.includes("VIERNES")) {
              mallaEstructurada.rangoDias = v;
            }
          });
        }
      }
    }
  });

  // B) Detectar franjas horarias (.__EMPTY = fracción de día)
  const franjasHorarias = [];
  datos.forEach(dato => {
    if (
      dato.__EMPTY !== undefined &&
      typeof dato.__EMPTY === 'number' &&
      dato.__EMPTY > 0 &&
      dato.__EMPTY < 1
    ) {
      const totalMin = Math.round(dato.__EMPTY * 24 * 60);
      const hh = Math.floor(totalMin / 60);
      const mm = totalMin % 60;

      let periodo = "AM";
      let hora12 = hh;
      if (hh >= 12) {
        periodo = "PM";
        hora12 = (hh === 12 ? 12 : hh - 12);
      }
      if (hh === 0) {
        hora12 = 12;
      }

      const horaStr = `${hora12}:${String(mm).padStart(2, '0')} ${periodo}`;
      franjasHorarias.push({
        hora: horaStr,
        indice: datos.indexOf(dato) // fila base
      });
    }
  });
  franjasHorarias.sort((a, b) => a.indice - b.indice);
  mallaEstructurada.franjas = franjasHorarias;

  // C) Detectar muelles (col)
  const muellesInfo = {};
  datos.forEach(dato => {
    Object.entries(dato).forEach(([key, value]) => {
      if (value === "MUELLE 01" || value === "MUELLE 02") {
        const col = parseInt(key.replace("__EMPTY_", ""), 10);
        const muelleKey = (value === "MUELLE 01") ? 'muelle1' : 'muelle2';
        if (!muellesInfo[muelleKey]) {
          muellesInfo[muelleKey] = [];
        }
        muellesInfo[muelleKey].push(col);
      }
    });
  });
  if (muellesInfo.muelle1) muellesInfo.muelle1.sort((a, b) => a - b);
  if (muellesInfo.muelle2) muellesInfo.muelle2.sort((a, b) => a - b);

  // D) Generar los 5 días (lunes-viernes) con su fecha
  parsearRangoDias();

  // E) Extraer Citas
  extraerCitas(datos, muellesInfo);

  return mallaEstructurada;
}

/**
 * Parsea la cadena "LUNES 31 AL VIERNES 4 DE ABRIL"
 * (o "LUNES 31 AL VIERNES 04 DE ABRIL") y maneja el cruce de mes.
 * Ej.: si dayStart=31 y dayEnd=4, la 1ª fecha es 31 de MARZO,
 * luego 1,2,3,4 de ABRIL, en vez de 32,33, etc.
 */
function parsearRangoDias() {
  const texto = mallaEstructurada.rangoDias;
  // Regex simple: p.ej. "31 AL ... 4 DE ABRIL"
  // Captura: dayStart, dayEnd, mesFin
  // Ojo: si dice "LUNES 31 DE MARZO AL VIERNES 4 DE ABRIL",
  // quedará: dayStart=31, dayEnd=4, month=ABRIL (al final).
  const regex = /(\d+)\s+AL\s+\D+(\d+)\s+DE\s+(\w+)/i;
  const match = regex.exec(texto);

  // Por defecto, creamos 5 días vacíos sin fecha
  diasSemana.forEach(d => {
    mallaEstructurada.dias[d] = {
      muelle1: {},
      muelle2: {}
    };
  });

  if (!match) {
    // Sin coincidencia, dejamos los días sin fecha
    return;
  }

  const dayStart = parseInt(match[1], 10); // 31
  const dayEnd   = parseInt(match[2], 10); // 4
  const finalMonthName = match[3].toLowerCase(); // "abril"

  // Convertimos a número
  const finalMonth = meses[finalMonthName] || 3; // fallback marzo
  const yearNum = parseInt(mallaEstructurada.año, 10) || 2025;

  // Función para obtener la fecha formateada
  function fechaFormateada(d, m, y) {
    return `${d} de ${nombreMes[m]} de ${y}`;
  }

  // Comprobamos cuántos días tiene el mes final, considerando bisiestos
  const diasMesFinal = getDiasMes(finalMonth, yearNum);
  // y del mes previo
  const monthPrev = (finalMonth === 1) ? 12 : finalMonth - 1;
  const yearPrev = (finalMonth === 1) ? yearNum - 1 : yearNum;

  // Arreglo final con 5 fechas
  const fechasDias = [];

  if (dayStart <= dayEnd) {
    // Caso "normal": todo en el mismo mes
    let d = dayStart;
    for (let i = 0; i < 5; i++) {
      fechasDias.push({
        diaSem: diasSemana[i],
        diaNum: d,
        mesNum: finalMonth,
        yearNum: yearNum
      });
      d++;
      if (d > diasMesFinal) {
        d = 1;
        const nextMonth = finalMonth % 12 + 1;
        const nextYear = (nextMonth === 1) ? yearNum + 1 : yearNum;
        fechasDias[fechasDias.length - 1].mesNum = nextMonth;
        fechasDias[fechasDias.length - 1].yearNum = nextYear;
      }
    }
  } else {
    // Caso "cruza de mes": dayStart > dayEnd
    let d = dayStart;
    let m = monthPrev;
    let y = yearPrev;

    for (let i = 0; i < 5; i++) {
      fechasDias.push({
        diaSem: diasSemana[i],
        diaNum: d,
        mesNum: m,
        yearNum: y
      });
      d++;
      if (d > getDiasMes(m, y)) {
        d = 1;
        m = m % 12 + 1;
        if (m === 1) {
          y++;
        }
      }
    }
  }

  // Ahora volcamos esas 5 fechas en la estructura
  fechasDias.forEach(fd => {
    const { diaSem, diaNum, mesNum, yearNum } = fd;
    mallaEstructurada.dias[diaSem] = {
      fechaDia: fechaFormateada(diaNum, mesNum, yearNum),
      muelle1: {},
      muelle2: {}
    };
  });

  // Inicializamos las franjas a 'disponible'
  if (mallaEstructurada.franjas.length > 0) {
    diasSemana.forEach(d => {
      mallaEstructurada.franjas.forEach(f => {
        mallaEstructurada.dias[d].muelle1[f.hora] = {
          estado: "disponible",
          proveedor: "",
          ordenCompra: "",
          peso: "",
          categoria: "",
          unidades: ""
        };
        mallaEstructurada.dias[d].muelle2[f.hora] = {
          estado: "disponible",
          proveedor: "",
          ordenCompra: "",
          peso: "",
          categoria: "",
          unidades: ""
        };
      });
    });
  }
}

/**
 * Función auxiliar: decide a qué día pertenece una columna,
 * corrigiendo 2 columnas de offset (porque 'Extract from XLSX'
 * deja todo desplazado).
 */
function getDayForColumn(colNumber) {
  // Ajuste de offset +2
  const realCol = colNumber + 2;

  // Rangos:
  //  Lunes:      3..8
  //  Martes:     9..14
  //  Miércoles: 15..20
  //  Jueves:    21..26
  //  Viernes:   27..32
  if (realCol >= 3 && realCol <= 8) {
    return "lunes";
  }
  if (realCol >= 9 && realCol <= 14) {
    return "martes";
  }
  if (realCol >= 15 && realCol <= 20) {
    return "miércoles";
  }
  if (realCol >= 21 && realCol <= 26) {
    return "jueves";
  }
  if (realCol >= 27 && realCol <= 32) {
    return "viernes";
  }
  // Si nada coincide, devolvemos null
  return null;
}

/**
 * Extraer las citas para cada franja:
 * - Para cada franja, miramos 3 filas:
 *    - Fila base => proveedor
 *    - Fila base+1 => orden
 *    - Fila base+2 => peso, categoría, unidades
 */
function extraerCitas(datos, muellesInfo) {
  mallaEstructurada.franjas.forEach(franja => {
    const indiceBase = franja.indice;
    if (indiceBase < 0 || indiceBase + 2 >= datos.length) {
      return; // no hay suficientes filas
    }

    // Recorremos muelle1, muelle2 y sus columnas
    const asignarCita = (muelleKey, cols) => {
      cols.forEach(col => {
        const provKey = `__EMPTY_${col}`;
        const filaProv = datos[indiceBase];

        // Si en esta fila hay un proveedor o algún texto
        if (
          filaProv &&
          typeof filaProv[provKey] === 'string' &&
          filaProv[provKey].length > 2
        ) {
          // Determinar el día real
          const diaOk = getDayForColumn(col);
          if (!diaOk || !mallaEstructurada.dias[diaOk]) {
            return; // si no coincide con ninguno, saltamos
          }

          // Recuperar valores para proveedor, ordenCompra y demás
          const filaOrden = datos[indiceBase + 1];
          const filaDet = datos[indiceBase + 2];

          const proveedor = filaProveedor && filaProveedor[provKey] ? filaProveedor[provKey] : "";
          const ordenCompra = filaOrden && filaOrden[provKey] ? filaOrden[provKey] : "";
          const peso = filaDet && filaDet[provKey] ? filaDet[provKey] : "";
          const cat = filaDet && filaDet[`__EMPTY_${col + 1}`] ? filaDet[`__EMPTY_${col + 1}`] : "";
          const und = filaDet && filaDet[`__EMPTY_${col + 2}`] ? filaDet[`__EMPTY_${col + 2}`] : "";

          // Obtener estado y valores corregidos
          const resultado = determinarEstadoYValores(proveedor, ordenCompra, peso, cat, und);

          // Asignar TODOS los valores según el resultado
          mallaEstructurada.dias[diaOk][muelleKey][franja.hora].estado = resultado.estado;
          mallaEstructurada.dias[diaOk][muelleKey][franja.hora].proveedor = resultado.proveedor;
          mallaEstructurada.dias[diaOk][muelleKey][franja.hora].ordenCompra = resultado.ordenCompra;
          mallaEstructurada.dias[diaOk][muelleKey][franja.hora].peso = resultado.peso;
          mallaEstructurada.dias[diaOk][muelleKey][franja.hora].categoria = resultado.categoria;
          mallaEstructurada.dias[diaOk][muelleKey][franja.hora].unidades = resultado.unidades;
        }
      });
    };

    if (muellesInfo.muelle1) {
      asignarCita('muelle1', muellesInfo.muelle1);
    }
    if (muellesInfo.muelle2) {
      asignarCita('muelle2', muellesInfo.muelle2);
    }
  });
}

