    const datosProveedor = buscarProveedor(proveedores, proveedor);
    if (!datosProveedor) {
      return { error: `No se encontró el proveedor "${proveedor}" en la base de datos` };
    }

    const unidadesPorHora = datosProveedor.unidadesPorHora;
    if (!unidadesPorHora || unidadesPorHora <= 0) {
      return { error: `El proveedor "${proveedor}" no tiene definidas unidades por hora válidas` };
    }

    /* ────────────────────────── 3. CÁLCULOS BÁSICOS ───────────────────────── */
    const horasNecesarias = Math.ceil(unidades / unidadesPorHora);
    const restriccionHoraSuperada = verificarRestriccionHoraCitaManana(fechaHoraActual, fechaSolicitada);

    /* ────────────────────────── 4. VALIDACIÓN DESCARGA SOBREDIMENSIONADA ─────────── */
    // Determinar qué malla usar para obtener el número de franjas disponibles
    let cantidadFranjasDiarias = 0;

    // Buscar en qué malla está la fecha solicitada para obtener el número de franjas
    const resultadoBusquedaMalla = buscarDiaPorFechaExacta(fechaSolicitada, mallaActual, mallaProxima);
    if (resultadoBusquedaMalla && resultadoBusquedaMalla.malla && resultadoBusquedaMalla.malla.franjas) {
      cantidadFranjasDiarias = resultadoBusquedaMalla.malla.franjas.length;
    } else {
      // Fallback: usar la malla actual si no se puede determinar
      cantidadFranjasDiarias = mallaActual.franjas ? mallaActual.franjas.length : 8;
    }

    // Verificar si la descarga es sobredimensionada
    const descargaSobredimensionada = horasNecesarias > cantidadFranjasDiarias;
    console.log(`Validación descarga sobredimensionada: horasNecesarias=${horasNecesarias}, cantidadFranjasDiarias=${cantidadFranjasDiarias}, sobredimensionada=${descargaSobredimensionada}`);

    /* ────────────────────────── 5. HORA NORMALIZADA ──────────────────────── */
    const horaNormalizada = horaSolicitada.trim();

    /* ────────────────────────── 5. MUELLE POR CATEGORÍA ──────────────────── */
    const muelleAsignado = determinarMuellePorCategoria(categorias, categoria);
    if (!muelleAsignado) {
      return { error: `No se encontró un muelle asignado para la categoría "${categoria}"` };
    }

    /* ────────────────────────── 6. DISPONIBILIDAD / ALTERNATIVAS ─────────── */
    let resultado;

    if (restriccionHoraSuperada) {
      // Caso: contactaron después de las 4:30 p.m. y la cita es para mañana
      const alternativas = buscarAlternativasDesdeDosDiasDespues(
        fechaHoraActual,
        mallaActual,
        mallaProxima,
        muelleAsignado,
        horasNecesarias
      );

      resultado = {
        disponibilidadConfirmada: false,
        fecha:           fechaSolicitada,
        horaInicio:      horaNormalizada,
        horaFin:         calcularHoraFin(fechaSolicitada, horaNormalizada, horasNecesarias),
        muelle:          muelleAsignado,
        duracionHoras:   horasNecesarias,
        restriccionHoraSuperada: true,
        descargaSobredimensionada: descargaSobredimensionada,
        alternativasDisponibles: alternativas.slice(0, 10)
      };
    } else {
      const disponible = verificarDisponibilidadHoraSolicitada(
        fechaSolicitada,
        horaNormalizada,
        muelleAsignado,
        horasNecesarias,
        mallaActual,
        mallaProxima
      );

      if (disponible) {
        resultado = {
          disponibilidadConfirmada: true,
          fecha:           fechaSolicitada,
          horaInicio:      horaNormalizada,
          horaFin:         calcularHoraFin(fechaSolicitada, horaNormalizada, horasNecesarias),
          muelle:          muelleAsignado,
          duracionHoras:   horasNecesarias,
          restriccionHoraSuperada: false,
          descargaSobredimensionada: descargaSobredimensionada,
          alternativasDisponibles: []
        };
      } else {
        const alternativasMismoDia = buscarAlternativasMismoDia(
          fechaSolicitada,
          muelleAsignado,
          horasNecesarias,
          mallaActual,
          mallaProxima
        );

        const todasAlternativas = alternativasMismoDia.length > 0
          ? alternativasMismoDia
          : buscarAlternativasDiasPeriores(
              fechaSolicitada,
              muelleAsignado,
              horasNecesarias,
              mallaActual,
              mallaProxima
            );

        resultado = {
          disponibilidadConfirmada: false,
          fecha:           fechaSolicitada,
          horaInicio:      horaNormalizada,
          horaFin:         calcularHoraFin(fechaSolicitada, horaNormalizada, horasNecesarias),
          muelle:          muelleAsignado,
          duracionHoras:   horasNecesarias,
          restriccionHoraSuperada: false,
          descargaSobredimensionada: descargaSobredimensionada,
          alternativasDisponibles: todasAlternativas
        };
      }
    }

    /* ────────────────────────── 7. DATOS PROVEEDOR ───────────────────────── */
    resultado.datosProveedorEncontrado = {
      nombre:           datosProveedor.proveedor,
      unidadesPorHora:  datosProveedor.unidadesPorHora,
      rotacion:         datosProveedor.rotacion,
      tipoProveedor:    datosProveedor.tipoProveedor
    };

    return { output: resultado };
  } catch (error) {
    console.error("Error en verificación de disponibilidad:", error);
    return { error: `Error en la verificación: ${error.message}` };
  }
}

/* ════════════════════════════════════════════════════════════════════════
 *  FUNCIONES DE APOYO
 * ════════════════════════════════════════════════════════════════════════ */

// (1) Búsqueda de proveedor
function buscarProveedor(listaProveedores, nombreProveedor) {
  if (!Array.isArray(listaProveedores) || listaProveedores.length === 0) return null;
  if (!nombreProveedor) return null;

  const nombreLower = nombreProveedor.toLowerCase();
  const exacta  = listaProveedores.find(p => (p.proveedor || "").toLowerCase() === nombreLower);
  if (exacta) return exacta;

  return listaProveedores.find(p => (p.proveedor || "").toLowerCase().includes(nombreLower));
}

// (2) Normalizador de hora para comparaciones
function limpiarHora(h) {
  return h ? h.trim().replace(/^0+/, "").toUpperCase() : "";
}

// (3) Determinar muelle por categoría
function determinarMuellePorCategoria(categorias, categoriaProducto) {
  if (!Array.isArray(categorias) || categorias.length === 0) return null;
  const catLower = categoriaProducto.toLowerCase();

  const exacta = categorias.find(c => (c.CATEGORIA || "").toLowerCase() === catLower);
  if (exacta) return exacta.MUELLE;

  const parcial = categorias.find(c => (c.CATEGORIA || "").toLowerCase().includes(catLower));
  return parcial ? parcial.MUELLE : null;
}

// (4) Calcular hora de fin (VERSIÓN CORREGIDA)
function calcularHoraFin(fecha, horaInicio, horasNecesarias) {
  // Si falta algún dato, no se puede calcular.
  if (!fecha || !horaInicio || horasNecesarias === undefined) {
    return "Cálculo inválido";
  }

  try {
    // 1. Creamos un objeto de fecha a partir de la fecha de la cita.
    //    Usamos la función que ya tienes para parsear fechas en español.
    const fechaInicio = parsearFechaEspanol(fecha);
    if (isNaN(fechaInicio.getTime())) {
      console.error("Fecha inválida proporcionada a calcularHoraFin:", fecha);
      return "Fecha inválida";
    }

    // 2. Parseamos la hora de inicio y la aplicamos al objeto de fecha.
    const [horaTxt, meridiano] = horaInicio.trim().toUpperCase().split(" ");
    let hora = parseInt(horaTxt.split(":")[0], 10);

    // Convertimos la hora a formato de 24h para poderla establecer correctamente.
    if (meridiano === "PM" && hora < 12) {
      hora += 12;
    }
    if (meridiano === "AM" && hora === 12) {
      hora = 0; // 12 AM es la hora 0.
    }

    // Establecemos la hora en nuestro objeto de fecha.
    fechaInicio.setHours(hora, 0, 0, 0);

    // 3. Añadimos las horas necesarias. El objeto Date manejará el cambio de día automáticamente.
    fechaInicio.setHours(fechaInicio.getHours() + horasNecesarias);

    // 4. Formateamos la nueva fecha/hora de fin al formato "H:00 AM/PM".
    let horaFin24 = fechaInicio.getHours();
    let meridianoFin = horaFin24 >= 12 ? "PM" : "AM";
    let horaFin12 = horaFin24 % 12;

    if (horaFin12 === 0) {
      horaFin12 = 12; // La hora 0 (medianoche) y la hora 12 (mediodía) se muestran como 12.
    }

    return `${horaFin12}:00 ${meridianoFin}`;

  } catch (error) {
    console.error("Error en la nueva función calcularHoraFin:", error);
    return "Error en cálculo";
  }
}

// (5) Restricción 4:30 p.m. → cita mañana (CORREGIDO - ZONA HORARIA)
function verificarRestriccionHoraCitaManana(fechaHoraActual, fechaSolicitada) {
  // 1. Obtener la fecha y hora actual en la zona horaria de Bogotá.
  //    Esto es CRUCIAL para que la comparación de las 4:30 PM funcione correctamente
  //    independientemente de la zona horaria del servidor.
  const ahoraEnBogota = new Date(new Date().toLocaleString("en-US", { timeZone: "America/Bogota" }));

  // 2. Verificar si la hora actual en Bogotá es después de las 4:30 PM.
  const horaBogota = ahoraEnBogota.getHours();
  const minutosBogota = ahoraEnBogota.getMinutes();
  const esDespuesDe430PM = horaBogota > 16 || (horaBogota === 16 && minutosBogota >= 30);

  // 3. Parsear la fecha que el usuario solicitó.
  const fechaSolicitadaObj = parsearFechaEspanol(fechaSolicitada);
  if (!fechaSolicitadaObj || isNaN(fechaSolicitadaObj.getTime())) {
    console.error("Fecha solicitada inválida:", fechaSolicitada);
    return false;
  }

  // 4. Calcular la fecha de "mañana" basándose en la fecha actual de Bogotá.
  const fechaManana = new Date(ahoraEnBogota);
  fechaManana.setDate(fechaManana.getDate() + 1);

  // 5. Comparar si la fecha solicitada es efectivamente "mañana".
  //    Se compara año, mes y día para evitar problemas con horas.
  const esMañana = fechaSolicitadaObj.getFullYear() === fechaManana.getFullYear() &&
                   fechaSolicitadaObj.getMonth() === fechaManana.getMonth() &&
                   fechaSolicitadaObj.getDate() === fechaManana.getDate();

  // 6. La restricción se aplica solo si AMBAS condiciones son verdaderas.
  return esMañana && esDespuesDe430PM;
}


// (6) Parsear fecha "30 de abril de 2025"
function parsearFechaEspanol(fechaTexto) {
  const [dia, , mesTxt, , anio] = fechaTexto.split(" ");
  return new Date(Number(anio), obtenerNumeroMes(mesTxt), Number(dia));
}

// (7) Obtener número de mes
function obtenerNumeroMes(nombreMes) {
  const meses = ["enero","febrero","marzo","abril","mayo","junio","julio","agosto","septiembre","octubre","noviembre","diciembre"];
  return meses.indexOf(nombreMes.toLowerCase());
}

// (7.5) FUNCIÓN: Normalizar fecha flexible - Interpreta CUALQUIER formato de fecha
// ===================== INICIO DE LA SECCIÓN CORREGIDA =====================
function normalizarFechaFlexible(fechaInput) {
  if (!fechaInput) return "";

  let fecha = fechaInput.toString().trim().toLowerCase();

  const mesesMap = {
    "enero": "enero", "ene": "enero", "1": "enero",
    "febrero": "febrero", "feb": "febrero", "2": "febrero", 
    "marzo": "marzo", "mar": "marzo", "3": "marzo",
    "abril": "abril", "abr": "abril", "4": "abril",
    "mayo": "mayo", "may": "mayo", "5": "mayo",
    "junio": "junio", "jun": "junio", "6": "junio",
    "julio": "julio", "jul": "julio", "7": "julio",
    "agosto": "agosto", "ago": "agosto", "8": "agosto",
    "septiembre": "septiembre", "sep": "septiembre", "sept": "septiembre", "9": "septiembre",
    "octubre": "octubre", "oct": "octubre", "10": "octubre",
    "noviembre": "noviembre", "nov": "noviembre", "11": "noviembre",
    "diciembre": "diciembre", "dic": "diciembre", "12": "diciembre"
  };

  let dia, mes, anio;
  const anioActual = new Date().getFullYear();

  console.log(`🔧 Normalizando fecha flexible: "${fechaInput}" → entrada limpia: "${fecha}"`);

  // PATRÓN 1: "05 de agosto de 2025" o "5 de agosto de 2025"
  let match = fecha.match(/(\d{1,2})\s*de\s*(\w+)\s*de\s*(\d{4})/);
  if (match) {
    dia = parseInt(match[1]);
    mes = mesesMap[match[2]] || match[2];
    anio = parseInt(match[3]);
    console.log(`✅ Patrón "DD de MMMM de YYYY": dia=${dia}, mes=${mes}, anio=${anio}`);
    return `${dia} de ${mes} de ${anio}`;
  }

  // PATRÓN 2: "05 agosto 2025" o "5 agosto 2025"
  match = fecha.match(/(\d{1,2})\s+(\w+)\s+(\d{4})/);
  if (match) {
    dia = parseInt(match[1]);
    mes = mesesMap[match[2]] || match[2];
    anio = parseInt(match[3]);
    console.log(`✅ Patrón "DD MMMM YYYY": dia=${dia}, mes=${mes}, anio=${anio}`);
    return `${dia} de ${mes} de ${anio}`;
  }

  // PATRÓN 3: "05 de agosto" o "5 de agosto" (año actual)
  match = fecha.match(/(\d{1,2})\s*de\s*(\w+)/);
  if (match) {
    dia = parseInt(match[1]);
    mes = mesesMap[match[2]] || match[2];
    anio = anioActual;
    console.log(`✅ Patrón "DD de MMMM": dia=${dia}, mes=${mes}, anio=${anio} (año actual)`);
    return `${dia} de ${mes} de ${anio}`;
  }

  // PATRÓN 4: "05 agosto" o "5 agosto" (año actual)
  match = fecha.match(/(\d{1,2})\s+(\w+)/);
  if (match && mesesMap[match[2]]) {
    dia = parseInt(match[1]);
    mes = mesesMap[match[2]];
    anio = anioActual;
    console.log(`✅ Patrón "DD MMMM": dia=${dia}, mes=${mes}, anio=${anio} (año actual)`);
    return `${dia} de ${mes} de ${anio}`;
  }

  // PATRÓN 5: "05-08-2025" o "5-8-2025"
  match = fecha.match(/(\d{1,2})-(\d{1,2})-(\d{4})/);
  if (match) {
    dia = parseInt(match[1]);
    const numeroMes = parseInt(match[2]);
    const mesesArray = ["enero","febrero","marzo","abril","mayo","junio","julio","agosto","septiembre","octubre","noviembre","diciembre"];
    mes = mesesArray[numeroMes - 1] || "enero";
    anio = parseInt(match[3]);
    console.log(`✅ Patrón "DD-MM-YYYY": dia=${dia}, mes=${mes}, anio=${anio}`);
    return `${dia} de ${mes} de ${anio}`;
  }

  // PATRÓN 6: "05-08" o "5-8" (año actual)
  match = fecha.match(/(\d{1,2})-(\d{1,2})/);
  if (match) {
    dia = parseInt(match[1]);
    const numeroMes = parseInt(match[2]);
    const mesesArray = ["enero","febrero","marzo","abril","mayo","junio","julio","agosto","septiembre","octubre","noviembre","diciembre"];
    mes = mesesArray[numeroMes - 1] || "enero";
    anio = anioActual;
    console.log(`✅ Patrón "DD-MM": dia=${dia}, mes=${mes}, anio=${anio} (año actual)`);
    return `${dia} de ${mes} de ${anio}`;
  }

  // PATRÓN 7: "05/08/2025" o "5/8/2025"
  match = fecha.match(/(\d{1,2})\/(\d{1,2})\/(\d{4})/);
  if (match) {
    dia = parseInt(match[1]);
    const numeroMes = parseInt(match[2]);
    const mesesArray = ["enero","febrero","marzo","abril","mayo","junio","julio","agosto","septiembre","octubre","noviembre","diciembre"];
    mes = mesesArray[numeroMes - 1] || "enero";
    anio = parseInt(match[3]);
    console.log(`✅ Patrón "DD/MM/YYYY": dia=${dia}, mes=${mes}, anio=${anio}`);
    return `${dia} de ${mes} de ${anio}`;
  }

  // PATRÓN 8: "05/08" o "5/8" (año actual)
  match = fecha.match(/(\d{1,2})\/(\d{1,2})/);
  if (match) {
    dia = parseInt(match[1]);
    const numeroMes = parseInt(match[2]);
    const mesesArray = ["enero","febrero","marzo","abril","mayo","junio","julio","agosto","septiembre","octubre","noviembre","diciembre"];
    mes = mesesArray[numeroMes - 1] || "enero";
    anio = anioActual;
    console.log(`✅ Patrón "DD/MM": dia=${dia}, mes=${mes}, anio=${anio} (año actual)`);
    return `${dia} de ${mes} de ${anio}`;
  }

  // PATRÓN 9: Solo número "5" (asumir mes actual)
  match = fecha.match(/^(\d{1,2})$/);
  if (match) {
    dia = parseInt(match[1]);
    const mesActual = new Date().getMonth();
    const mesesArray = ["enero","febrero","marzo","abril","mayo","junio","julio","agosto","septiembre","octubre","noviembre","diciembre"];
    mes = mesesArray[mesActual];
    anio = anioActual;
    console.log(`✅ Patrón "DD solo": dia=${dia}, mes=${mes} (mes actual), anio=${anio} (año actual)`);
    return `${dia} de ${mes} de ${anio}`;
  }

  console.log(`❌ No se pudo normalizar la fecha: "${fechaInput}"`);
  return fechaInput;
}
