// (8) NUEVA FUNCIÓN: Buscar día por fecha exacta en todas las mallas
function buscarDiaPorFechaExacta(fechaSolicitada, mallaActual, mallaProxima) {
  console.log(`🔍 BUSCANDO FECHA EXACTA: "${fechaSolicitada}"`);

  const mallas = [
    { malla: mallaActual, nombre: "actual" },
    { malla: mallaProxima, nombre: "proxima" }
  ];

  for (const { malla, nombre } of mallas) {
    console.log(`  📋 Revisando malla "${nombre}"`);

    if (!malla) {
      console.log(`    ❌ Malla "${nombre}" es nula`);
      continue;
    }

    if (!malla.dias) {
      console.log(`    ❌ Malla "${nombre}" no tiene días`);
      continue;
    }

    console.log(`    ✅ Malla "${nombre}" tiene ${Object.keys(malla.dias).length} días`);

    // Recorrer todos los días de la malla
    for (const [nombreDia, datosDia] of Object.entries(malla.dias)) {
      if (!datosDia) {
        console.log(`      ⚠️ Día "${nombreDia}" tiene datos nulos`);
        continue;
      }

      // NORMALIZAR AMBAS FECHAS PARA COMPARACIÓN FLEXIBLE
      const fechaMallaNormalizada = normalizarFechaFlexible(datosDia.fechaDia || "");
      const fechaSolicitadaNormalizada = normalizarFechaFlexible(fechaSolicitada);

      console.log(`      🗓️ Comparando día "${nombreDia}": fechaDia="${datosDia.fechaDia}" (norm: "${fechaMallaNormalizada}") vs fechaSolicitada="${fechaSolicitada}" (norm: "${fechaSolicitadaNormalizada}")`);

      if (fechaMallaNormalizada === fechaSolicitadaNormalizada) {
        console.log(`      🎉 FECHA ENCONTRADA! Día "${nombreDia}" en malla "${nombre}"`);
        return {
          malla: malla,
          nombreDia: nombreDia,
          datosDia: datosDia,
          mallaUsada: nombre
        };
      }
    }

    console.log(`    ❌ Fecha "${fechaSolicitada}" NO encontrada en malla "${nombre}"`);
  }

  console.log(`❌ Fecha "${fechaSolicitada}" NO encontrada en NINGUNA malla`);
  return null;
}

// (9) Verificar disponibilidad de la hora solicitada (NUEVA LÓGICA)
function verificarDisponibilidadHoraSolicitada(
  fechaSolicitada,
  horaSolicitada,
  muelle,
  horasNecesarias,
  mallaActual,
  mallaProxima
) {
  try {
    console.log(`🔍 INICIANDO VERIFICACIÓN DE DISPONIBILIDAD`);
    console.log(`  Fecha solicitada: "${fechaSolicitada}"`);
    console.log(`  Hora solicitada: "${horaSolicitada}"`);
    console.log(`  Muelle: "${muelle}"`);
    console.log(`  Horas necesarias: ${horasNecesarias}`);

    /* ── Buscar el día por fecha exacta ── */
    console.log(`🔍 Buscando fecha exacta: "${fechaSolicitada}"`);
    const resultadoBusqueda = buscarDiaPorFechaExacta(fechaSolicitada, mallaActual, mallaProxima);

    if (!resultadoBusqueda) {
      console.log(`❌ No se encontró la fecha "${fechaSolicitada}" en ninguna malla`);
      return false;
    }

    const { malla, nombreDia, datosDia } = resultadoBusqueda;
    console.log(`✅ Fecha encontrada en malla. Día: "${nombreDia}", fechaDia: "${datosDia.fechaDia}"`);

    /* ── Verificar muelle ── */
    const nombreMuelle = muelle.toLowerCase().replace(/\s+/g, ""); // "MUELLE 1" → "muelle1"
    console.log(`🔍 Transformando muelle: "${muelle}" → "${nombreMuelle}"`);

    if (!datosDia[nombreMuelle]) {
      console.log(`❌ No se encontró el muelle "${nombreMuelle}" para el día ${nombreDia}`);
      console.log(`  Muelles disponibles:`, Object.keys(datosDia).filter(k => k !== 'fechaDia'));
      return false;
    }
    console.log(`✅ Muelle "${nombreMuelle}" encontrado`);

    /* ── Verificar franjas ── */
    if (!malla.franjas || !Array.isArray(malla.franjas) || malla.franjas.length === 0) {
      console.log(`❌ No hay franjas definidas en la malla`);
      return false;
    }

    const franjas = malla.franjas.map(f => f.hora);
    console.log(`🔍 Franjas disponibles:`, franjas);
    console.log(`🔍 Buscando hora "${horaSolicitada}" (limpia: "${limpiarHora(horaSolicitada)}")`);

    const indiceInicio = franjas.findIndex(h => {
      const horaLimpia = limpiarHora(h);
      const horaSolicitadaLimpia = limpiarHora(horaSolicitada);
      console.log(`  Comparando: "${h}" (${horaLimpia}) vs "${horaSolicitada}" (${horaSolicitadaLimpia})`);
      return horaLimpia === horaSolicitadaLimpia;
    });

    if (indiceInicio === -1) {
      console.log(`❌ No se encontró la hora "${horaSolicitada}" en las franjas disponibles`);
      console.log(`  Franjas disponibles:`, franjas);
      return false;
    }
    console.log(`✅ Hora encontrada en índice ${indiceInicio}`);

    if (indiceInicio + horasNecesarias > franjas.length) {
      console.log(`❌ No hay suficientes franjas consecutivas disponibles (necesita ${horasNecesarias})`);
      return false;
    }

    /* ── Verificar disponibilidad de todas las franjas necesarias ── */
    console.log(`🔍 Verificando disponibilidad de ${horasNecesarias} franja(s) consecutiva(s)`);
    for (let i = 0; i < horasNecesarias; i++) {
      const horaFranja = franjas[indiceInicio + i];
      const franja = datosDia[nombreMuelle][horaFranja];

      console.log(`  Revisando franja ${i + 1}/${horasNecesarias}: "${horaFranja}"`);

      if (!franja) {
        console.log(`❌ No se encontró la franja "${horaFranja}" en "${nombreMuelle}"`);
        return false;
      }

      const estadoFranja = (franja.estado || "").toLowerCase().trim();
      console.log(`    Estado: "${franja.estado}" → "${estadoFranja}"`);

      if (estadoFranja !== "disponible") {
        console.log(`❌ La franja "${horaFranja}" no está disponible. Estado: "${franja.estado}"`);
        return false;
      }
      console.log(`    ✅ Franja "${horaFranja}" está disponible`);
    }

    console.log(`🎉 TODAS las franjas están disponibles para ${fechaSolicitada} ${horaSolicitada}`);
    return true;
  } catch (error) {
    console.error(`❌ Error verificando disponibilidad: ${error.message}`);
    console.error(error.stack);
    return false;
  }
}

// (10) Formatear fecha en formato español
function formatearFechaEspanol(fecha) {
  if (!fecha || !(fecha instanceof Date)) {
    return "";
  }

  const dia = fecha.getDate();
  const meses = [
    "enero", "febrero", "marzo", "abril", "mayo", "junio",
    "julio", "agosto", "septiembre", "octubre", "noviembre", "diciembre"
  ];
  const mes = meses[fecha.getMonth()];
  const anio = fecha.getFullYear();

  return `${dia} de ${mes} de ${anio}`;
}

