// Código para preparar la actualización de Excel con citas
// Este nodo SOLO prepara el formato para enviarlos a Microsoft Graph

// Obtener datos de entrada: datos validados y datos de la cita
const datosValidados = $node["If CREACION DE CITA EN CRONOGRAMA"].json.datosValidados;
const datosCita = $('Code Verificar Tiempo Transcurrido').first().json.respuesta.datosCita;
const datosMalla = $('Unificación y consistencia de datos2').first().json.datosMalla;

// Extraer la información relevante
const {
  fecha,
  fechaFormateada,
  horaInicio,
  horaFin,
  muelle,
  duracionHoras
} = datosValidados;

const {
  proveedor = "",
  ordenCompra = "",
  peso = "",
  categoria = "",
  unidades = ""
} = datosCita;

// Obtener el día de la semana a partir de la fecha
function obtenerDiaSemana(fechaStr) {
  // Extrae el día, mes y año de la cadena
  const regex = /(\d+)\s+de\s+(\w+)\s+de\s+(\d+)/;
  const match = regex.exec(fechaStr);

  if (!match) return null;

  const dia = parseInt(match[1], 10);
  const mesStr = match[2].toLowerCase();
  const año = parseInt(match[3], 10);

  // Mapeo de nombres de meses a números
  const meses = {
    "enero": 0,
    "febrero": 1,
    "marzo": 2,
    "abril": 3,
    "mayo": 4,
    "junio": 5,
    "julio": 6,
    "agosto": 7,
    "septiembre": 8,
    "octubre": 9,
    "noviembre": 10,
    "diciembre": 11
  };

  const mesNum = meses[mesStr];

  if (mesNum === undefined) return null;

  // Crear objeto Date
  const fecha = new Date(año, mesNum, dia);

  // Obtener día de la semana (0=domingo, 1=lunes, ..., 6=sábado)
  const diaSemana = fecha.getDay();

  // Convertir a nombre en español
  const diasSemana = ["domingo", "lunes", "martes", "miércoles", "jueves", "viernes"];

  return diasSemana[diaSemana];
}

// Determinar el día de la semana
const diaSemana = obtenerDiaSemana(fecha);

// Determinar el muelle (muelle1 o muelle2)
const muelleKey = muelle.toLowerCase().includes("1") ? "muelle1" : "muelle2";

// Mapeo correcto de columnas para cada día y muelle según lo proporcionado
const mapeoColumnas = {
  'lunes': {
    'muelle1': 'C', 
    'muelle2': 'F' 
  },
  'martes': {
    'muelle1': 'I', 
    'muelle2': 'L'  
  },
  'miércoles': {
    'muelle1': 'O', 
    'muelle2': 'R'  
  },
  'jueves': {
    'muelle1': 'U', 
    'muelle2': 'X' 
  },
  'viernes': {
    'muelle1': 'AA', 
    'muelle2': 'AD'  
  }
};

// Verificar si tenemos mapeo para este día
if (!mapeoColumnas[diaSemana]) {
  console.log(`Día no soportado: ${diaSemana}`);
  return {
    json: {
      success: false,
      mensaje: `Día no soportado: ${diaSemana}`,
      error: "DIA_NO_SOPORTADO"
    }
  };
}

// Obtener la columna base para el día y muelle específicos
const columnaBase = mapeoColumnas[diaSemana][muelleKey];
if (!columnaBase) {
  console.log(`Configuración no soportada: día=${diaSemana}, muelle=${muelleKey}`);
  return {
    json: {
      success: false,
      mensaje: `Configuración no soportada: día=${diaSemana}, muelle=${muelleKey}`,
      error: "CONFIGURACION_NO_SOPORTADA"
    }
  };
}

// Calcular la siguiente columna en Excel
function siguienteColumna(columna) {
  if (columna.length === 1) {
    // Para columnas de una letra (A-Z)
    return String.fromCharCode(columna.charCodeAt(0) + 1);
  } else {
    // Para columnas de dos letras (AA-ZZ)
    const primerChar = columna.charAt(0);
    const segundoChar = columna.charAt(1);

    if (segundoChar === 'Z') {
      // Si es 'Z', pasar a la siguiente letra para el primer carácter
      return String.fromCharCode(primerChar.charCodeAt(0) + 1) + 'A';
    } else {
      // Incrementar solo el segundo carácter
      return primerChar + String.fromCharCode(segundoChar.charCodeAt(0) + 1);
    }
  }
}

// Función para comparar horas de forma flexible
function horasIguales(h1, h2) {
  const normalizar = (h) =>
    h
      .toString()
      .trim()
      .toLowerCase()
      .replace(/\./g, '')            // quita puntos (por si viene "a.m.")
      .replace(/\s+/g, '')           // quita espacios
      .replace(/^0+/, '');           // quita ceros a la izquierda
  return normalizar(h1) === normalizar(h2);
}

// Ordenar franjas por hora
function obtenerFranjasOrdenadas(franjas) {
  return [...franjas].sort((a, b) => {
    // Convertir horas a formato comparable (número de minutos desde medianoche)
    function horaAMinutos(hora) {
      const match = hora.match(/(\d+):(\d+)\s*([AP]M)/i);
      if (!match) return 0;

      let horas = parseInt(match[1], 10);
      const minutos = parseInt(match[2], 10);
      const periodo = match[3].toUpperCase();

      // Convertir a formato 24 horas
      if (periodo === 'PM' && horas !== 12) {
        horas += 12;
      } else if (periodo === 'AM' && horas === 12) {
        horas = 0;
      }

      return horas * 60 + minutos;
    }

    return horaAMinutos(a.hora) - horaAMinutos(b.hora);
  });
}

// Obtener la posición de la franja en el array ordenado
function obtenerPosicionFranja(hora, franjas) {
  const franjasOrdenadas = obtenerFranjasOrdenadas(franjas);
  return franjasOrdenadas.findIndex(f => horasIguales(f.hora, hora));
}

// Función corregida para calcular la fila Excel basada en la posición de la franja
function calcularFilaExcel(hora, franjas) {
  const posicion = obtenerPosicionFranja(hora, franjas);

  if (posicion === -1) {
    console.log(`No se encontró la posición para la hora ${hora}`);
    return null;
  }

  // Mapeo de posiciones a filas base en Excel
  // Basado en las observaciones del Excel:
  const FILA_PRIMERA_FRANJA = 12;  // 6:00 AM
  const INCREMENTO_ESTANDAR = 3;   // Incremento estándar entre franjas

  // Caso especial para 9:00 AM
  if (posicion === 3) {
    return 21;  // Fila exacta para 9:00 AM
  } else if (posicion === 4) {
    return 24;  // Fila exacta para 10:00 AM
  } else if (posicion === 5) {
    return 27;  // Fila exacta para 11:00 AM
  } else if (posicion < 3) {
    // Para posiciones antes de 9:00 AM, usamos el incremento estándar
    return FILA_PRIMERA_FRANJA + (posicion * INCREMENTO_ESTANDAR);
  } else {
    // Para posiciones después de 11:00 AM, calculamos a partir de 11:00 AM
    return 27 + ((posicion - 5) * INCREMENTO_ESTANDAR);
  }
}

// Función para encontrar la franja horaria siguiente
function encontrarSiguienteFranja(horaActual, franjas) {
  const franjasOrdenadas = obtenerFranjasOrdenadas(franjas);
  const posicionActual = franjasOrdenadas.findIndex(f => horasIguales(f.hora, horaActual));

  if (posicionActual === -1 || posicionActual >= franjasOrdenadas.length - 1) {
    return null; // No hay siguiente franja
  }

  return franjasOrdenadas[posicionActual + 1];
}

// Encontrar información de la franja horaria inicial en los datos de la malla
const franjaInicio = datosMalla.franjas.find(f => horasIguales(f.hora, horaInicio));
if (!franjaInicio) {
  console.log(`No se encontró la franja de inicio: ${horaInicio}`);
  return {
    json: {
      success: false,
      mensaje: `No se encontró la franja de inicio: ${horaInicio}`,
      error: "FRANJA_NO_ENCONTRADA"
    }
  };
}

// Array para almacenar todas las actualizaciones
let todasLasActualizaciones = [];

// Procesar cada hora de la cita (según duracionHoras)
let horaActual = horaInicio;
let horasRestantes = duracionHoras;

while (horasRestantes > 0) {
  // Encontrar la franja actual
  const franjaActual = datosMalla.franjas.find(f => horasIguales(f.hora, horaActual));

  if (!franjaActual) {
    console.log(`No se encontró información para la franja: ${horaActual}`);
    break;
  }

  // Calcular la fila base de forma dinámica
  const filaBase = calcularFilaExcel(horaActual, datosMalla.franjas);

  if (!filaBase) {
    console.log(`No se pudo determinar la fila para la franja: ${horaActual}`);
    break;
  }

  console.log(`Calculando celdas para: ${horaActual}, fila base: ${filaBase}`);

  const columnaLineas = siguienteColumna(columnaBase);
  const columnaUnd = siguienteColumna(columnaLineas);

  // Calcular las celdas para esta franja
  const rangoCeldas = {
    proveedor: `${columnaBase}${filaBase}`,
    ordenCompra: `${columnaBase}${filaBase + 1}`,
    peso: `${columnaBase}${filaBase + 2}`,
    categoria: `${columnaLineas}${filaBase + 2}`,
    unidades: `${columnaUnd}${filaBase + 2}`
  };

  console.log(`Celdas para ${horaActual}, ${diaSemana}, ${muelleKey}:`, JSON.stringify(rangoCeldas));

  // Agregar actualizaciones para esta franja horaria
  todasLasActualizaciones.push(
    {
      celda: rangoCeldas.proveedor,
      valor: proveedor
    },
    {
      celda: rangoCeldas.ordenCompra,
      valor: ordenCompra
    },
    {
      celda: rangoCeldas.peso,
      valor: peso
    },
    {
      celda: rangoCeldas.categoria,
      valor: categoria
    },
    {
      celda: rangoCeldas.unidades,
      valor: unidades
    }
  );

  // Preparar para la siguiente hora
  horasRestantes--;

  if (horasRestantes > 0) {
    const siguienteFranja = encontrarSiguienteFranja(horaActual, datosMalla.franjas);

    if (siguienteFranja) {
      horaActual = siguienteFranja.hora;
    } else {
      console.log(`No se encontró la siguiente franja horaria después de ${horaActual}`);
      break;
    }
  }
}

// Preparar el formato final para la petición a Microsoft Graph
const actualizacionesGraph = todasLasActualizaciones.map(act => ({
  address: act.celda,
  values: [[act.valor]]
}));

// Usar el nombre correcto de la hoja desde datosMalla
const nombreHojaExcel = $('Code VALIDACIÓN DE CITA EN CRONOGRAMA').first().json.nombreHoja;

// Preparar el resultado para el siguiente nodo
return {
  json: {
    success: true,
    mensaje: `Preparadas ${todasLasActualizaciones.length} actualizaciones para la cita del ${fecha} de ${horaInicio} a ${horaFin} en ${muelle} (${duracionHoras} horas)`,
    nombreHoja: nombreHojaExcel,
