const franjas = [];
for (let i = 0; i < numFranjas; i++) {
  const inicio = i * UPDATES_PER_FRANJA;
  const fin = Math.min(inicio + UPDATES_PER_FRANJA, updates.length);

  // Obtener las actualizaciones de esta franja
  const actualizacionesFranja = updates.slice(inicio, fin);

