};

// Código para verificar si la inserción fue exitosa (sin acciones correctivas)
const input = $input.first().json;

// Verificar que tenemos la estructura esperada en la respuesta
if (!input || !input.responses || !Array.isArray(input.responses) || input.responses.length === 0) {
  console.log('Estructura de datos de respuesta no válida');
  return {
    json: {
      error: "RESPUESTA_INVALIDA",
      mensaje: "La estructura de datos de la respuesta no es válida",
      exito: false
    }
  };
}

// Obtener la primera respuesta
const response = input.responses[0];

// Extraer información de la solicitud original del ID
// El formato del ID es: "CELDA_INDICE_TIMESTAMP"
const idPartes = response.id.split('_');
const celdaOriginal = idPartes[0];
const indice = idPartes[1];

// Verificar que la respuesta tiene un cuerpo
if (!response.body || !response.body.address) {
  console.log(`Error: La respuesta no contiene información sobre la dirección de celda`);
  return {
    json: {
      error: "RESPUESTA_SIN_DIRECCION",
      mensaje: "La respuesta no incluye información sobre la dirección de celda",
      requestId: response.id,
      status: response.status,
      celdaOriginal: celdaOriginal,
      exito: false
    }
  };
}

// Extraer la dirección de celda de la respuesta
let direccionRespuesta = "";
if (response.body && response.body.address) {
  const partesDireccion = response.body.address.split('!');
  if (partesDireccion.length > 1) {
    direccionRespuesta = partesDireccion[1];
  }
}

// Obtener el valor insertado de la respuesta
const valorInsertado = response.body.values && response.body.values[0] && response.body.values[0][0];

// Verificar si la inserción se realizó en la celda correcta
const insercionCorrecta = direccionRespuesta === celdaOriginal;

// Verificar el código de estado HTTP
const estadoExitoso = response.status >= 200 && response.status < 300;

// Crear objeto para almacenar el resultado de la verificación
const resultado = {
