/*
===========================================================
  Nodo Code - VALIDAR INSERCIONES EN EXCEL (n8n)
  • Analiza la salida del “Loop Over Uno a Uno”.
  • Agrupa los ítems por franja (campo `indice` si existe;
    de lo contrario, cada bloque de 5 ítems forma una franja).
  • Verifica que cada inserción tenga:
        exito === true
        statusCode === 200
        falloEstado === false
  • Devuelve un único objeto JSON con:
        – Resumen global.
        – Detalle por franja (éxitos / fallos y celdas con error).
===========================================================
*/

// 1) Obtener todos los ítems que llegan al nodo
const items = $input.all();

// 2) Función auxiliar para determinar el identificador de la franja
function obtenerIdFranja(item, idx) {
  // Si existe 'indice', úsalo; de lo contrario agrupa cada 5 ítems
  if (item.json && item.json.indice !== undefined && item.json.indice !== null) {
    return String(item.json.indice).trim();
  }
  // +1 para que las franjas queden 1-basadas
  return String(Math.floor(idx / 5) + 1);
}

// 3) Agrupar ítems por franja
const franjas = {};
items.forEach((item, idx) => {
  const idFranja = obtenerIdFranja(item, idx);
  if (!franjas[idFranja]) {
    franjas[idFranja] = { id: idFranja, items: [] };
  }
  franjas[idFranja].items.push(item.json);
});

// 4) Analizar cada franja y construir el detalle
const detallesPorFranja = Object.values(franjas).map(franja => {
  const totalOps   = franja.items.length;
  const exitosas   = franja.items.filter(i =>
    i.exito === true &&
    i.statusCode === 200 &&
    i.falloEstado === false
  ).length;

  const fallidas   = totalOps - exitosas;
  const exitoTotal = fallidas === 0;

  // Celdas con error para un diagnóstico claro
  const celdasFallidas = franja.items
    .filter(i => !(i.exito && i.statusCode === 200 && !i.falloEstado))
    .map(i => ({
      celdaOriginal   : i.celdaOriginal,
      celdaInsertada  : i.celdaInsertada,
      statusCode      : i.statusCode,
      falloEstado     : i.falloEstado,
      celdasDiferentes: i.celdasDiferentes,
      mensaje         : 'Inserción fallida'
    }));

  return {
    idFranja                : franja.id,
    exitoFranja             : exitoTotal,
    operacionesTotales      : totalOps,
    operacionesExitosas     : exitosas,
    operacionesFallidas     : fallidas,
    porcentajeExitosas      : ((exitosas / totalOps) * 100).toFixed(2) + '%',
    celdasFallidas
  };
});

// 5) Resumen global
const operacionesTotales   = items.length;
const operacionesExitosas  = detallesPorFranja.reduce((sum, f) => sum + f.operacionesExitosas, 0);
const operacionesFallidas  = operacionesTotales - operacionesExitosas;

const franjasTotales   = detallesPorFranja.length;
const franjasExitosas  = detallesPorFranja.filter(f => f.exitoFranja).length;
const franjasFallidas  = franjasTotales - franjasExitosas;


const resumen = {
  estadoGeneral              : franjasFallidas === 0 ? 'ÉXITO_COMPLETO' : 'ÉXITO_PARCIAL',
  franjasTotales,
  franjasExitosas,
  franjasFallidas,
  porcentajeFranjasExitosas  : ((franjasExitosas / franjasTotales) * 100).toFixed(2) + '%',
  operacionesTotales,
  operacionesExitosas,
  operacionesFallidas,
  porcentajeOperacionesExitosas : ((operacionesExitosas / operacionesTotales) * 100).toFixed(2) + '%',
  detallesPorFranja
};

// 6) Devolver un único ítem con el resumen
return [
  {
    json: resumen
  }
];

// 1) Leer chat_id una sola vez
const chatId = $node["Organiza el Mensaje"].json.infoMensaje.chat_id;

// 2) Tomamos los datos de informacionCita una sola vez
const cita = $input.first().json.informacionCita || {};
const historialMensajes = $('Unificación y consistencia de datos').first().json.infoMensaje.historialMensajes;

// Función para convertir fechas en formato "06 de mayo de 2025" a "YYYY-MM-DD HH:mm:ss"
function convertirFecha(fechaTexto) {
  const meses = {
    enero: '01', febrero: '02', marzo: '03', abril: '04', mayo: '05',
    junio: '06', julio: '07', agosto: '08', septiembre: '09',
    octubre: '10', noviembre: '11', diciembre: '12'
  };

  // Dividir la fecha y mapear el mes
  const [dia, de, mes, deNuevo, anio] = fechaTexto.split(' ');
  const mesNumerico = meses[mes.toLowerCase()]; // Convertir el mes a número
  return `${anio}-${mesNumerico}-${dia.padStart(2, '0')} 00:00:00`; // Formato DATETIME
}

// Convertimos la fecha aquí
const fechaCitaConvertida = convertirFecha(cita.fecha);

// 3) Retornamos un solo objeto con todos los datos necesarios
return {
  json: {
    chat_id: chatId,
    proveedor: cita.proveedor,
    orden_compra: cita.ordenCompra,
    peso: cita.peso,
    unidades: cita.unidades,
    categoria: cita.categoria,
    muelle: cita.muelle,
    fecha_cita: fechaCitaConvertida, // Fecha ya convertida
    hora_inicio: cita.horaInicio,
    hora_fin: cita.horaFin,
    chat_historial: historialMensajes,
    // Incluimos cualquier otro dato que necesites
  }
};

/**
 * Nodo Code – “Unir Franjas en Historial”
 * Coloca este nodo inmediatamente después de “Microsoft SQL Franjas Temporales”.
 * No requiere parámetros adicionales.
 */

// 1) Tomamos todos los ítems que llegan desde el nodo SQL
const items = $input.all();   // ← cada item = { id, chat_id, franjas_json }

// 2) Armamos la cadena tipo historial:  "Franja 1: {...}\n\nFranja 2: {...}\n\n…"
let historial = '';
items.forEach((item, idx) => {
  const numero = idx + 1;                                 // 1, 2, 3, …
  const cuerpoFranja = JSON.stringify(JSON.parse(item.json.franjas_json), null, 2);    // viene como string desde SQL
  historial += `Franja ${numero}: ${cuerpoFranja}\n\n`;
});

// 3) (Opcional) quitamos el último salto de línea doble
historial = historial.trimEnd();

// 4) Devolvemos **UN** ítem con **UNA** propiedad (ajusta el nombre si quieres)
return [
  {
    json: {
      franjas_historial: historial            // ← cadena final
    }
  }
];

/* -------------------------------------------------------------------------
   Ejemplo de salida cuando hay 3 filas:

   {
     "franjas_historial": "Franja 1: {\"disponibilidadConfirmada\":false,...}\n\n
                           Franja 2: {\"disponibilidadConfirmada\":true,...}\n\n
                           Franja 3: {...}"
   }
--------------------------------------------------------------------------- */

