// Nodo Code: Validación de datos para reprogramación de citas
// Recibe los datos extraídos del nodo LLM y aplica las validaciones de negocio

// Obtener los datos del nodo anterior usando la ruta específica
const datosExtraidos = $('Basic LLM Extracción y Validacion de Datos Editar Cita').first().json.output;

// Mostrar datos extraídos para debug
console.log("Datos recibidos del nodo anterior:", JSON.stringify(datosExtraidos, null, 2));

// Función principal que procesa los datos de entrada
function procesarDatosReprogramacion(datos) {
  try {
    // Estructura de respuesta final (siguiendo el formato del prompt original)
    const respuesta = {
      todoCorrecto: false,
      datosPreviamenteConfirmados: false,
      idCita: datos.idCita || "",
      ordenCompra: datos.ordenCompra || "",
      nuevaFecha: datos.nuevaFecha || "",
      nuevaHora: datos.nuevaHora || "",
      camposFaltantes: datos.camposFaltantes || [],
      mensajeSistema: "",
      rechazoTemporal: false,
      motivoRechazo: "",
      intentaModificarDatos: false
    };

    console.log("Datos iniciales en respuesta:", JSON.stringify(respuesta, null, 2));

    // 1. Verificar si hay campos faltantes
    if (respuesta.camposFaltantes && respuesta.camposFaltantes.length > 0) {
      respuesta.todoCorrecto = false;
      respuesta.mensajeSistema = generarMensajeCamposFaltantes(respuesta.camposFaltantes);
      return respuesta;
    }

    // 2. Validar las fechas y reglas temporales
    const validacionTemporal = validarReglasTemporales(respuesta.nuevaFecha, datos.fechaActual, datos.horaActual);
    console.log("Resultado de validación temporal:", JSON.stringify(validacionTemporal, null, 2));

    if (validacionTemporal.esValido === false) {
      respuesta.todoCorrecto = false;
      respuesta.rechazoTemporal = true;
      respuesta.motivoRechazo = validacionTemporal.motivoRechazo;
      respuesta.mensajeSistema = validacionTemporal.mensaje;
      return respuesta;
    }

    // 3. Si todo está correcto, preparar mensaje de confirmación
    respuesta.todoCorrecto = true;
    respuesta.mensajeSistema = `Entendido. Estás solicitando reprogramar la cita 🔖 ${respuesta.idCita} con orden de compra ${respuesta.ordenCompra} para el ${respuesta.nuevaFecha} a las ${respuesta.nuevaHora}. Verificaré la disponibilidad para este horario.`;

    return respuesta;
  } catch (error) {
    console.error("Error al procesar datos de reprogramación:", error);
    return {
      todoCorrecto: false,
      datosPreviamenteConfirmados: false,
      idCita: datos.idCita || "",
      ordenCompra: datos.ordenCompra || "",
      nuevaFecha: datos.nuevaFecha || "",
      nuevaHora: datos.nuevaHora || "",
      camposFaltantes: ["error_procesamiento"],
      mensajeSistema: "Ha ocurrido un error al procesar tu solicitud. Por favor, intenta nuevamente proporcionando todos los datos necesarios.",
      rechazoTemporal: false,
      motivoRechazo: "error_sistema",
      intentaModificarDatos: false
    };
  }
}

// --- Configuración Centralizada de Campos ---
// Aquí definimos toda la información de cada campo en un solo lugar.
const detallesCampos = {
  idCita: {
    label: "🔖 *Número de cita* (aparece en tu confirmación)",
    ejemploSimple: "Mi número de cita es *12345*",
    ejemploCompuesto: "cita *12345*"
  },
  ordenCompra: {
    label: "📋 *Número de orden de compra* (para verificación)",
    ejemploSimple: "Mi orden de compra es *ARGM47896*",
    ejemploCompuesto: "con orden de compra *ARGM47896*"
  },
  nuevaFecha: {
    label: "📅 *Nueva fecha* que prefieres",
    ejemploSimple: "Para el *25 de mayo*",
    ejemploCompuesto: "para el *25 de mayo*"
  },
  nuevaHora: {
    label: "⏰ *Nueva hora* que prefieres",
    ejemploSimple: "A las *2:00 PM*",
    ejemploCompuesto: "a las *2:00 PM*"
  }
};

// --- Función Mejorada y Dinámica ---
function generarMensajeCamposFaltantes(camposFaltantes) {
  const totalCampos = camposFaltantes.length;

  // Caso 0: No falta nada (opcional, por si acaso)
  if (totalCampos === 0) {
    return "Tengo toda la información necesaria. ¡Gracias!";
  }

  // Caso 1: Falta un solo campo. El mensaje es más directo y el ejemplo es simple.
  if (totalCampos === 1) {
    const campo = camposFaltantes[0];
    const detalle = detallesCampos[campo];
    let introduccion = "Para continuar, necesito que me indiques:";

    if (campo === 'idCita') introduccion = "Para reprogramar tu cita, necesito el";
    if (campo === 'ordenCompra') introduccion = "Para verificar tu identidad, necesito el";
    if (campo === 'nuevaFecha') introduccion = "Para continuar, necesito que me indiques la";
    if (campo === 'nuevaHora') introduccion = "Para completar la reprogramación, necesito que me indiques la";

    // Usamos el label sin el emoji para la frase.
    const labelSinEmoji = detalle.label.substring(2); 

    return `${detalle.label.split(' ')[0]} ${introduccion} ${labelSinEmoji}.\n\n💬 *Ejemplo:* "${detalle.ejemploSimple}"`;
  }

  // Caso 2: Faltan todos los campos. Usamos el mensaje más completo.
  if (totalCampos === 4) {
    return "📝 Para reprogramar tu cita, necesito *todos estos datos*:\n\n" +
           "• " + detallesCampos.idCita.label + "\n" +
           "• " + detallesCampos.ordenCompra.label + "\n" +
           "• " + detallesCampos.nuevaFecha.label + "\n" +
           "• " + detallesCampos.nuevaHora.label + "\n\n" +
           "💬 *Ejemplo completo:* \"Quiero reprogramar mi cita *12345* con orden de compra *ARGM47896* para el *25 de mayo* a las *2:00 PM*\"";
  }

  // Caso 3: Faltan 2 o 3 campos. Construimos el mensaje dinámicamente.

  // 1. Construir la lista de requerimientos
  const listaRequerimientos = camposFaltantes
    .map(campo => `• ${detallesCampos[campo].label}`)
    .join('\n');

  // 2. Construir el ejemplo dinámico
  let partesEjemplo = [];
  // El orden importa para que la frase tenga sentido
  if (camposFaltantes.includes('idCita')) partesEjemplo.push(detallesCampos.idCita.ejemploCompuesto);
  if (camposFaltantes.includes('ordenCompra')) partesEjemplo.push(detallesCampos.ordenCompra.ejemploCompuesto);
  if (camposFaltantes.includes('nuevaFecha')) partesEjemplo.push(detallesCampos.nuevaFecha.ejemploCompuesto);
  if (camposFaltantes.includes('nuevaHora')) partesEjemplo.push(detallesCampos.nuevaHora.ejemploCompuesto);

  const ejemploDinamico = "Reprogramar " + partesEjemplo.join(' ');

  return `📝 Para reprogramar tu cita, necesito los siguientes datos:\n\n${listaRequerimientos}\n\n💬 *Ejemplo:* "${ejemploDinamico}"`;
}

// Validar reglas temporales (mismo día, día siguiente, etc.)
function validarReglasTemporales(nuevaFecha, fechaActual, horaActual) {
  try {
    console.log(`Validando fechas - Nueva: ${nuevaFecha}, Actual: ${fechaActual}, Hora: ${horaActual}`);

    // Convertir fechas a objetos Date para comparación
    const fechaNueva = parsearFecha(nuevaFecha);
    const fechaHoy = parsearFechaActual(fechaActual);

    console.log(`Fechas parseadas - Nueva: ${fechaNueva.toISOString()}, Hoy: ${fechaHoy.toISOString()}`);

    // Calcular fecha de mañana
    const fechaManana = new Date(fechaHoy);
    fechaManana.setDate(fechaManana.getDate() + 1);
    console.log(`Fecha mañana: ${fechaManana.toISOString()}`);

    // 1. Validar si es el mismo día
    if (
      fechaNueva.getFullYear() === fechaHoy.getFullYear() &&
      fechaNueva.getMonth() === fechaHoy.getMonth() &&
      fechaNueva.getDate() === fechaHoy.getDate()
    ) {
      console.log("Es el mismo día - RECHAZADO");
      return {
        esValido: false,
        motivoRechazo: "mismo_dia",
        mensaje: "Lo siento, pero no es posible reprogramar citas para el mismo día. Por políticas internas, las modificaciones deben hacerse con al menos un día de anticipación. Por favor, selecciona una fecha futura a partir de mañana."
      };
    }

    // 2. NUEVA VALIDACIÓN: Verificar si es fin de semana (sábado o domingo)
    const diaSemana = fechaNueva.getDay();
    console.log(`Día de la semana de la fecha nueva: ${diaSemana} (0=domingo, 6=sábado)`);

    if (diaSemana === 0 || diaSemana === 6) {
      console.log("Es fin de semana - RECHAZADO");
      return {
        esValido: false,
        motivoRechazo: "fin_de_semana",
        mensaje: "La fecha solicitada cae en fin de semana. Por favor, selecciona un día hábil (lunes a viernes) para tu cita."
      };
    }

    // 3. Validar si es el día siguiente y es después de las 4:30 PM
    const esMañana = 
      fechaNueva.getFullYear() === fechaManana.getFullYear() &&
      fechaNueva.getMonth() === fechaManana.getMonth() &&
      fechaNueva.getDate() === fechaManana.getDate();

    const esTarde = esHoraPosteriorA430PM(horaActual);

    console.log(`Es mañana: ${esMañana}, Es tarde (post 4:30pm): ${esTarde}`);

    if (esMañana && esTarde) {
      console.log("Es para mañana después de las 4:30 PM - RECHAZADO");
      return {
        esValido: false,
        motivoRechazo: "tarde_para_manana",
        mensaje: `Lo siento, pero no es posible reprogramar citas para mañana después de las 4:30 PM del día actual. Por favor, selecciona una fecha a partir del ${formatearFecha(new Date(fechaManana.getTime() + 86400000))}. Las modificaciones para el día siguiente solo pueden realizarse antes de las 4:30 PM.`
      };
    }

    // Si pasa todas las validaciones
    console.log("Validación temporal APROBADA");
    return {
      esValido: true
    };
  } catch (error) {
    console.error("Error al validar reglas temporales:", error);
    return {
      esValido: true // En caso de error, permitimos continuar para no bloquear al usuario
    };
  }
}

// Funciones auxiliares para manipulación de fechas

// Parsea una fecha en formato "DD de mes de YYYY"
function parsearFecha(fechaStr) {
  try {
    console.log(`Parseando fecha: "${fechaStr}"`);
    const partes = fechaStr.split(" de ");

    if (partes.length < 3) {
      console.error(`Formato de fecha incorrecto: "${fechaStr}"`);
      return new Date(); // Devolver fecha actual en caso de error de formato
    }

    const dia = parseInt(partes[0], 10);

    const meses = {
      "enero": 0, "febrero": 1, "marzo": 2, "abril": 3, "mayo": 4, "junio": 5,
      "julio": 6, "agosto": 7, "septiembre": 8, "octubre": 9, "noviembre": 10, "diciembre": 11
    };

    const mes = meses[partes[1].toLowerCase()];
    const anio = parseInt(partes[2], 10);

    console.log(`Día: ${dia}, Mes: ${mes}, Año: ${anio}`);

    if (isNaN(dia) || mes === undefined || isNaN(anio)) {
      console.error(`Valores de fecha inválidos: día=${dia}, mes=${mes}, año=${anio}`);
      return new Date();
    }

    return new Date(anio, mes, dia);
  } catch (error) {
    console.error("Error al parsear fecha:", error);
    return new Date(); // Devuelve fecha actual en caso de error
  }
}

// Parsea la fecha actual en formato YYYY-MM-DD
function parsearFechaActual(fechaStr) {
  try {
    console.log(`Parseando fecha actual: "${fechaStr}"`);

    if (!fechaStr || typeof fechaStr !== 'string') {
      console.error(`Formato de fecha actual inválido: "${fechaStr}"`);
      return new Date();
    }

    const [anio, mes, dia] = fechaStr.split("-").map(num => parseInt(num, 10));

    if (isNaN(anio) || isNaN(mes) || isNaN(dia)) {
      console.error(`Valores de fecha actual inválidos: año=${anio}, mes=${mes}, día=${dia}`);
      return new Date();
    }

    return new Date(anio, mes - 1, dia); // Restar 1 al mes porque en JS los meses van de 0-11
  } catch (error) {
    console.error("Error al parsear fecha actual:", error);
    return new Date(); // Devuelve fecha actual en caso de error
  }
}

// Verifica si la hora es posterior a las 4:30 PM
function esHoraPosteriorA430PM(horaStr) {
  try {
    console.log(`Verificando si hora es posterior a 4:30 PM: "${horaStr}"`);

    if (!horaStr || typeof horaStr !== 'string') {
      console.error(`Formato de hora inválido: "${horaStr}"`);
      return false;
    }

    // Convierte la hora a minutos para facilitar la comparación
    let horas = 0;
    let minutos = 0;

    // Manejar varios formatos posibles de hora
    if (horaStr.includes(":")) {
      // Formato HH:MM (am/pm)
      const [hora, resto] = horaStr.split(":");
      horas = parseInt(hora, 10);

      // Extraer minutos y posible AM/PM
      if (resto.toLowerCase().includes("p.m.") || resto.toLowerCase().includes("pm")) {
        if (horas !== 12) horas += 12; // Convertir a formato 24h
        minutos = parseInt(resto.replace(/[^\d]/g, ""), 10);
      } else {
        if (horas === 12) horas = 0; // Mediodía en formato 12h es 0 en formato 24h
        minutos = parseInt(resto.replace(/[^\d]/g, ""), 10);
      }
    } else {
      // Intentar otros formatos
      const match = horaStr.match(/(\d+)(?:\D+)(\d*)\s*([ap]\.?m\.?)?/i);
      if (match) {
        horas = parseInt(match[1], 10);
        minutos = match[2] ? parseInt(match[2], 10) : 0;

        const periodo = match[3] ? match[3].toLowerCase() : "";
        if (periodo.includes("p")) {
          if (horas !== 12) horas += 12;
        } else if (periodo.includes("a") && horas === 12) {
          horas = 0;
        }
      }
    }

    // Convertir todo a minutos
    const totalMinutos = horas * 60 + minutos;
    console.log(`Hora convertida a minutos: ${totalMinutos} (${horas}:${minutos})`);

    // 4:30 PM = 16:30 = 990 minutos
    return totalMinutos > 990;

  } catch (error) {
    console.error("Error al evaluar la hora:", error);
    return false; // En caso de error, asumimos que no es después de las 4:30 PM
  }
}

// Formatea una fecha como "DD de mes de YYYY"
function formatearFecha(fecha) {
  try {
    const dia = fecha.getDate();

    const meses = [
      "enero", "febrero", "marzo", "abril", "mayo", "junio",
      "julio", "agosto", "septiembre", "octubre", "noviembre", "diciembre"
    ];

    const mes = meses[fecha.getMonth()];
    const anio = fecha.getFullYear();

    return `${dia} de ${mes} de ${anio}`;
  } catch (error) {
    console.error("Error al formatear fecha:", error);
    return ""; // Devuelve string vacío en caso de error
  }
}

// Aplicar la función principal a los datos extraídos
const resultado = procesarDatosReprogramacion(datosExtraidos);
console.log("Resultado final:", JSON.stringify(resultado, null, 2));

// Devolver el resultado procesado
return resultado;

