    // Si no se encuentra en la malla principal, buscar en la próxima semana
    if (!fechaEncontrada) {
        for (const dia in mallas.datosMallaProxSemana.dias) {
            if (compararFechasFlexible(mallas.datosMallaProxSemana.dias[dia].fechaDia, nuevaFecha)) {
                console.log(`Fecha encontrada en ${mallas.datosMallaProxSemana.semana} (${dia})`);
                fechaEncontrada = true;
                diaSemana = dia;
                datosDia = mallas.datosMallaProxSemana.dias[dia];
                nombreHoja = mallas.datosMallaProxSemana.semana;
                break;
            }
        }
    }

    // Si la fecha no se encuentra en ninguna malla
    if (!fechaEncontrada) {
        return [{
            json: {
                franjaDisponible: false,
                mensaje: `La fecha ${nuevaFecha} no se encuentra en la programación disponible.`
            }
        }];
    }

    // 4. Verificar disponibilidad en la malla correcta
    // Verificar que exista el muelle para ese día
    const muelleKey = `muelle${numeroMuelle}`;
    if (!datosDia[muelleKey]) {
        return [{
            json: {
                franjaDisponible: false,
                mensaje: `No existe el muelle ${numeroMuelle} para el día ${diaSemana}.`
            }
        }];
    }

    // Verificar que la hora exista en el muelle
    const muelle = datosDia[muelleKey];

    console.log(`Verificando hora: ${nuevaHora} en muelle ${numeroMuelle}`);
    console.log(`Estado: ${muelle[nuevaHora]?.estado || 'no definido'}`);

    if (!muelle[nuevaHora]) {
        return [{
            json: {
                franjaDisponible: false,
                mensaje: `La hora ${nuevaHoraConFormato} no está definida para el día ${diaSemana}.`
            }
        }];
    }

    // Verificar disponibilidad para horas consecutivas
    let disponible = true;
    let franjasNoDisponibles = [];
    let esMismaCita = false; // Nueva bandera para verificar si es reprogramación de la misma cita

    // Encontrar la hora en las franjas
    const franjas = nombreHoja === mallas.datosMalla.semana 
        ? mallas.datosMalla.franjas 
        : mallas.datosMallaProxSemana.franjas;

    let indiceInicio = -1;

    // Buscar usando normalización flexible de horas
    for (let i = 0; i < franjas.length; i++) {
        if (compararHorasFlexible(franjas[i].hora, nuevaHora)) {
            indiceInicio = i;
            console.log(`¡Coincidencia encontrada en índice ${i}!`);
            break;
        }
    }

    if (indiceInicio === -1) {
        console.log("No se encontró la hora en las franjas");
        return [{
            json: {
                franjaDisponible: false,
                mensaje: `La hora ${nuevaHoraConFormato} no se encuentra en las franjas horarias disponibles.`
            }
        }];
    }

    // Verificar disponibilidad para cada hora necesaria
    for (let i = 0; i < horasEntrega; i++) {
        if (indiceInicio + i >= franjas.length) {
            disponible = false;
            console.log("No hay suficientes horas disponibles en el día");
            break;
        }

        const franja = franjas[indiceInicio + i];
        const estadoFranja = muelle[franja.hora];

        console.log(`Verificando franja ${franja.hora}: ${estadoFranja?.estado || 'no definido'}`);

        if (!estadoFranja) {
            disponible = false;
            franjasNoDisponibles.push(franja.hora);
        } else if (estadoFranja.estado === "disponible") {
            // Franja disponible, todo bien
            continue;
        } else if (estadoFranja.estado === "ocupado") {
            // Verificar si está ocupada por la misma cita
            if (estadoFranja.ordenCompra === ordenCompra && 
                estadoFranja.proveedor === proveedorOriginal) {
                console.log(`Franja ${franja.hora} ocupada por la misma cita - permitir reprogramación`);
                esMismaCita = true;
                continue;
            } else {
                // Ocupada por otra cita
                disponible = false;
                franjasNoDisponibles.push(franja.hora);
            }
        } else {
            // Fuera de servicio u otro estado
            disponible = false;
            franjasNoDisponibles.push(franja.hora);
        }
    }

    // CÁLCULO PRECISO DEL nombreHoja PARA LA CITA ORIGINAL
    let nombreHojaOriginal = calcularNombreHojaOriginal(citaOriginal.fecha_cita, mallas);

    // Si hay disponibilidad o es reprogramación de la misma cita, retornar respuesta positiva
    if (disponible || esMismaCita) {
        const horaFin = calcularHoraFin(nuevaHoraConFormato, horasEntrega);
        const fechaFormateada = convertirFechaAFormatoISO(nuevaFecha);

        // Normalizar horaInicio para la respuesta (quitar cero inicial si existe)
        const horaInicioNormalizada = nuevaHoraConFormato.replace(/^0(\d:00 [AP]M)$/, "$1");

        const tipoOperacion = esMismaCita ? "reprogramación" : "nueva programación";

        const respuesta = {
            franjaDisponible: true,
            mensaje: `¡Genial! Hay espacio para la ${tipoOperacion} el ${nuevaFecha}, de ${horaInicioNormalizada} a ${horaFin} en MUELLE ${numeroMuelle}.`,
            datosValidados: {
                fecha: nuevaFecha,
                fechaFormateada: fechaFormateada,
                horaInicio: horaInicioNormalizada,
                horaFin: horaFin,
                muelle: `MUELLE ${numeroMuelle}`,
                duracionHoras: horasEntrega,
                disponibilidadConfirmada: true,
                tipoOperacion: tipoOperacion
            },
            nombreHoja: nombreHoja,
            citaAntigua: {
                id: idCitaDB,
                fechaFormateada: citaOriginal.fecha_cita ? citaOriginal.fecha_cita.split('T')[0] : "",
                horaInicio: horaInicioOriginal,
                horaFin: horaFinOriginal,
                muelle: muelleOriginal,
                nombreHoja: nombreHojaOriginal || nombreHoja // Usar valor calculado o default
            }
        };

        return [{ json: respuesta }];
    } else {
        // Si no hay disponibilidad, buscar alternativas
        const franjasSemana = nombreHoja === mallas.datosMalla.semana 
            ? mallas.datosMalla.franjas 
            : mallas.datosMallaProxSemana.franjas;

        const alternativas = buscarAlternativasParaDia(
            datosDia,
            franjasSemana,
            horasEntrega,
            numeroMuelle
        );

        let mensajeAlternativas = `Lo siento, la franja horaria seleccionada ya no está disponible. `;

        if (alternativas.length > 0) {
            mensajeAlternativas += `Aquí hay algunas alternativas disponibles en el muelle ${numeroMuelle}:\n\n`;

            alternativas.forEach((alt, index) => {
                mensajeAlternativas += `Opción ${index + 1}: ${alt.fecha} - Muelle ${alt.muelle} - De ${alt.horaInicio} a ${alt.horaFin}\n`;
            });

            mensajeAlternativas += "\nSi desea elegir una de estas alternativas, por favor envíe un nuevo mensaje con todos los datos de reprogramación. Este chat se reiniciará para evitar confusiones con mensajes anteriores.";
        } else {
            mensajeAlternativas += `No se encontraron horarios alternativos disponibles para el muelle ${numeroMuelle} en las próximas fechas. Por favor, intente con otra fecha u horario. Este chat se reiniciará para evitar confusiones con mensajes anteriores.`;
        }

        return [{
            json: {
                franjaDisponible: false,
                mensaje: mensajeAlternativas,
                alternativas: alternativas
            }
        }];
    }
} catch (error) {
    console.error("Error en la validación:", error);
    return [{
        json: {
            franjaDisponible: false,
            mensaje: "Error en el proceso de validación: " + error.message
        }
    }];
}

// FUNCIONES AUXILIARES MEJORADAS

// Función para normalizar horas (maneja ambas direcciones)
function normalizarHora(hora) {
    if (!hora) return hora;

    // Convertir a string si no lo es
    const horaStr = hora.toString().trim();

    // Patrón para capturar horas con posible cero inicial
    const patron = /^(0?)(\d{1,2}):(\d{2})\s*([AP]M)$/i;
    const match = horaStr.match(patron);

    if (match) {
        const [, ceroInicial, hora, minutos, periodo] = match;
        // Quitar cero inicial si existe y la hora es de un dígito
        const horaSinCero = parseInt(hora, 10);
        return `${horaSinCero}:${minutos} ${periodo.toUpperCase()}`;
    }

    return horaStr;
}

// Función para comparar horas de manera flexible
function compararHorasFlexible(hora1, hora2) {
    const horaNormalizada1 = normalizarHora(hora1);
    const horaNormalizada2 = normalizarHora(hora2);

    console.log(`Comparando horas: "${horaNormalizada1}" con "${horaNormalizada2}"`);

    return horaNormalizada1 === horaNormalizada2;
}

// Función para normalizar fechas (maneja ambas direcciones)
function normalizarFecha(fecha) {
    if (!fecha) return fecha;

    const fechaStr = fecha.toString().trim();

    // Patrón para capturar fecha con posible cero inicial en el día
    const patron = /^(0?)(\d{1,2})\s+de\s+(\w+)(?:\s+de\s+(\d{4}))?$/i;
    const match = fechaStr.match(patron);

    if (match) {
        const [, ceroInicial, dia, mes, año] = match;
        // Quitar cero inicial si existe
        const diaSinCero = parseInt(dia, 10);

        if (año) {
            return `${diaSinCero} de ${mes.toLowerCase()} de ${año}`;
        } else {
            return `${diaSinCero} de ${mes.toLowerCase()}`;
        }
    }

    return fechaStr;
}

// Función para comparar fechas de manera flexible
function compararFechasFlexible(fecha1, fecha2) {
    const fechaNormalizada1 = normalizarFecha(fecha1);
    const fechaNormalizada2 = normalizarFecha(fecha2);

    console.log(`Comparando fechas: "${fechaNormalizada1}" con "${fechaNormalizada2}"`);

    // Comparación exacta
    if (fechaNormalizada1 === fechaNormalizada2) {
        return true;
    }

    // Comparación sin año (para casos donde una tiene año y otra no)
    const fecha1SinAño = fechaNormalizada1.replace(/\s+de\s+\d{4}$/, '');
    const fecha2SinAño = fechaNormalizada2.replace(/\s+de\s+\d{4}$/, '');

    return fecha1SinAño === fecha2SinAño;
}

// Función para calcular el nombreHoja de la cita original
function calcularNombreHojaOriginal(fechaCitaOriginal, mallas) {
    if (!fechaCitaOriginal) return null;

    const formatosFechaOriginal = convertirFechaISOaLegible(fechaCitaOriginal);
    console.log("Formatos de fecha original:", formatosFechaOriginal);

    // Buscar en malla principal
    for (const dia in mallas.datosMalla.dias) {
        const fechaDiaMalla = mallas.datosMalla.dias[dia].fechaDia;

        if (compararFechasFlexible(fechaDiaMalla, formatosFechaOriginal.conAño) || 
            compararFechasFlexible(fechaDiaMalla, formatosFechaOriginal.sinAño)) {
            console.log(`¡Cita original encontrada en ${mallas.datosMalla.semana}!`);
            return mallas.datosMalla.semana;
        }
    }

    // Buscar en próxima semana
    for (const dia in mallas.datosMallaProxSemana.dias) {
        const fechaDiaMalla = mallas.datosMallaProxSemana.dias[dia].fechaDia;

        if (compararFechasFlexible(fechaDiaMalla, formatosFechaOriginal.conAño) || 
            compararFechasFlexible(fechaDiaMalla, formatosFechaOriginal.sinAño)) {
            console.log(`¡Cita original encontrada en ${mallas.datosMallaProxSemana.semana}!`);
            return mallas.datosMallaProxSemana.semana;
        }
    }

    // Método adicional: por día de la semana y rango de fechas
    const fechaISO = new Date(fechaCitaOriginal);
    const diaNumerico = fechaISO.getDate();
    const mes = fechaISO.getMonth();

    // Función para extraer rango de días de un string como "LUNES 02 AL VIERNES 06 DE JUNIO"
    function extraerRangoDias(rango) {
        const partes = rango.split(" ");
        let diaInicio = null;
        let diaFin = null;
        let mesTexto = null;

        for (let i = 0; i < partes.length; i++) {
            if (/^\d+$/.test(partes[i])) {
                if (diaInicio === null) {
                    diaInicio = parseInt(partes[i]);
                } else {
                    diaFin = parseInt(partes[i]);
                }
            }
            if (i > 0 && partes[i-1] === "DE") {
                mesTexto = partes[i].toLowerCase();
            }
        }

        const meses = {
            'enero': 0, 'febrero': 1, 'marzo': 2, 'abril': 3, 'mayo': 4, 'junio': 5,
            'julio': 6, 'agosto': 7, 'septiembre': 8, 'octubre': 9, 'noviembre': 10, 'diciembre': 11
        };

        return { diaInicio, diaFin, mes: meses[mesTexto] };
    }

    // Verificar rangos
    const rangoMallaPrincipal = extraerRangoDias(mallas.datosMalla.rangoDias);
    console.log("Rango malla principal:", rangoMallaPrincipal);

    if (mes === rangoMallaPrincipal.mes && 
        diaNumerico >= rangoMallaPrincipal.diaInicio && 
        diaNumerico <= rangoMallaPrincipal.diaFin) {
        console.log(`Cita original en rango de ${mallas.datosMalla.semana}`);
        return mallas.datosMalla.semana;
    }

    const rangoProxSemana = extraerRangoDias(mallas.datosMallaProxSemana.rangoDias);
    console.log("Rango próxima semana:", rangoProxSemana);

    if (mes === rangoProxSemana.mes && 
        diaNumerico >= rangoProxSemana.diaInicio && 
        diaNumerico <= rangoProxSemana.diaFin) {
        console.log(`Cita original en rango de ${mallas.datosMallaProxSemana.semana}`);
        return mallas.datosMallaProxSemana.semana;
    }

    console.log(`No se pudo determinar la hoja original, usando default: ${mallas.datosMalla.semana}`);
    return mallas.datosMalla.semana;
}

// Función para convertir una fecha ISO a un formato legible
function convertirFechaISOaLegible(fechaISO) {
    const fechaObj = new Date(fechaISO);
    const dia = fechaObj.getDate();
    const meses = ['enero', 'febrero', 'marzo', 'abril', 'mayo', 'junio', 'julio', 'agosto', 'septiembre', 'octubre', 'noviembre', 'diciembre'];
    const mes = meses[fechaObj.getMonth()];
    const año = fechaObj.getFullYear();

    return {
        conAño: `${dia} de ${mes} de ${año}`,
        sinAño: `${dia} de ${mes}`
    };
}

// Función para calcular la hora final
function calcularHoraFin(horaInicio, horasNecesarias) {
    try {
        const partes = horaInicio.trim().split(/\s+/);
        const horaMinutos = partes[0].split(':');
        const periodo = partes[1]; // AM o PM

        let hora = parseInt(horaMinutos[0], 10);
        let minutos = parseInt(horaMinutos[1] || "0", 10);

        // Convertir a formato 24 horas para el cálculo
        if (periodo.toUpperCase() === "PM" && hora < 12) {
            hora += 12;
        } else if (periodo.toUpperCase() === "AM" && hora === 12) {
            hora = 0;
        }

        // Sumar las horas necesarias
        hora = (hora + horasNecesarias) % 24;

        // Convertir de nuevo a formato 12 horas
        let nuevoPeriodo = "AM";
        if (hora >= 12) {
            nuevoPeriodo = "PM";
            if (hora > 12) {
                hora -= 12;
            }
        }
        if (hora === 0) {
            hora = 12;
        }

        return `${hora}:${minutos.toString().padStart(2, '0')} ${nuevoPeriodo}`;
    } catch (error) {
        console.error("Error al calcular hora fin:", error);
        return "Hora desconocida";
    }
}

// Función para convertir fecha a formato ISO
function convertirFechaAFormatoISO(fechaTexto) {
    try {
        const partes = fechaTexto.split(" de ");
        const dia = parseInt(partes[0], 10);
        const mes = partes[1].toLowerCase();

        const meses = {
            'enero': 0, 'febrero': 1, 'marzo': 2, 'abril': 3, 'mayo': 4, 'junio': 5,
            'julio': 6, 'agosto': 7, 'septiembre': 8, 'octubre': 9, 'noviembre': 10, 'diciembre': 11
        };

        const mesNum = meses[mes];
        if (mesNum === undefined) {
            throw new Error(`Mes no válido: ${mes}`);
        }

        let anio = new Date().getFullYear();
        if (partes.length > 2) {
            anio = parseInt(partes[2], 10);
        }

        return `${anio}-${(mesNum + 1).toString().padStart(2, '0')}-${dia.toString().padStart(2, '0')}`;
    } catch (error) {
        console.error("Error al convertir fecha a formato ISO:", error);
        return "";
    }
}

// Función para buscar alternativas en un día
function buscarAlternativasParaDia(datoDia, franjas, horasNecesarias, numeroMuelle) {
    const alternativas = [];
    const muelleKey = `muelle${numeroMuelle}`;
    const muelle = datoDia[muelleKey];

    if (!muelle) return alternativas;

    // Para cada posible hora de inicio
    for (let i = 0; i <= franjas.length - horasNecesarias; i++) {
        let disponible = true;

        // Verificar franjas consecutivas
        for (let j = 0; j < horasNecesarias; j++) {
            const franja = franjas[i + j];
            if (!muelle[franja.hora] || muelle[franja.hora].estado !== "disponible") {
                disponible = false;
                break;
            }
        }

        if (disponible) {
            const horaInicio = franjas[i].hora;
            const horaFin = calcularHoraFin(horaInicio, horasNecesarias);

            alternativas.push({
                fecha: datoDia.fechaDia,
                muelle: numeroMuelle,
                horaInicio: horaInicio,
                horaFin: horaFin,
                franjasHorarias: franjas.slice(i, i + horasNecesarias).map(f => f.hora)
            });
        }
    }

    return alternativas;
}

