        ordenCompra,
        peso,
        categoria,
        unidades
      }
    });

    horasRestantes--;
    if (horasRestantes > 0) {
      const siguienteFranja = encontrarSiguienteFranja(horaActual, datosMalla.franjas);
      if (siguienteFranja) {
        horaActual = siguienteFranja.hora;
      } else {
        break;
      }
    }
  }

  return franjas;
}

function generarFranjasAntiguas() {
  if (!citaAntigua) return [];

  const franjas = [];
  let horaActual = citaAntigua.horaInicio;
  let horasRestantes = duracionHoras; // Usar la misma duración

  while (horasRestantes > 0) {
    const franjaActual = datosMalla.franjas.find(f => horasIguales(f.hora, horaActual));

    if (!franjaActual) {
      console.log(`No se encontró información para la franja antigua: ${horaActual}`);
      break;
    }

    const filaBase = calcularFilaExcel(horaActual, datosMalla.franjas);
    if (!filaBase) {
      console.log(`No se pudo determinar la fila para la franja antigua: ${horaActual}`);
      break;
    }

    franjas.push({
      hora: horaActual,
