// Nueva función auxiliar para obtener el día de la semana desde una fecha ISO (YYYY-MM-DD)
function obtenerDiaSemanaDesdeISO(fechaISO) {
  if (!fechaISO) return null;
  // Se añade T12:00:00Z para evitar problemas de zona horaria al parsear la fecha
  const fecha = new Date(`${fechaISO}T12:00:00Z`);
  const diasSemana = ["domingo", "lunes", "martes", "miércoles", "jueves", "viernes", "sábado"];
  // getUTCDay() es más seguro para evitar desplazamientos de día por zona horaria
  return diasSemana[fecha.getUTCDay()];
}

// Función modificada para aceptar el día de la semana como parámetro
function procesarFranjas(franjas, nombreHoja, diaSemana) {
  let actualizaciones = [];

  const muelleKey = muelle.toLowerCase().includes("1") ? "muelle1" : "muelle2";

  const mapeoColumnas = {
    'lunes': { 'muelle1': 'C', 'muelle2': 'F' },
    'martes': { 'muelle1': 'I', 'muelle2': 'L' },
    'miércoles': { 'muelle1': 'O', 'muelle2': 'R' },
    'jueves': { 'muelle1': 'U', 'muelle2': 'X' },
    'viernes': { 'muelle1': 'AA', 'muelle2': 'AD' }
  };

  if (!mapeoColumnas[diaSemana] || !mapeoColumnas[diaSemana][muelleKey]) {
    console.log(`No hay mapeo para: día=${diaSemana}, muelle=${muelleKey}`);
    return [];
  }

  const columnaBase = mapeoColumnas[diaSemana][muelleKey];

  function siguienteColumna(columna) {
    if (columna.length === 1) {
      return String.fromCharCode(columna.charCodeAt(0) + 1);
    } else {
      const primerChar = columna.charAt(0);
      const segundoChar = columna.charAt(1);

      if (segundoChar === 'Z') {
        return String.fromCharCode(primerChar.charCodeAt(0) + 1) + 'A';
      } else {
        return primerChar + String.fromCharCode(segundoChar.charCodeAt(0) + 1);
      }
    }
  }

  franjas.forEach(franja => {
    const filaBase = franja.fila;
    const columnaLineas = siguienteColumna(columnaBase);
    const columnaUnd = siguienteColumna(columnaLineas);

    const rangoCeldas = {
      proveedor: `${columnaBase}${filaBase}`,
      ordenCompra: `${columnaBase}${filaBase + 1}`,
      peso: `${columnaBase}${filaBase + 2}`,
      categoria: `${columnaLineas}${filaBase + 2}`,
      unidades: `${columnaUnd}${filaBase + 2}`
    };

    actualizaciones.push(
      { celda: rangoCeldas.proveedor, valor: franja.datos.proveedor, nombreHoja },
      { celda: rangoCeldas.ordenCompra, valor: franja.datos.ordenCompra, nombreHoja },
      { celda: rangoCeldas.peso, valor: franja.datos.peso, nombreHoja },
      { celda: rangoCeldas.categoria, valor: franja.datos.categoria, nombreHoja },
      { celda: rangoCeldas.unidades, valor: franja.datos.unidades, nombreHoja }
    );
  });

  return actualizaciones;
}

