// Funciones auxiliares (mantener las existentes)
function obtenerDiaSemanaDesdeTexto(fechaStr) {
  const regex = /(\d+)\s+de\s+(\w+)\s+de\s+(\d+)/;
  const match = regex.exec(fechaStr);

  if (!match) return null;

  const dia = parseInt(match[1], 10);
  const mesStr = match[2].toLowerCase();
  const año = parseInt(match[3], 10);

  const meses = {
    "enero": 0, "febrero": 1, "marzo": 2, "abril": 3, "mayo": 4, "junio": 5,
    "julio": 6, "agosto": 7, "septiembre": 8, "octubre": 9, "noviembre": 10, "diciembre": 11
  };

  const mesNum = meses[mesStr];
  if (mesNum === undefined) return null;

  const fecha = new Date(año, mesNum, dia);
  const diaSemana = fecha.getDay();
  const diasSemana = ["domingo", "lunes", "martes", "miércoles", "jueves", "viernes", "sábado"];

  return diasSemana[diaSemana];
}

function horasIguales(h1, h2) {
  const normalizar = (h) =>
    h.toString().trim().toLowerCase().replace(/\./g, '').replace(/\s+/g, '').replace(/^0+/, '');
  return normalizar(h1) === normalizar(h2);
}

function calcularFilaExcel(hora, franjas) {
  const franjasOrdenadas = [...franjas].sort((a, b) => {
    function horaAMinutos(hora) {
      const match = hora.match(/(\d+):(\d+)\s*([AP]M)/i);
      if (!match) return 0;

      let horas = parseInt(match[1], 10);
      const minutos = parseInt(match[2], 10);
      const periodo = match[3].toUpperCase();

      if (periodo === 'PM' && horas !== 12) {
        horas += 12;
      } else if (periodo === 'AM' && horas === 12) {
        horas = 0;
      }

      return horas * 60 + minutos;
    }

    return horaAMinutos(a.hora) - horaAMinutos(b.hora);
  });

  const posicion = franjasOrdenadas.findIndex(f => horasIguales(f.hora, hora));

  if (posicion === -1) {
    console.log(`No se encontró la posición para la hora ${hora}`);
    return null;
  }

  const FILA_PRIMERA_FRANJA = 12;
  const INCREMENTO_ESTANDAR = 3;

  if (posicion === 3) {
    return 21;
  } else if (posicion === 4) {
    return 24;
  } else if (posicion === 5) {
    return 27;
  } else if (posicion < 3) {
    return FILA_PRIMERA_FRANJA + (posicion * INCREMENTO_ESTANDAR);
  } else {
    return 27 + ((posicion - 5) * INCREMENTO_ESTANDAR);
  }
}

function encontrarSiguienteFranja(horaActual, franjas) {
  const franjasOrdenadas = [...franjas].sort((a, b) => {
    function horaAMinutos(hora) {
      const match = hora.match(/(\d+):(\d+)\s*([AP]M)/i);
      if (!match) return 0;

      let horas = parseInt(match[1], 10);
      const minutos = parseInt(match[2], 10);
      const periodo = match[3].toUpperCase();

      if (periodo === 'PM' && horas !== 12) {
        horas += 12;
      } else if (periodo === 'AM' && horas === 12) {
        horas = 0;
      }

      return horas * 60 + minutos;
    }

    return horaAMinutos(a.hora) - horaAMinutos(b.hora);
  });

  const posicionActual = franjasOrdenadas.findIndex(f => horasIguales(f.hora, horaActual));

  if (posicionActual === -1 || posicionActual >= franjasOrdenadas.length - 1) {
    return null;
  }

  return franjasOrdenadas[posicionActual + 1];
}

