const historialMensajes = $('Unificación y consistencia de datos').first().json.infoMensaje.historialMensajes;

// Función para convertir fechas en formato "06 de mayo de 2025" a "YYYY-MM-DD HH:mm:ss"
function convertirFecha(fechaTexto) {
  const meses = {
    enero: '01', febrero: '02', marzo: '03', abril: '04', mayo: '05',
    junio: '06', julio: '07', agosto: '08', septiembre: '09',
    octubre: '10', noviembre: '11', diciembre: '12'
  };

  // Dividir la fecha y mapear el mes
  const [dia, de, mes, deNuevo, anio] = fechaTexto.split(' ');
  const mesNumerico = meses[mes.toLowerCase()]; // Convertir el mes a número
  return `${anio}-${mesNumerico}-${dia.padStart(2, '0')} 00:00:00`; // Formato DATETIME
}

// Convertimos la fecha aquí
const fechaCitaConvertida = convertirFecha(cita.fecha);

// 3) Retornamos un solo objeto con todos los datos necesarios
