    }
  };
}

// Analizar las respuestas para determinar a qué operación corresponde cada una
const respuestas = input.responses.map(response => {
  // Extraer información del ID de la respuesta
  const idPartes = response.id.split('_');
  const tipoOperacion = idPartes[0].toLowerCase(); // "reintento", "limpiar", etc.
  const tipoEntidad = idPartes[1]?.toLowerCase(); // "insercion", "eliminacion", etc.
  const celda = idPartes[2] || ""; // La celda como I21, K23, etc.

  // Extraer información de la dirección en la respuesta
  let direccionRespuesta = "";
