    celdaCorrecta,
    statusCode: response.status,
    respuesta: response
  };
});

// Separar las respuestas por tipo de operación
const respuestasInsercion = respuestas.filter(r => 
  r.tipoEntidad === "insercion" || 
  (r.tipoOperacion === "reintento" && input._metadata?.operaciones?.[0]?.tipo === "insercion")
);

const respuestasEliminacion = respuestas.filter(r => 
  r.tipoEntidad === "eliminacion" || 
  (r.tipoOperacion === "reintento" && input._metadata?.operaciones?.[0]?.tipo === "eliminacion")
);

// Determinar éxito por tipo de operación
