                        respuestasInsercion.every(r => r.estadoExitoso && r.celdaCorrecta);

const eliminacionExitosa = respuestasEliminacion.length > 0 && 
                          respuestasEliminacion.every(r => r.estadoExitoso && r.celdaCorrecta);

// Determinar éxito global basado en las operaciones presentes
