  // Si solo hay inserción, solo esa debe ser exitosa
  exitoGlobal = insercionExitosa;
} else if (respuestasEliminacion.length > 0) {
  // Si solo hay eliminación, solo esa debe ser exitosa
  exitoGlobal = eliminacionExitosa;
} else {
  // Si no hay operaciones identificables, considerar como no exitoso
  exitoGlobal = false;
}

// Obtener la información más relevante para cada tipo de operación
const infoInsercion = respuestasInsercion.length > 0 ? respuestasInsercion[0] : null;
const infoEliminacion = respuestasEliminacion.length > 0 ? respuestasEliminacion[0] : null;

