      celda: infoEliminacion.celda,
      direccionRespuesta: infoEliminacion.direccionRespuesta,
      hoja: infoEliminacion.hoja,
      valor: infoEliminacion.valor,
      statusCode: infoEliminacion.statusCode
    } : null
  },
  mensaje: exitoGlobal ? 
