/**
 * Función para determinar si un año es bisiesto
 * Un año es bisiesto si es divisible por 4, excepto aquellos divisibles por 100 
 * que no son divisibles por 400
 */
function esBisiesto(year) {
  return (year % 4 === 0 && year % 100 !== 0) || (year % 400 === 0);
}

/**
 * Función para obtener el número de días en un mes, considerando años bisiestos
 */
function getDiasMes(mes, año) {
  const diasPorMes = {
    1: 31, // enero
    2: esBisiesto(año) ? 29 : 28, // febrero - ajustado para año bisiesto
    3: 31, // marzo
    4: 30, // abril
    5: 31, // mayo
    6: 30, // junio
    7: 31, // julio
    8: 31, // agosto
    9: 30, // septiembre
    10: 31, // octubre
    11: 30, // noviembre
    12: 31  // diciembre
  };

  return diasPorMes[mes];
}

/**
 * Función para eliminar acentos y pasar a minúsculas (para buscar palabras clave sin importar tildes ni mayúsculas).
 */
function normalizarTexto(texto) {
  if (typeof texto !== 'string') return "";
  // Normaliza (NFD) y elimina diacríticos, luego pasa a minúsculas.
  return texto
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, "") // quita acentos
    .toLowerCase();
}

/**
