// (8) NUEVA FUNCIÓN: Buscar día por fecha exacta en todas las mallas
function buscarDiaPorFechaExacta(fechaSolicitada, mallaActual, mallaProxima) {
  console.log(`🔍 BUSCANDO FECHA EXACTA: "${fechaSolicitada}"`);

  const mallas = [
    { malla: mallaActual, nombre: "actual" },
    { malla: mallaProxima, nombre: "proxima" }
  ];

  for (const { malla, nombre } of mallas) {
    console.log(`  📋 Revisando malla "${nombre}"`);

    if (!malla) {
      console.log(`    ❌ Malla "${nombre}" es nula`);
      continue;
    }

    if (!malla.dias) {
      console.log(`    ❌ Malla "${nombre}" no tiene días`);
      continue;
    }

    console.log(`    ✅ Malla "${nombre}" tiene ${Object.keys(malla.dias).length} días`);

    // Recorrer todos los días de la malla
    for (const [nombreDia, datosDia] of Object.entries(malla.dias)) {
      if (!datosDia) {
        console.log(`      ⚠️ Día "${nombreDia}" tiene datos nulos`);
        continue;
      }

      // NORMALIZAR AMBAS FECHAS PARA COMPARACIÓN FLEXIBLE
      const fechaMallaNormalizada = normalizarFechaFlexible(datosDia.fechaDia || "");
      const fechaSolicitadaNormalizada = normalizarFechaFlexible(fechaSolicitada);

      console.log(`      🗓️ Comparando día "${nombreDia}": fechaDia="${datosDia.fechaDia}" (norm: "${fechaMallaNormalizada}") vs fechaSolicitada="${fechaSolicitada}" (norm: "${fechaSolicitadaNormalizada}")`);

      if (fechaMallaNormalizada === fechaSolicitadaNormalizada) {
        console.log(`      🎉 FECHA ENCONTRADA! Día "${nombreDia}" en malla "${nombre}"`);
        return {
          malla: malla,
          nombreDia: nombreDia,
          datosDia: datosDia,
          mallaUsada: nombre
        };
      }
    }

    console.log(`    ❌ Fecha "${fechaSolicitada}" NO encontrada en malla "${nombre}"`);
  }

  console.log(`❌ Fecha "${fechaSolicitada}" NO encontrada en NINGUNA malla`);
  return null;
}

// (9) Verificar disponibilidad de la hora solicitada (NUEVA LÓGICA)
function verificarDisponibilidadHoraSolicitada(
  fechaSolicitada,
  horaSolicitada,
  muelle,
  horasNecesarias,
  mallaActual,
  mallaProxima
) {
  try {
    console.log(`🔍 INICIANDO VERIFICACIÓN DE DISPONIBILIDAD`);
    console.log(`  Fecha solicitada: "${fechaSolicitada}"`);
    console.log(`  Hora solicitada: "${horaSolicitada}"`);
    console.log(`  Muelle: "${muelle}"`);
    console.log(`  Horas necesarias: ${horasNecesarias}`);

    /* ── Buscar el día por fecha exacta ── */
    console.log(`🔍 Buscando fecha exacta: "${fechaSolicitada}"`);
    const resultadoBusqueda = buscarDiaPorFechaExacta(fechaSolicitada, mallaActual, mallaProxima);

    if (!resultadoBusqueda) {
      console.log(`❌ No se encontró la fecha "${fechaSolicitada}" en ninguna malla`);
      return false;
    }

    const { malla, nombreDia, datosDia } = resultadoBusqueda;
    console.log(`✅ Fecha encontrada en malla. Día: "${nombreDia}", fechaDia: "${datosDia.fechaDia}"`);

    /* ── Verificar muelle ── */
    const nombreMuelle = muelle.toLowerCase().replace(/\s+/g, ""); // "MUELLE 1" → "muelle1"
