    if (!datosDia[nombreMuelle]) {
      console.log(`❌ No se encontró el muelle "${nombreMuelle}" para el día ${nombreDia}`);
      console.log(`  Muelles disponibles:`, Object.keys(datosDia).filter(k => k !== 'fechaDia'));
      return false;
    }
    console.log(`✅ Muelle "${nombreMuelle}" encontrado`);

    /* ── Verificar franjas ── */
    if (!malla.franjas || !Array.isArray(malla.franjas) || malla.franjas.length === 0) {
      console.log(`❌ No hay franjas definidas en la malla`);
      return false;
    }

    const franjas = malla.franjas.map(f => f.hora);
    console.log(`🔍 Franjas disponibles:`, franjas);
    console.log(`🔍 Buscando hora "${horaSolicitada}" (limpia: "${limpiarHora(horaSolicitada)}")`);

    const indiceInicio = franjas.findIndex(h => {
      const horaLimpia = limpiarHora(h);
      const horaSolicitadaLimpia = limpiarHora(horaSolicitada);
      console.log(`  Comparando: "${h}" (${horaLimpia}) vs "${horaSolicitada}" (${horaSolicitadaLimpia})`);
      return horaLimpia === horaSolicitadaLimpia;
    });

    if (indiceInicio === -1) {
      console.log(`❌ No se encontró la hora "${horaSolicitada}" en las franjas disponibles`);
      console.log(`  Franjas disponibles:`, franjas);
      return false;
    }
    console.log(`✅ Hora encontrada en índice ${indiceInicio}`);

    if (indiceInicio + horasNecesarias > franjas.length) {
      console.log(`❌ No hay suficientes franjas consecutivas disponibles (necesita ${horasNecesarias})`);
      return false;
    }

    /* ── Verificar disponibilidad de todas las franjas necesarias ── */
    console.log(`🔍 Verificando disponibilidad de ${horasNecesarias} franja(s) consecutiva(s)`);
    for (let i = 0; i < horasNecesarias; i++) {
      const horaFranja = franjas[indiceInicio + i];
      const franja = datosDia[nombreMuelle][horaFranja];

      console.log(`  Revisando franja ${i + 1}/${horasNecesarias}: "${horaFranja}"`);

      if (!franja) {
        console.log(`❌ No se encontró la franja "${horaFranja}" en "${nombreMuelle}"`);
        return false;
      }

      const estadoFranja = (franja.estado || "").toLowerCase().trim();
      console.log(`    Estado: "${franja.estado}" → "${estadoFranja}"`);

      if (estadoFranja !== "disponible") {
        console.log(`❌ La franja "${horaFranja}" no está disponible. Estado: "${franja.estado}"`);
        return false;
      }
      console.log(`    ✅ Franja "${horaFranja}" está disponible`);
    }

    console.log(`🎉 TODAS las franjas están disponibles para ${fechaSolicitada} ${horaSolicitada}`);
    return true;
  } catch (error) {
    console.error(`❌ Error verificando disponibilidad: ${error.message}`);
    console.error(error.stack);
    return false;
  }
}

// (10) Formatear fecha en formato español
function formatearFechaEspanol(fecha) {
  if (!fecha || !(fecha instanceof Date)) {
    return "";
  }

  const dia = fecha.getDate();
  const meses = [
    "enero", "febrero", "marzo", "abril", "mayo", "junio",
    "julio", "agosto", "septiembre", "octubre", "noviembre", "diciembre"
  ];
  const mes = meses[fecha.getMonth()];
  const anio = fecha.getFullYear();

  return `${dia} de ${mes} de ${anio}`;
}

