// Buscar alternativas desde dos días después
function buscarAlternativasDesdeDosDiasDespues(
  fechaActual,
  mallaActual,
  mallaProxima,
  muelle,
  horasNecesarias
) {
  try {
    const fechaDosDiasDespues = new Date(fechaActual);
    fechaDosDiasDespues.setDate(fechaDosDiasDespues.getDate() + 2);

    // Ajustar si cae en fin de semana
    if (fechaDosDiasDespues.getDay() === 0) { // Domingo
      fechaDosDiasDespues.setDate(fechaDosDiasDespues.getDate() + 1);
    } else if (fechaDosDiasDespues.getDay() === 6) { // Sábado
      fechaDosDiasDespues.setDate(fechaDosDiasDespues.getDate() + 2);
    }

    const fechaDosDiasDespuesStr = formatearFechaEspanol(fechaDosDiasDespues);
    const alternativas = buscarAlternativasParaDiaConFechaExacta(
      fechaDosDiasDespuesStr,
      muelle,
      horasNecesarias,
      mallaActual,
      mallaProxima
    );

    // Solo devolver máximo 10 alternativas de ese día
    return alternativas.slice(0, 10);
  } catch (error) {
    console.error(`Error buscando alternativas: ${error.message}`);
    return [];
  }
}

// Buscar alternativas para un día específico usando fecha exacta
function buscarAlternativasParaDiaConFechaExacta(
  fechaTexto,
  muelle,
  horasNecesarias,
  mallaActual,
  mallaProxima
) {
  try {
    const alternativas = [];

    const resultadoBusqueda = buscarDiaPorFechaExacta(fechaTexto, mallaActual, mallaProxima);
    if (!resultadoBusqueda) {
      return alternativas;
    }

    const { malla, datosDia } = resultadoBusqueda;
    const nombreMuelle = muelle.toLowerCase().replace(/\s+/g, "");

    if (!datosDia[nombreMuelle]) {
      return alternativas;
    }

    if (!malla.franjas || !Array.isArray(malla.franjas) || malla.franjas.length === 0) {
      return alternativas;
    }

    const franjasHorario = malla.franjas.map(f => f.hora);

    // Verificar cada posible hora de inicio
    for (let i = 0; i <= franjasHorario.length - horasNecesarias; i++) {
      const horaInicio = franjasHorario[i];
      let todasDisponibles = true;

      // Verificar disponibilidad de todas las franjas consecutivas
      for (let j = 0; j < horasNecesarias; j++) {
        const horaActual = franjasHorario[i + j];
        const franja = datosDia[nombreMuelle][horaActual];

        if (!franja || (franja.estado || "").toLowerCase().trim() !== "disponible") {
          todasDisponibles = false;
          break;
        }
      }

      // Si todas las franjas están disponibles, agregar como alternativa
      if (todasDisponibles) {
        const horaFin = calcularHoraFin(horaInicio, horasNecesarias);
        alternativas.push({
          fecha: fechaTexto,
          horaInicio: horaInicio,
          horaFin: horaFin,
          muelle: muelle
        });
      }
    }

    return alternativas;
  } catch (error) {
    console.error(`Error buscando alternativas para día: ${error.message}`);
    return [];
  }
}

// Buscar alternativas en el mismo día
function buscarAlternativasMismoDia(
  fechaSolicitada,
  muelle,
  horasNecesarias,
  mallaActual,
  mallaProxima
) {
  const alternativas = buscarAlternativasParaDiaConFechaExacta(
    fechaSolicitada,
    muelle,
    horasNecesarias,
    mallaActual,
    mallaProxima
  );

  // Limitar a máximo 8 alternativas del mismo día
  return alternativas.slice(0, 8);
}

// Buscar alternativas en días posteriores
function buscarAlternativasDiasPeriores(
  fechaSolicitada,
  muelle,
  horasNecesarias,
  mallaActual,
  mallaProxima
) {
  try {
    const fechaSolicitadaObj = parsearFechaEspanol(fechaSolicitada);
    const fechaSiguiente = new Date(fechaSolicitadaObj);
    fechaSiguiente.setDate(fechaSiguiente.getDate() + 1);

    // Solo buscar en el día siguiente
    const fechaSiguienteStr = formatearFechaEspanol(fechaSiguiente);
    const alternativasDiaSiguiente = buscarAlternativasParaDiaConFechaExacta(
      fechaSiguienteStr,
      muelle,
      horasNecesarias,
      mallaActual,
      mallaProxima
    );

    // Limitar a máximo 8 alternativas del día siguiente
    return alternativasDiaSiguiente.slice(0, 8);
  } catch (error) {
    console.error(`Error buscando alternativas posteriores: ${error.message}`);
    return [];
  }
}

