return [resultado];

// =========  VALIDACIÓN DE CITA EN CRONOGRAMA  =========
// Valida que la franja siga libre justo después de la confirmación del proveedor

// NUEVO: Detectar retractación al inicio
const validacionSeleccion = $input.first().json.output;

if (validacionSeleccion.esRetractacion === true) {
  return [{
    json: {
      franjaDisponible: false,
      esRetractacion: true,
      forzarReset: true,
      motivoRetractacion: validacionSeleccion.motivoRetractacion,
      mensajeReset: "🔄 **Proceso reiniciado por cambios múltiples**\n\nPara evitar errores, cuando te presento opciones, por favor elige una de la lista.\n\n📝 Proporciona todos los datos completos en un solo mensaje:\n• Proveedor\n• Fecha\n• Hora\n• Categoría\n• Peso\n• Unidades\n• Orden de compra"
    }
  }];
