// **NUEVA FUNCIONALIDAD: Optimización de franjas cruzadas**
function calcularFranjasOptimizadas() {
  if (!citaAntigua) {
    // Si no hay cita antigua, proceder normalmente
    return {
      franjasAEliminar: [],
      franjasAInsertar: generarFranjasNuevas(),
      optimizacionAplicada: false,
      tipoOperacion: "nueva_cita"
    };
  }

  // Verificar si es el mismo día
  const fechaNueva = new Date(fechaFormateada);
  const fechaAntigua = new Date(citaAntigua.fechaFormateada);

  const esMismoDia = fechaNueva.toDateString() === fechaAntigua.toDateString();

  if (!esMismoDia) {
    // Días diferentes: eliminar todas las antiguas, insertar todas las nuevas
    return {
      franjasAEliminar: generarFranjasAntiguas(),
      franjasAInsertar: generarFranjasNuevas(),
      optimizacionAplicada: false,
      tipoOperacion: "cambio_dia"
    };
  }

  // **MISMO DÍA: Aplicar optimización**
  const franjasAntiguas = generarFranjasAntiguas();
  const franjasNuevas = generarFranjasNuevas();

  // Calcular intersección (franjas que se cruzan)
  const franjasAntiguasSet = new Set(franjasAntiguas.map(f => f.hora));
  const franjasNuevasSet = new Set(franjasNuevas.map(f => f.hora));

  const franjasComunes = franjasAntiguas.filter(f => franjasNuevasSet.has(f.hora));
  const franjasComnesSet = new Set(franjasComunes.map(f => f.hora));

  // Calcular operaciones optimizadas
  const franjasAEliminar = franjasAntiguas.filter(f => !franjasComnesSet.has(f.hora));
  const franjasAInsertar = franjasNuevas.filter(f => !franjasComnesSet.has(f.hora));

  console.log(`Optimización aplicada:\n    - Franjas antiguas: ${franjasAntiguas.length}\n    - Franjas nuevas: ${franjasNuevas.length}\n    - Franjas comunes (mantenidas): ${franjasComunes.length}\n    - Franjas a eliminar: ${franjasAEliminar.length}\n    - Franjas a insertar: ${franjasAInsertar.length}\n    - Operaciones evitadas: ${franjasComunes.length * 2}`);

  return {
    franjasAEliminar,
    franjasAInsertar,
    franjasComunes,
    optimizacionAplicada: true,
    tipoOperacion: "mismo_dia_optimizado",
    estadisticas: {
      operacionesEvitadas: franjasComunes.length * 2,
      eficiencia: ((franjasComunes.length * 2) / (franjasAntiguas.length + franjasNuevas.length) * 100).toFixed(2) + '%'
    }
  };
}

