                respuesta.body.values[0] && respuesta.body.values[0][0] !== undefined ? 
                respuesta.body.values[0][0] : null;

  // Verificar el código de estado HTTP
  const estadoExitoso = respuesta.status >= 200 && respuesta.status < 300;

  return {
    id: respuesta.id,
    tipoOperacion,
    celda,
    direccionRespuesta,
    hoja,
