DECLARE @nowBogota    DATETIME;

/* Obtener la fecha/hora actual en zona horaria de Bogotá           */
/* (SQL Server 2016+ ‑ requiere soporte AT TIME ZONE)                */
SET @nowBogota =
    CONVERT(DATETIME, SYSDATETIMEOFFSET() AT TIME ZONE 'SA Pacific Standard Time');

/* ─────────────  UPDATE  ───────────── */
