};

// Verificar si hay campos faltantes
if (respuesta.camposFaltantes && respuesta.camposFaltantes.length > 0) {
  respuesta.todoCorrecto = false;
  respuesta.mensajeSistema = generarMensajeCamposFaltantes(respuesta.camposFaltantes);
} else {
  respuesta.todoCorrecto = true;
  respuesta.mensajeSistema = `Entendido. Estás solicitando cancelar la cita 🔖 ${respuesta.idCita} con orden de compra ${respuesta.ordenCompra}. Verificaré si es posible realizar la cancelación.`;
}

function generarMensajeCamposFaltantes(camposFaltantes) {
  let mensaje = "Para poder cancelar tu cita, necesito ";

  if (camposFaltantes.includes("idCita")) {
    mensaje += "el número de cita 🔖 que aparece en tu confirmación, ";
  }

  if (camposFaltantes.includes("ordenCompra")) {
