// Función para normalizar texto (quitar espacios, convertir a minúsculas)
function normalizarTexto(texto) {
  if (!texto) return "";
  return texto.toString().trim().toLowerCase();
}

// Función para buscar cita en una malla específica
function buscarCitaEnMalla(malla, citaBuscar) {
  const diasSemana = ['lunes', 'martes', 'miércoles', 'jueves', 'viernes'];
  const muelles = ['muelle1', 'muelle2'];

  for (const dia of diasSemana) {
    if (!malla.dias[dia]) continue;

    for (const muelle of muelles) {
      if (!malla.dias[dia][muelle]) continue;

      // Revisar todas las franjas horarias
      for (const [hora, citaMalla] of Object.entries(malla.dias[dia][muelle])) {
        // Solo revisar citas ocupadas
        if (citaMalla.estado !== 'ocupado') continue;

        // Comparar campos clave
        const proveedorCoincide = normalizarTexto(citaMalla.proveedor) === normalizarTexto(citaBuscar.nombre);
        const ordenCoincide = normalizarTexto(citaMalla.ordenCompra) === normalizarTexto(citaBuscar.orden_compra);

        // Verificar peso (puede ser number o string)
        let pesoCoincide = false;
        const pesoBD = parseFloat(citaBuscar.peso) || 0;
        const pesoMalla = parseFloat(citaMalla.peso) || 0;
        pesoCoincide = Math.abs(pesoBD - pesoMalla) < 0.01; // tolerancia para decimales

        // Verificar unidades
        let unidadesCoincide = false;
        const unidadesBD = parseFloat(citaBuscar.unidades) || 0;
        const unidadesMalla = parseFloat(citaMalla.unidades) || 0;
        unidadesCoincide = Math.abs(unidadesBD - unidadesMalla) < 0.01;

        // Si coinciden los campos principales, es la misma cita
        if (proveedorCoincide && ordenCoincide && pesoCoincide && unidadesCoincide) {
          return {
            encontrada: true,
            ubicacion: {
              malla: malla.semana,
              dia: dia,
              hora: hora,
              muelle: muelle,
              fechaDia: malla.dias[dia].fechaDia || "Fecha no disponible"
            },
            detalles: citaMalla
          };
        }
      }
    }
  }

  return { encontrada: false };
}

