// Funciones auxiliares (mantener las mismas del nodo original)
function calcularFilaExcel(hora, franjas) {
  const franjasOrdenadas = [...franjas].sort((a, b) => {
    function horaAMinutos(hora) {
      const match = hora.match(/(\\d+):(\\d+)\\s*([AP]M)/i);
      if (!match) return 0;

      let horas = parseInt(match[1], 10);
      const minutos = parseInt(match[2], 10);
      const periodo = match[3].toUpperCase();

      if (periodo === 'PM' && horas !== 12) {
        horas += 12;
      } else if (periodo === 'AM' && horas === 12) {
        horas = 0;
      }

      return horas * 60 + minutos;
    }

    return horaAMinutos(a.hora) - horaAMinutos(b.hora);
  });

  const posicion = franjasOrdenadas.findIndex(f => 
    f.hora.trim().toLowerCase() === hora.trim().toLowerCase()
  );

  if (posicion === -1) {
    console.log(`No se encontró la posición para la hora ${hora}`);
    return null;
  }

  const FILA_PRIMERA_FRANJA = 12;
  const INCREMENTO_ESTANDAR = 3;

  if (posicion === 3) {
    return 21;
  } else if (posicion === 4) {
    return 24;
  } else if (posicion === 5) {
    return 27;
  } else if (posicion < 3) {
    return FILA_PRIMERA_FRANJA + (posicion * INCREMENTO_ESTANDAR);
  } else {
    return 27 + ((posicion - 5) * INCREMENTO_ESTANDAR);
  }
}

function procesarFranjas(franjas, nombreHoja) {
  let actualizaciones = [];

  const diaSemana = ubicacionCita.dia;
  const muelleKey = ubicacionCita.muelle.toLowerCase().includes("1") ? "muelle1" : "muelle2";

  const mapeoColumnas = {
    'lunes': { 'muelle1': 'C', 'muelle2': 'F' },
    'martes': { 'muelle1': 'I', 'muelle2': 'L' },
    'miércoles': { 'muelle1': 'O', 'muelle2': 'R' },
    'jueves': { 'muelle1': 'U', 'muelle2': 'X' },
    'viernes': { 'muelle1': 'AA', 'muelle2': 'AD' }
  };

  if (!mapeoColumnas[diaSemana] || !mapeoColumnas[diaSemana][muelleKey]) {
    console.log(`No hay mapeo para: día=${diaSemana}, muelle=${muelleKey}`);
    return [];
  }

  const columnaBase = mapeoColumnas[diaSemana][muelleKey];

  function siguienteColumna(columna) {
    if (columna.length === 1) {
      return String.fromCharCode(columna.charCodeAt(0) + 1);
    } else {
      const primerChar = columna.charAt(0);
      const segundoChar = columna.charAt(1);

      if (segundoChar === 'Z') {
        return String.fromCharCode(primerChar.charCodeAt(0) + 1) + 'A';
      } else {
        return primerChar + String.fromCharCode(segundoChar.charCodeAt(0) + 1);
      }
    }
  }

  franjas.forEach((franja, index) => {
    const filaBase = franja.fila;
    const columnaLineas = siguienteColumna(columnaBase);
    const columnaUnd = siguienteColumna(columnaLineas);

    const rangoCeldas = {
      proveedor: `${columnaBase}${filaBase}`,
      ordenCompra: `${columnaBase}${filaBase + 1}`,
      peso: `${columnaBase}${filaBase + 2}`,
      categoria: `${columnaLineas}${filaBase + 2}`,
      unidades: `${columnaUnd}${filaBase + 2}`
    };

    console.log(`Limpiando franja ${index + 1}: ${franja.hora} - Celdas: ${JSON.stringify(rangoCeldas)}`);

    // Para cancelación, todos los valores son vacíos
    actualizaciones.push(
      { celda: rangoCeldas.proveedor, valor: "", nombreHoja },
      { celda: rangoCeldas.ordenCompra, valor: "", nombreHoja },
      { celda: rangoCeldas.peso, valor: "", nombreHoja },
      { celda: rangoCeldas.categoria, valor: "", nombreHoja },
      { celda: rangoCeldas.unidades, valor: "", nombreHoja }
    );
  });

  return actualizaciones;
}

