const response = input.responses[0];

// Extraer información de la solicitud original del ID
const idPartes = response.id.split('_');
const tipoOperacion = idPartes[0]; // "cancelacion"
const celdaOriginal = idPartes[1];
const indice = idPartes[2];

// Verificar que la respuesta tiene un cuerpo
if (!response.body || !response.body.address) {
  console.log(`Error: La respuesta no contiene información sobre la dirección de celda`);
  return {
    json: {
      error: "RESPUESTA_SIN_DIRECCION",
      mensaje: "La respuesta no incluye información sobre la dirección de celda",
      requestId: response.id,
      status: response.status,
      celdaOriginal: celdaOriginal,
      exito: false
    }
  };
}

// Extraer la dirección de celda de la respuesta
let direccionRespuesta = "";
if (response.body && response.body.address) {
  const partesDireccion = response.body.address.split('!');
  if (partesDireccion.length > 1) {
    direccionRespuesta = partesDireccion[1];
  }
}

// Obtener el valor de la respuesta (debe ser vacío para cancelación)
const valorRespuesta = response.body.values && response.body.values[0] && response.body.values[0][0];

// Verificar si la operación se realizó en la celda correcta
const celdaCorrecta = direccionRespuesta === celdaOriginal;

// Verificar el código de estado HTTP
const estadoExitoso = response.status >= 200 && response.status < 300;

// Para cancelación, también verificamos que el valor sea vacío
const valorCorrecto = tipoOperacion === "cancelacion" ? valorRespuesta === "" : true;

// Crear objeto para almacenar el resultado de la verificación
const resultado = {
  exito: celdaCorrecta && estadoExitoso && valorCorrecto,
  tipoOperacion: tipoOperacion,
  celdaOriginal: celdaOriginal,
  celdaInsertada: direccionRespuesta,
  valorInsertado: valorRespuesta,
  statusCode: response.status,
  requestId: response.id,
  indice: indice,
  celdasDiferentes: !celdaCorrecta && estadoExitoso,
  falloEstado: !estadoExitoso,
  falloValor: !valorCorrecto
};

const chatId = $('Organiza el Mensaje').first().json.infoMensaje.chat_id;

if (!resultado.exito) {
  console.log(`Error en ${tipoOperacion}: celda=${celdaOriginal}, insertada=${direccionRespuesta}, status=${response.status}, valor="${valorRespuesta}"`);
} else {
  console.log(`${tipoOperacion} exitosa en celda ${celdaOriginal}`);
}

// Incluir la respuesta original para tener toda la información
resultado.respuestaOriginal = response;

// Retornar el resultado de la verificación
return {
  json: resultado,
  chat_id: chatId
};

// Código para limpiar una celda donde se insertó incorrectamente un valor
// Actualizado para manejar la nueva estructura con operaciones de inserción y eliminación
const input = $input.first().json;

// Constantes para los IDs de SharePoint/OneDrive
const siteId = "gexpresscargo.sharepoint.com,a592f886-4560-4e62-9646-1eee7add7abe,8b514073-41ae-4619-8f76-724bd912bc65";
const driveId = "b!hviSpWBFYk6WRh7uet16vnNAUYuuQRlGj3ZyS9kSvGVZ4DSg6c5MRqYYm2MdAER8";
const itemId = "01PPLUUYXY5E2GK3SJ25EZLJPUYJ43H3ME";

// Verificar que tenemos la información necesaria
if (!input || (!input.operaciones && !input.celdaInsertada)) {
  console.log('Información insuficiente para limpiar la celda incorrecta');
  return {
    json: {
      error: "INFORMACION_INSUFICIENTE",
      mensaje: "No hay suficiente información para limpiar la celda incorrecta"
    }
  };
}

// Determinar qué celdas necesitan limpieza
const celdasALimpiar = [];

// Verificar si estamos usando la nueva estructura con operaciones
if (input.operaciones) {
  // Nueva estructura: usar información de las operaciones
  const { insercion, eliminacion } = input.operaciones;

  // Si la inserción falló, agregarla para limpieza
  if (insercion && !insercion.exito && insercion.celdaRespuesta) {
    celdasALimpiar.push({
      celda: insercion.celdaRespuesta,
      hoja: insercion.hoja || ""
    });
  }

  // Si la eliminación falló, agregarla para limpieza
  if (eliminacion && !eliminacion.exito && eliminacion.celdaRespuesta) {
    celdasALimpiar.push({
      celda: eliminacion.celdaRespuesta,
      hoja: eliminacion.hoja || ""
    });
  }
} else {
  // Estructura anterior: solo tenemos información de inserción
  // Obtener la celda a limpiar y la información de la hoja
  const celdaALimpiar = input.celdaInsertada;
  let hojaExcel = input.nombreHoja || ""; // Usar el nombreHoja del input

  // Intentar extraer el nombre de la hoja de la dirección en la respuesta si no está disponible
  if ((!hojaExcel || hojaExcel === "") && input.respuestaOriginal?.body?.address) {
    const direccionCompleta = input.respuestaOriginal.body.address;
    hojaExcel = direccionCompleta.split('!')[0].replace(/'/g, '');
  }

  celdasALimpiar.push({
    celda: celdaALimpiar,
    hoja: hojaExcel
  });
}

// Si no hay celdas para limpiar, devolver error
if (celdasALimpiar.length === 0) {
  console.log('No se identificaron celdas para limpiar');
  return {
    json: {
      error: "NO_HAY_CELDAS",
      mensaje: "No se identificaron celdas que necesiten limpieza",
      input: input
    }
  };
}

// Crear solicitudes para limpiar cada celda identificada
const solicitudes = celdasALimpiar.map((info, index) => {
  console.log(`Limpiando celda incorrecta ${info.celda} en hoja ${info.hoja}`);

  return {
    id: `Limpiar_${info.celda}_${Date.now()}_${index}`,
    method: "PATCH",
    url: `/sites/${siteId}/drives/${driveId}/items/${itemId}/workbook/worksheets/${encodeURIComponent(info.hoja)}/range(address='${info.celda}')`,
    body: {
      values: [[""]] // Valor vacío para limpiar la celda
    },
    headers: {
      "Content-Type": "application/json"
    }
  };
});

// Crear la solicitud para limpiar las celdas
const requestBody = {
  requests: solicitudes,
  _metadata: {
    accion: "limpiar",
    celdasLimpiadas: celdasALimpiar,
    datoOriginal: input
  }
};

// Retornar el request para limpiar la celda
return {
  json: requestBody
};

// Código para reintentar la inserción en la celda correcta
// Actualizado para manejar la nueva estructura con operaciones de inserción y eliminación
const input = $input.first().json;

// Constantes para los IDs de SharePoint/OneDrive
const siteId = "gexpresscargo.sharepoint.com,a592f886-4560-4e62-9646-1eee7add7abe,8b514073-41ae-4619-8f76-724bd912bc65";
const driveId = "b!hviSpWBFYk6WRh7uet16vnNAUYuuQRlGj3ZyS9kSvGVZ4DSg6c5MRqYYm2MdAER8";
const itemId = "01PPLUUYXY5E2GK3SJ25EZLJPUYJ43H3ME";

// Si el input viene del nodo anterior de limpiar celda, extraer el dato original
const datoOriginal = input._metadata?.datoOriginal || input;

// Verificar que tenemos la información necesaria
if (!datoOriginal) {
  console.log('Información insuficiente para reintentar la inserción/eliminación');
  return {
    json: {
      error: "INFORMACION_INSUFICIENTE",
      mensaje: "No hay suficiente información para reintentar las operaciones"
    }
  };
}

// Operaciones a reintentar
const operacionesAReintentar = [];

// Determinar qué operaciones necesitan reintento
if (datoOriginal.operaciones) {
  // Nueva estructura: evaluar inserción y eliminación
  const { insercion, eliminacion } = datoOriginal.operaciones;

  // Verificar si la inserción necesita reintento
  if (insercion && !insercion.exito) {
    operacionesAReintentar.push({
      tipo: "insercion",
      celda: insercion.celda,
      valor: insercion.valor,
      hoja: insercion.hoja || datoOriginal.resultadoDetallado?.operaciones?.insercion?.hoja || ""
    });
  }

  // Verificar si la eliminación necesita reintento
  if (eliminacion && !eliminacion.exito) {
    operacionesAReintentar.push({
      tipo: "eliminacion",
      celda: eliminacion.celda,
      valor: "", // Para eliminación, siempre usamos valor vacío
      hoja: eliminacion.hoja || datoOriginal.resultadoDetallado?.operaciones?.eliminacion?.hoja || ""
    });
  }
} else {
  // Estructura anterior: solo tenemos información de inserción
  // Obtener la celda correcta donde insertar y el valor a insertar
  const celdaCorrecta = datoOriginal.celdaOriginal;

  // Intentar obtener el valor correcto a insertar
  let valorAInsertar;

  // Primero, verificar si tenemos el valor de la respuesta original
  if (datoOriginal.valorInsertado !== undefined) {
    valorAInsertar = datoOriginal.valorInsertado;
  } 
  // Si no, intentar obtenerlo del body de la respuesta
  else if (datoOriginal.respuestaOriginal?.body?.values?.[0]?.[0] !== undefined) {
    valorAInsertar = datoOriginal.respuestaOriginal.body.values[0][0];
  } 
  // Como último recurso, usar un valor vacío
  else {
    valorAInsertar = "";
    console.log('No se pudo determinar el valor a insertar, usando valor vacío');
  }

  // Obtener la hoja de Excel - primero del dato original
  let hojaExcel = datoOriginal.nombreHoja || ""; 

  // Si no está disponible en el objeto principal, intentar obtenerlo de otros lugares
  if (!hojaExcel || hojaExcel === "") {
    // Verificar si está en el _metadata
    hojaExcel = input._metadata?.hoja || "";

    // Como último recurso, extraer de la dirección en la respuesta
    if ((!hojaExcel || hojaExcel === "") && datoOriginal.respuestaOriginal?.body?.address) {
      const direccionCompleta = datoOriginal.respuestaOriginal.body.address;
      hojaExcel = direccionCompleta.split('!')[0].replace(/'/g, '');
    }
  }

  operacionesAReintentar.push({
    tipo: "insercion",
    celda: celdaCorrecta,
    valor: valorAInsertar,
    hoja: hojaExcel
  });
}

// Si no hay operaciones para reintentar, devolver error
if (operacionesAReintentar.length === 0) {
  console.log('No se identificaron operaciones para reintentar');
  return {
    json: {
      error: "NO_HAY_OPERACIONES",
      mensaje: "No se identificaron operaciones que necesiten reintento",
      input: datoOriginal
    }
  };
}

// Crear solicitudes para cada operación a reintentar
const solicitudes = operacionesAReintentar.map((op, index) => {
  // Determinar el tipo de valor y formatearlo adecuadamente
  let valorFormateado;
  if (typeof op.valor === 'number') {
    valorFormateado = [[op.valor]]; // Mantener el tipo numérico
  } else {
    valorFormateado = [[op.valor.toString()]]; // Convertir a string para otros tipos
  }

  console.log(`Reintentando ${op.tipo} en celda ${op.celda} de la hoja ${op.hoja} con valor "${op.valor}"`);

  return {
    id: `Reintento_${op.tipo}_${op.celda}_${Date.now()}_${index}`,
    method: "PATCH",
    url: `/sites/${siteId}/drives/${driveId}/items/${itemId}/workbook/worksheets/${encodeURIComponent(op.hoja)}/range(address='${op.celda}')`,
    body: {
      values: valorFormateado
    },
    headers: {
      "Content-Type": "application/json"
    }
  };
});

// Crear la solicitud para reintentar las operaciones
const requestBody = {
  requests: solicitudes,
  _metadata: {
    accion: "reintentar",
    operaciones: operacionesAReintentar,
    datoOriginal: datoOriginal,
    intentos: (datoOriginal._metadata?.intentos || 0) + 1
  }
};

// Retornar el request para reintentar las operaciones
return {
  json: requestBody
};

// Código para verificar si la inserción y eliminación fueron exitosas
// Actualizado para manejar la nueva estructura
const input = $input.first().json;

// Verificar que tenemos la estructura esperada en la respuesta
if (!input || !input.responses || !Array.isArray(input.responses)) {
  console.log('Estructura de datos de respuesta no válida');
  return {
    json: {
      error: "RESPUESTA_INVALIDA",
      mensaje: "La estructura de datos de la respuesta no es válida",
      exito: false
    }
  };
}

// Si no hay respuestas, devolver error
if (input.responses.length === 0) {
  console.log('No se recibieron respuestas del servidor');
  return {
    json: {
      error: "SIN_RESPUESTAS",
      mensaje: "No se recibieron respuestas del servidor",
      exito: false
    }
  };
}

// Analizar las respuestas para determinar a qué operación corresponde cada una
const respuestas = input.responses.map(response => {
  // Extraer información del ID de la respuesta
  const idPartes = response.id.split('_');
  const tipoOperacion = idPartes[0].toLowerCase(); // "reintento", "limpiar", etc.
  const tipoEntidad = idPartes[1]?.toLowerCase(); // "insercion", "eliminacion", etc.
  const celda = idPartes[2] || ""; // La celda como I21, K23, etc.

  // Extraer información de la dirección en la respuesta
  let direccionRespuesta = "";
  let hojaExcel = "";

  if (response.body && response.body.address) {
    const direccionCompleta = response.body.address;
    const partesDireccion = direccionCompleta.split('!');

    if (partesDireccion.length > 1) {
      // Quitar las comillas simples del nombre de la hoja
      hojaExcel = partesDireccion[0].replace(/'/g, '');
      direccionRespuesta = partesDireccion[1];
    }
  }

  // Obtener el valor de la respuesta
  const valor = response.body?.values?.[0]?.[0];

  // Verificar si la operación fue exitosa (código 200-299)
  const estadoExitoso = response.status >= 200 && response.status < 300;

  // Verificar si la celda en la respuesta coincide con la esperada
  const celdaCorrecta = direccionRespuesta === celda;

  return {
    id: response.id,
    tipoOperacion,
    tipoEntidad,
    celda,
    direccionRespuesta,
    hoja: hojaExcel,
    valor,
    estadoExitoso,
    celdaCorrecta,
    statusCode: response.status,
    respuesta: response
  };
});

// Separar las respuestas por tipo de operación
const respuestasInsercion = respuestas.filter(r => 
  r.tipoEntidad === "insercion" || 
  (r.tipoOperacion === "reintento" && input._metadata?.operaciones?.[0]?.tipo === "insercion")
);

const respuestasEliminacion = respuestas.filter(r => 
  r.tipoEntidad === "eliminacion" || 
  (r.tipoOperacion === "reintento" && input._metadata?.operaciones?.[0]?.tipo === "eliminacion")
);

// Determinar éxito por tipo de operación
const insercionExitosa = respuestasInsercion.length > 0 && 
                        respuestasInsercion.every(r => r.estadoExitoso && r.celdaCorrecta);

const eliminacionExitosa = respuestasEliminacion.length > 0 && 
                          respuestasEliminacion.every(r => r.estadoExitoso && r.celdaCorrecta);

// Determinar éxito global basado en las operaciones presentes
let exitoGlobal;

if (respuestasInsercion.length > 0 && respuestasEliminacion.length > 0) {
  // Si hay ambas operaciones, ambas deben ser exitosas
  exitoGlobal = insercionExitosa && eliminacionExitosa;
} else if (respuestasInsercion.length > 0) {
  // Si solo hay inserción, solo esa debe ser exitosa
  exitoGlobal = insercionExitosa;
} else if (respuestasEliminacion.length > 0) {
  // Si solo hay eliminación, solo esa debe ser exitosa
  exitoGlobal = eliminacionExitosa;
} else {
  // Si no hay operaciones identificables, considerar como no exitoso
  exitoGlobal = false;
}

// Obtener la información más relevante para cada tipo de operación
const infoInsercion = respuestasInsercion.length > 0 ? respuestasInsercion[0] : null;
const infoEliminacion = respuestasEliminacion.length > 0 ? respuestasEliminacion[0] : null;

// Crear el resultado detallado
const resultadoDetallado = {
  exito: exitoGlobal,
  operaciones: {
    insercion: infoInsercion ? {
      exito: infoInsercion.estadoExitoso && infoInsercion.celdaCorrecta,
      celda: infoInsercion.celda,
      direccionRespuesta: infoInsercion.direccionRespuesta,
      hoja: infoInsercion.hoja,
      valor: infoInsercion.valor,
      statusCode: infoInsercion.statusCode
    } : null,
    eliminacion: infoEliminacion ? {
      exito: infoEliminacion.estadoExitoso && infoEliminacion.celdaCorrecta,
      celda: infoEliminacion.celda,
      direccionRespuesta: infoEliminacion.direccionRespuesta,
      hoja: infoEliminacion.hoja,
      valor: infoEliminacion.valor,
      statusCode: infoEliminacion.statusCode
    } : null
  },
  mensaje: exitoGlobal ? 
    "Todas las operaciones se completaron exitosamente" : 
    "Hubo problemas con una o más operaciones",
  respuestasCompletas: respuestas
};

// Crear el resultado compatible con el formato anterior
const resultadoCompatible = {
  exito: exitoGlobal,
  // Usar la primera respuesta para compatibilidad
  celdaOriginal: infoInsercion?.celda || infoEliminacion?.celda || "",
  celdaInsertada: infoInsercion?.direccionRespuesta || infoEliminacion?.direccionRespuesta || "",
  valorInsertado: infoInsercion?.valor || infoEliminacion?.valor || "",
  statusCode: infoInsercion?.statusCode || infoEliminacion?.statusCode || 0,
  requestId: infoInsercion?.id || infoEliminacion?.id || "",
  indice: infoInsercion?.celda || infoEliminacion?.celda || "",
  celdasDiferentes: (infoInsercion && infoInsercion.celda !== infoInsercion.direccionRespuesta) ||
                   (infoEliminacion && infoEliminacion.celda !== infoEliminacion.direccionRespuesta),
  falloEstado: !exitoGlobal,
  // Información adicional para la versión mejorada
  resultadoDetallado: resultadoDetallado
};

// Registrar el resultado en consola
if (exitoGlobal) {
  console.log("Operaciones completadas exitosamente:");
  if (infoInsercion) {
    console.log(`- Inserción en hoja ${infoInsercion.hoja}, celda ${infoInsercion.celda}, valor: "${infoInsercion.valor}"`);
  }
  if (infoEliminacion) {
    console.log(`- Eliminación en hoja ${infoEliminacion.hoja}, celda ${infoEliminacion.celda}`);
  }
} else {
  console.log("Problemas detectados en las operaciones:");
  if (infoInsercion && (!infoInsercion.estadoExitoso || !infoInsercion.celdaCorrecta)) {
    console.log(`- Fallo en inserción: celda=${infoInsercion.celda}, dirección respuesta=${infoInsercion.direccionRespuesta}, status=${infoInsercion.statusCode}`);
  }
  if (infoEliminacion && (!infoEliminacion.estadoExitoso || !infoEliminacion.celdaCorrecta)) {
    console.log(`- Fallo en eliminación: celda=${infoEliminacion.celda}, dirección respuesta=${infoEliminacion.direccionRespuesta}, status=${infoEliminacion.statusCode}`);
  }
}

// Retornar el resultado
return {
  json: resultadoCompatible
};

/*
===========================================================
  Nodo Code - VALIDAR INSERCIONES EN EXCEL (n8n)
  • Analiza la salida del “Loop Over Uno a Uno”.
  • Agrupa los ítems por franja (campo `indice` si existe;
    de lo contrario, cada bloque de 5 ítems forma una franja).
  • Verifica que cada inserción tenga:
        exito === true
        statusCode === 200
        falloEstado === false
  • Devuelve un único objeto JSON con:
        – Resumen global.
        – Detalle por franja (éxitos / fallos y celdas con error).
===========================================================
*/

// 1) Obtener todos los ítems que llegan al nodo
const items = $input.all();

// 2) Función auxiliar para determinar el identificador de la franja
function obtenerIdFranja(item, idx) {
  // Si existe 'indice', úsalo; de lo contrario agrupa cada 5 ítems
  if (item.json && item.json.indice !== undefined && item.json.indice !== null) {
    return String(item.json.indice).trim();
  }
  // +1 para que las franjas queden 1-basadas
  return String(Math.floor(idx / 5) + 1);
}

// 3) Agrupar ítems por franja
const franjas = {};
items.forEach((item, idx) => {
  const idFranja = obtenerIdFranja(item, idx);
  if (!franjas[idFranja]) {
    franjas[idFranja] = { id: idFranja, items: [] };
  }
  franjas[idFranja].items.push(item.json);
});

// 4) Analizar cada franja y construir el detalle
const detallesPorFranja = Object.values(franjas).map(franja => {
  const totalOps   = franja.items.length;
  const exitosas   = franja.items.filter(i =>
    i.exito === true &&
    i.statusCode === 200 &&
    i.falloEstado === false
  ).length;

  const fallidas   = totalOps - exitosas;
  const exitoTotal = fallidas === 0;

  // Celdas con error para un diagnóstico claro
  const celdasFallidas = franja.items
    .filter(i => !(i.exito && i.statusCode === 200 && !i.falloEstado))
    .map(i => ({
      celdaOriginal   : i.celdaOriginal,
      celdaInsertada  : i.celdaInsertada,
      statusCode      : i.statusCode,
      falloEstado     : i.falloEstado,
      celdasDiferentes: i.celdasDiferentes,
      mensaje         : 'Inserción fallida'
    }));

  return {
    idFranja                : franja.id,
    exitoFranja             : exitoTotal,
    operacionesTotales      : totalOps,
    operacionesExitosas     : exitosas,
    operacionesFallidas     : fallidas,
    porcentajeExitosas      : ((exitosas / totalOps) * 100).toFixed(2) + '%',
    celdasFallidas
  };
});

// 5) Resumen global
const operacionesTotales   = items.length;
const operacionesExitosas  = detallesPorFranja.reduce((sum, f) => sum + f.operacionesExitosas, 0);
const operacionesFallidas  = operacionesTotales - operacionesExitosas;

const franjasTotales   = detallesPorFranja.length;
const franjasExitosas  = detallesPorFranja.filter(f => f.exitoFranja).length;
const franjasFallidas  = franjasTotales - franjasExitosas;


const resumen = {
  estadoGeneral              : franjasFallidas === 0 ? 'ÉXITO_COMPLETO' : 'ÉXITO_PARCIAL',
  franjasTotales,
  franjasExitosas,
  franjasFallidas,
  porcentajeFranjasExitosas  : ((franjasExitosas / franjasTotales) * 100).toFixed(2) + '%',
  operacionesTotales,
  operacionesExitosas,
  operacionesFallidas,
  porcentajeOperacionesExitosas : ((operacionesExitosas / operacionesTotales) * 100).toFixed(2) + '%',
  detallesPorFranja
};

// 6) Devolver un único ítem con el resumen
return [
  {
    json: resumen
  }
];

