DECLARE @nowBogota    DATETIME;

/* Obtener fecha/hora actual en Bogotá */
SET @nowBogota = CONVERT(DATETIME, SYSDATETIMEOFFSET() AT TIME ZONE 'SA Pacific Standard Time');

/* UPDATE para CANCELACIÓN */
UPDATE dbo.CitasRecepcion
SET estado = N'CANCELADA',
    fecha_modificacion = @nowBogota
WHERE id = @id;

/* Devolver la fila modificada */
SELECT *
FROM dbo.CitasRecepcion
WHERE id = @id;

