// Código para preparar datos de CANCELACIÓN
const datosValidacion = $('Code validación de cita en malla').first().json.datosValidacion;
const citaBD = datosValidacion.citaBD;
const chatId = $('Organiza el Mensaje').first().json.infoMensaje.chat_id;
