function verificarDisponibilidadFranjas(datosEntrada) {
  try {
    console.log(`游 ===== INICIANDO VERIFICACI칍N DE DISPONIBILIDAD =====`);

    /* 較較較較較較較較較較較較較較較較較較較較較較較較較較較 1. DATOS B츼SICOS 較較較較較較較較較較較較較較較較較較較較較較較較較較較 */
    const {
      datosCita,
      datosMalla:        mallaActual,
      datosMallaProxSemana: mallaProxima,
      datoscategorias:   categorias,
      datosproveedor:    proveedores
    } = datosEntrada;

    const fechaHoraActual = new Date();

    const proveedor       = datosCita.proveedor   || "";
    const fechaSolicitada = datosCita.fecha       || "";
    const horaSolicitada  = datosCita.hora        || "";
    const categoria       = datosCita.categoria   || "";
    const unidades        = Number.parseInt(String(datosCita.unidades || "0"), 10);

    console.log(`游늶 DATOS EXTRA칈DOS DE LA CITA:`);
    console.log(`  Proveedor: "${proveedor}"`);
    console.log(`  Fecha solicitada: "${fechaSolicitada}"`);
    console.log(`  Hora solicitada: "${horaSolicitada}"`);
    console.log(`  Categor칤a: "${categoria}"`);
    console.log(`  Unidades: ${unidades}`);

    /* Validaciones m칤nimas */
    if (!proveedor)       return { error: "Proveedor no especificado" };
    if (!fechaSolicitada) return { error: "Fecha no especificada" };
    if (!horaSolicitada)  return { error: "Hora no especificada" };
    if (!categoria)       return { error: "Categor칤a no especificada" };
    if (Number.isNaN(unidades) || unidades <= 0) return { error: "Cantidad de unidades inv치lida" };

    /* 較較較較較較較較較較較較較較較較較較較較較較較較較較 2. PROVEEDOR 較較較較較較較較較較較較較較較較較較較較較較較較較較較較較較較 */
    const datosProveedor = buscarProveedor(proveedores, proveedor);
    if (!datosProveedor) {
      return { error: `No se encontr칩 el proveedor "${proveedor}" en la base de datos` };
    }

    const unidadesPorHora = datosProveedor.unidadesPorHora;
    if (!unidadesPorHora || unidadesPorHora <= 0) {
      return { error: `El proveedor "${proveedor}" no tiene definidas unidades por hora v치lidas` };
    }

    /* 較較較較較較較較較較較較較較較較較較較較較較較較較較 3. C츼LCULOS B츼SICOS 較較較較較較較較較較較較較較較較較較較較較較較較較 */
    const horasNecesarias = Math.ceil(unidades / unidadesPorHora);
    const restriccionHoraSuperada = verificarRestriccionHoraCitaManana(fechaHoraActual, fechaSolicitada);

    /* 較較較較較較較較較較較較較較較較較較較較較較較較較較 4. VALIDACI칍N DESCARGA SOBREDIMENSIONADA 較較較較較較較較較較較 */
    // Determinar qu칠 malla usar para obtener el n칰mero de franjas disponibles
    let cantidadFranjasDiarias = 0;

    // Buscar en qu칠 malla est치 la fecha solicitada para obtener el n칰mero de franjas
    const resultadoBusquedaMalla = buscarDiaPorFechaExacta(fechaSolicitada, mallaActual, mallaProxima);
    if (resultadoBusquedaMalla && resultadoBusquedaMalla.malla && resultadoBusquedaMalla.malla.franjas) {
      cantidadFranjasDiarias = resultadoBusquedaMalla.malla.franjas.length;
    } else {
      // Fallback: usar la malla actual si no se puede determinar
      cantidadFranjasDiarias = mallaActual.franjas ? mallaActual.franjas.length : 8;
    }

    // Verificar si la descarga es sobredimensionada
    const descargaSobredimensionada = horasNecesarias > cantidadFranjasDiarias;
    console.log(`Validaci칩n descarga sobredimensionada: horasNecesarias=${horasNecesarias}, cantidadFranjasDiarias=${cantidadFranjasDiarias}, sobredimensionada=${descargaSobredimensionada}`);

    /* 較較較較較較較較較較較較較較較較較較較較較較較較較較 5. HORA NORMALIZADA 較較較較較較較較較較較較較較較較較較較較較較較較 */
    const horaNormalizada = horaSolicitada.trim();

    /* 較較較較較較較較較較較較較較較較較較較較較較較較較較 5. MUELLE POR CATEGOR칈A 較較較較較較較較較較較較較較較較較較較較 */
    const muelleAsignado = determinarMuellePorCategoria(categorias, categoria);
    if (!muelleAsignado) {
      return { error: `No se encontr칩 un muelle asignado para la categor칤a "${categoria}"` };
    }

    /* 較較較較較較較較較較較較較較較較較較較較較較較較較較 6. DISPONIBILIDAD / ALTERNATIVAS 較較較較較較較較較較較 */
    let resultado;

    if (restriccionHoraSuperada) {
      // Caso: contactaron despu칠s de las 4:30 p.m. y la cita es para ma침ana
      const alternativas = buscarAlternativasDesdeDosDiasDespues(
        fechaHoraActual,
        mallaActual,
        mallaProxima,
        muelleAsignado,
        horasNecesarias
      );

      resultado = {
        disponibilidadConfirmada: false,
        fecha:           fechaSolicitada,
        horaInicio:      horaNormalizada,
        horaFin:         calcularHoraFin(fechaSolicitada, horaNormalizada, horasNecesarias),
        muelle:          muelleAsignado,
        duracionHoras:   horasNecesarias,
        restriccionHoraSuperada: true,
        descargaSobredimensionada: descargaSobredimensionada,
        alternativasDisponibles: alternativas.slice(0, 10)
      };
    } else {
      const disponible = verificarDisponibilidadHoraSolicitada(
        fechaSolicitada,
        horaNormalizada,
        muelleAsignado,
        horasNecesarias,
        mallaActual,
        mallaProxima
      );

      if (disponible) {
        resultado = {
          disponibilidadConfirmada: true,
          fecha:           fechaSolicitada,
          horaInicio:      horaNormalizada,
          horaFin:         calcularHoraFin(fechaSolicitada, horaNormalizada, horasNecesarias),
          muelle:          muelleAsignado,
          duracionHoras:   horasNecesarias,
          restriccionHoraSuperada: false,
          descargaSobredimensionada: descargaSobredimensionada,
          alternativasDisponibles: []
        };
      } else {
        const alternativasMismoDia = buscarAlternativasMismoDia(
          fechaSolicitada,
          muelleAsignado,
          horasNecesarias,
          mallaActual,
          mallaProxima
        );

        const todasAlternativas = alternativasMismoDia.length > 0
          ? alternativasMismoDia
          : buscarAlternativasDiasPeriores(
              fechaSolicitada,
              muelleAsignado,
              horasNecesarias,
              mallaActual,
              mallaProxima
            );

        resultado = {
          disponibilidadConfirmada: false,
          fecha:           fechaSolicitada,
          horaInicio:      horaNormalizada,
          horaFin:         calcularHoraFin(fechaSolicitada, horaNormalizada, horasNecesarias),
          muelle:          muelleAsignado,
          duracionHoras:   horasNecesarias,
          restriccionHoraSuperada: false,
          descargaSobredimensionada: descargaSobredimensionada,
          alternativasDisponibles: todasAlternativas
        };
      }
    }

    /* 較較較較較較較較較較較較較較較較較較較較較較較較較較 7. DATOS PROVEEDOR 較較較較較較較較較較較較較較較較較較較較較較較較較 */
    resultado.datosProveedorEncontrado = {
      nombre:           datosProveedor.proveedor,
      unidadesPorHora:  datosProveedor.unidadesPorHora,
      rotacion:         datosProveedor.rotacion,
      tipoProveedor:    datosProveedor.tipoProveedor
    };

    return { output: resultado };
  } catch (error) {
    console.error("Error en verificaci칩n de disponibilidad:", error);
    return { error: `Error en la verificaci칩n: ${error.message}` };
  }
}

