// (1) B칰squeda de proveedor
function buscarProveedor(listaProveedores, nombreProveedor) {
  if (!Array.isArray(listaProveedores) || listaProveedores.length === 0) return null;
  if (!nombreProveedor) return null;

  const nombreLower = nombreProveedor.toLowerCase();
  const exacta  = listaProveedores.find(p => (p.proveedor || "").toLowerCase() === nombreLower);
  if (exacta) return exacta;

  return listaProveedores.find(p => (p.proveedor || "").toLowerCase().includes(nombreLower));
}

// (2) Normalizador de hora para comparaciones
function limpiarHora(h) {
  return h ? h.trim().replace(/^0+/, "").toUpperCase() : "";
}

// (3) Determinar muelle por categor칤a
function determinarMuellePorCategoria(categorias, categoriaProducto) {
  if (!Array.isArray(categorias) || categorias.length === 0) return null;
  const catLower = categoriaProducto.toLowerCase();

  const exacta = categorias.find(c => (c.CATEGORIA || "").toLowerCase() === catLower);
  if (exacta) return exacta.MUELLE;

  const parcial = categorias.find(c => (c.CATEGORIA || "").toLowerCase().includes(catLower));
  return parcial ? parcial.MUELLE : null;
}

// (4) Calcular hora de fin (VERSI칍N CORREGIDA)
function calcularHoraFin(fecha, horaInicio, horasNecesarias) {
  // Si falta alg칰n dato, no se puede calcular.
  if (!fecha || !horaInicio || horasNecesarias === undefined) {
    return "C치lculo inv치lido";
  }

  try {
    // 1. Creamos un objeto de fecha a partir de la fecha de la cita.
    //    Usamos la funci칩n que ya tienes para parsear fechas en espa침ol.
    const fechaInicio = parsearFechaEspanol(fecha);
    if (isNaN(fechaInicio.getTime())) {
      console.error("Fecha inv치lida proporcionada a calcularHoraFin:", fecha);
      return "Fecha inv치lida";
    }

    // 2. Parseamos la hora de inicio y la aplicamos al objeto de fecha.
    const [horaTxt, meridiano] = horaInicio.trim().toUpperCase().split(" ");
    let hora = parseInt(horaTxt.split(":")[0], 10);

    // Convertimos la hora a formato de 24h para poderla establecer correctamente.
    if (meridiano === "PM" && hora < 12) {
      hora += 12;
    }
    if (meridiano === "AM" && hora === 12) {
      hora = 0; // 12 AM es la hora 0.
    }

    // Establecemos la hora en nuestro objeto de fecha.
    fechaInicio.setHours(hora, 0, 0, 0);

    // 3. A침adimos las horas necesarias. El objeto Date manejar치 el cambio de d칤a autom치ticamente.
    fechaInicio.setHours(fechaInicio.getHours() + horasNecesarias);

    // 4. Formateamos la nueva fecha/hora de fin al formato "H:00 AM/PM".
    let horaFin24 = fechaInicio.getHours();
    let meridianoFin = horaFin24 >= 12 ? "PM" : "AM";
    let horaFin12 = horaFin24 % 12;

    if (horaFin12 === 0) {
      horaFin12 = 12; // La hora 0 (medianoche) y la hora 12 (mediod칤a) se muestran como 12.
    }

    return `${horaFin12}:00 ${meridianoFin}`;

  } catch (error) {
    console.error("Error en la nueva funci칩n calcularHoraFin:", error);
    return "Error en c치lculo";
  }
}

// (5) Restricci칩n 4:30 p.m.  cita ma침ana (CORREGIDO - ZONA HORARIA)
function verificarRestriccionHoraCitaManana(fechaHoraActual, fechaSolicitada) {
  // 1. Obtener la fecha y hora actual en la zona horaria de Bogot치.
  //    Esto es CRUCIAL para que la comparaci칩n de las 4:30 PM funcione correctamente
  //    independientemente de la zona horaria del servidor.
  const ahoraEnBogota = new Date(new Date().toLocaleString("en-US", { timeZone: "America/Bogota" }));

  // 2. Verificar si la hora actual en Bogot치 es despu칠s de las 4:30 PM.
  const horaBogota = ahoraEnBogota.getHours();
  const minutosBogota = ahoraEnBogota.getMinutes();
  const esDespuesDe430PM = horaBogota > 16 || (horaBogota === 16 && minutosBogota >= 30);

  // 3. Parsear la fecha que el usuario solicit칩.
  const fechaSolicitadaObj = parsearFechaEspanol(fechaSolicitada);
  if (!fechaSolicitadaObj || isNaN(fechaSolicitadaObj.getTime())) {
    console.error("Fecha solicitada inv치lida:", fechaSolicitada);
    return false;
  }

  // 4. Calcular la fecha de "ma침ana" bas치ndose en la fecha actual de Bogot치.
  const fechaManana = new Date(ahoraEnBogota);
  fechaManana.setDate(fechaManana.getDate() + 1);

  // 5. Comparar si la fecha solicitada es efectivamente "ma침ana".
  //    Se compara a침o, mes y d칤a para evitar problemas con horas.
  const esMa침ana = fechaSolicitadaObj.getFullYear() === fechaManana.getFullYear() &&
                   fechaSolicitadaObj.getMonth() === fechaManana.getMonth() &&
                   fechaSolicitadaObj.getDate() === fechaManana.getDate();

  // 6. La restricci칩n se aplica solo si AMBAS condiciones son verdaderas.
  return esMa침ana && esDespuesDe430PM;
}


// (6) Parsear fecha "30 de abril de 2025"
function parsearFechaEspanol(fechaTexto) {
  const [dia, , mesTxt, , anio] = fechaTexto.split(" ");
  return new Date(Number(anio), obtenerNumeroMes(mesTxt), Number(dia));
}

// (7) Obtener n칰mero de mes
function obtenerNumeroMes(nombreMes) {
  const meses = ["enero","febrero","marzo","abril","mayo","junio","julio","agosto","septiembre","octubre","noviembre","diciembre"];
  return meses.indexOf(nombreMes.toLowerCase());
}

// (7.5) FUNCI칍N: Normalizar fecha flexible - Interpreta CUALQUIER formato de fecha
// ===================== INICIO DE LA SECCI칍N CORREGIDA =====================
function normalizarFechaFlexible(fechaInput) {
  if (!fechaInput) return "";

  let fecha = fechaInput.toString().trim().toLowerCase();

  const mesesMap = {
    "enero": "enero", "ene": "enero", "1": "enero",
    "febrero": "febrero", "feb": "febrero", "2": "febrero", 
    "marzo": "marzo", "mar": "marzo", "3": "marzo",
    "abril": "abril", "abr": "abril", "4": "abril",
    "mayo": "mayo", "may": "mayo", "5": "mayo",
    "junio": "junio", "jun": "junio", "6": "junio",
    "julio": "julio", "jul": "julio", "7": "julio",
    "agosto": "agosto", "ago": "agosto", "8": "agosto",
    "septiembre": "septiembre", "sep": "septiembre", "sept": "septiembre", "9": "septiembre",
    "octubre": "octubre", "oct": "octubre", "10": "octubre",
