// (8) NUEVA FUNCI칍N: Buscar d칤a por fecha exacta en todas las mallas
function buscarDiaPorFechaExacta(fechaSolicitada, mallaActual, mallaProxima) {
  console.log(`游댌 BUSCANDO FECHA EXACTA: "${fechaSolicitada}"`);

  const mallas = [
    { malla: mallaActual, nombre: "actual" },
    { malla: mallaProxima, nombre: "proxima" }
  ];

  for (const { malla, nombre } of mallas) {
    console.log(`  游늶 Revisando malla "${nombre}"`);

    if (!malla) {
      console.log(`    仇 Malla "${nombre}" es nula`);
      continue;
    }

    if (!malla.dias) {
      console.log(`    仇 Malla "${nombre}" no tiene d칤as`);
      continue;
    }

    console.log(`    九 Malla "${nombre}" tiene ${Object.keys(malla.dias).length} d칤as`);

    // Recorrer todos los d칤as de la malla
    for (const [nombreDia, datosDia] of Object.entries(malla.dias)) {
      if (!datosDia) {
        console.log(`      丘멆잺 D칤a "${nombreDia}" tiene datos nulos`);
        continue;
      }

      // NORMALIZAR AMBAS FECHAS PARA COMPARACI칍N FLEXIBLE
      const fechaMallaNormalizada = normalizarFechaFlexible(datosDia.fechaDia || "");
      const fechaSolicitadaNormalizada = normalizarFechaFlexible(fechaSolicitada);

      console.log(`      游딉勇 Comparando d칤a "${nombreDia}": fechaDia="${datosDia.fechaDia}" (norm: "${fechaMallaNormalizada}") vs fechaSolicitada="${fechaSolicitada}" (norm: "${fechaSolicitadaNormalizada}")`);

      if (fechaMallaNormalizada === fechaSolicitadaNormalizada) {
        console.log(`      游꿀 FECHA ENCONTRADA! D칤a "${nombreDia}" en malla "${nombre}"`);
        return {
          malla: malla,
          nombreDia: nombreDia,
          datosDia: datosDia,
          mallaUsada: nombre
        };
      }
    }

    console.log(`    仇 Fecha "${fechaSolicitada}" NO encontrada en malla "${nombre}"`);
  }

  console.log(`仇 Fecha "${fechaSolicitada}" NO encontrada en NINGUNA malla`);
  return null;
}

