      console.log(`  Franjas disponibles:`, franjas);
      return false;
    }
    console.log(`九 Hora encontrada en 칤ndice ${indiceInicio}`);

    if (indiceInicio + horasNecesarias > franjas.length) {
      console.log(`仇 No hay suficientes franjas consecutivas disponibles (necesita ${horasNecesarias})`);
      return false;
    }

    /* 較較 Verificar disponibilidad de todas las franjas necesarias 較較 */
    console.log(`游댌 Verificando disponibilidad de ${horasNecesarias} franja(s) consecutiva(s)`);
    for (let i = 0; i < horasNecesarias; i++) {
      const horaFranja = franjas[indiceInicio + i];
      const franja = datosDia[nombreMuelle][horaFranja];

      console.log(`  Revisando franja ${i + 1}/${horasNecesarias}: "${horaFranja}"`);

      if (!franja) {
