    }

    console.log(`游꿀 TODAS las franjas est치n disponibles para ${fechaSolicitada} ${horaSolicitada}`);
    return true;
  } catch (error) {
    console.error(`仇 Error verificando disponibilidad: ${error.message}`);
    console.error(error.stack);
    return false;
  }
