    if (!resultado.fechaSolicitada) {
      resultado.camposFaltantes.push("fecha");
      resultado.mensajesCamposFaltantes["fecha"] = "Por favor es necesaria la fecha de entrega para que el sistema pueda revisar el cronograma y entregarte informaci칩n precisa.";
    }

    if (!resultado.horaSolicitada) {
      resultado.camposFaltantes.push("hora");
      resultado.mensajesCamposFaltantes["hora"] = "Por favor indica la hora preferida para la cita, es necesaria para verificar la disponibilidad en nuestro cronograma.";
    }

    if (!resultado.pesoIdentificado) {
      resultado.camposFaltantes.push("peso");
      resultado.mensajesCamposFaltantes["peso"] = "Te falta el dato del peso de la mercanc칤a. Por favor proporcionar este dato ojal치 en kilos, es necesario para que el sistema calcule la duraci칩n del descargue de la mercanc칤a.";
    }

    if (!resultado.unidadesIdentificadas) {
      resultado.camposFaltantes.push("unidades");
      resultado.mensajesCamposFaltantes["unidades"] = "Te falt칩 el dato de la cantidad de unidades a descargar. Este dato es importante para que el sistema pueda calcular el tiempo que se necesita para el descargue.";
    }

    if (!resultado.ordenCompraIdentificada) {
      resultado.camposFaltantes.push("orden de compra");
      resultado.mensajesCamposFaltantes["orden de compra"] = "Falta el n칰mero de la orden de compra. Por favor proporcionar este dato ya que es necesario para que la cita en el cronograma quede relacionada a esta orden de compra por temas de trazabilidad en la operaci칩n.";
    }

    // 6. VALIDACI칍N: Verificar que la fecha y hora sean v치lidas
    if (resultado.fechaSolicitada && resultado.horaSolicitada) {
      const validacionTemporal = validarFechaHora(resultado.fechaSolicitada, resultado.horaSolicitada, fechaActualObj);
      resultado.fechaValida = validacionTemporal.fechaValida;
      resultado.horaValida = validacionTemporal.horaValida;
      resultado.diaHabil = validacionTemporal.diaHabil;
      resultado.mensajeValidacionTemporal = validacionTemporal.mensaje;

      // Si la fecha u hora no son v치lidas, a침adir a campos faltantes
      if (!validacionTemporal.fechaValida) {
        if (!resultado.camposFaltantes.includes("fecha")) {
          resultado.camposFaltantes.push("fecha");
        }
        resultado.mensajesCamposFaltantes["fecha"] = validacionTemporal.mensaje;
      }

      if (!validacionTemporal.horaValida) {
        if (!resultado.camposFaltantes.includes("hora")) {
          resultado.camposFaltantes.push("hora");
        }
        resultado.mensajesCamposFaltantes["hora"] = validacionTemporal.mensaje;
      }

      if (!validacionTemporal.diaHabil) {
        if (!resultado.camposFaltantes.includes("fecha")) {
          resultado.camposFaltantes.push("fecha");
        }
        resultado.mensajesCamposFaltantes["fecha"] = validacionTemporal.mensaje;
      }
    }

    // 7. DETERMINAR ESTADO FINAL
    if (
      resultado.camposFaltantes.length === 0 && 
      resultado.proveedoresSimilares.length === 0 && 
      resultado.categoriasSimilares.length === 0 &&
      resultado.proveedorIdentificado && 
      resultado.categoriaIdentificada &&
      resultado.fechaValida &&
      resultado.horaValida &&
      resultado.diaHabil
    ) {
      resultado.todoCorrecto = true;
    }

    return resultado;
  } catch (e) {
    console.error("Error en procesamiento de cita:", e);
    return {
      error: e.message,
      todoCorrecto: false,
      mensajeSistema: "Ocurri칩 un error durante el procesamiento de tu solicitud. Por favor, intenta nuevamente."
    };
  }
}

/**
 * Valida que la fecha y hora solicitadas sean v치lidas
 * (no en el pasado, d칤a h치bil, etc.)
 */
function validarFechaHora(fechaStr, horaStr, fechaActualObj) {
  const resultado = {
    fechaValida: true,
    horaValida: true,
    diaHabil: true,
    mensaje: ""
  };

  try {
    // Crear el objeto fecha a partir del string de fecha
    let fechaSolicitadaObj = parsearFechaEspanol(fechaStr);
    console.log("Fecha solicitada parseada:", fechaSolicitadaObj);

    // Verificar si es un d칤a h치bil (L-V)
    const diaSemana = fechaSolicitadaObj.getDay();
    if (diaSemana === 0 || diaSemana === 6) { // 0 = domingo, 6 = s치bado
      resultado.diaHabil = false;
      resultado.mensaje = "La fecha solicitada cae en fin de semana. Por favor elige un d칤a h치bil (lunes a viernes).";
      return resultado;
    }

    // Verificar si la fecha es hoy
    const esHoy = esMismaFecha(fechaSolicitadaObj, fechaActualObj);
    console.log("쮼s hoy?", esHoy);

    // Si es hoy, verificar que la hora sea futura y con al menos 2 horas de anticipaci칩n
    if (esHoy) {
      // Convertir la hora solicitada a un objeto Date
      const horaSolicitadaObj = parsearHora(horaStr, fechaSolicitadaObj);
      console.log("Hora solicitada parseada:", horaSolicitadaObj);

      // Verificar que sea futura
      if (horaSolicitadaObj < fechaActualObj) {
        resultado.horaValida = false;
        resultado.mensaje = "La hora solicitada ya pas칩. Por favor elige una hora futura.";
        return resultado;
      }

      // Verificar que haya al menos 2 horas de anticipaci칩n
      const diferenciaMilis = horaSolicitadaObj - fechaActualObj;
      const diferenciaHoras = diferenciaMilis / (1000 * 60 * 60);
      console.log("Diferencia en horas:", diferenciaHoras);

      if (diferenciaHoras < 2) {
        resultado.horaValida = false;
        resultado.mensaje = "Necesitamos al menos 2 horas de anticipaci칩n para programar una cita. Por favor elige una hora posterior o una fecha futura.";
        return resultado;
      }
    }

    // Verificar que la fecha no sea pasada
    if (fechaSolicitadaObj < fechaActualObj && !esHoy) {
      resultado.fechaValida = false;
      resultado.mensaje = "La fecha solicitada ya pas칩. Por favor elige una fecha futura.";
      return resultado;
    }

    // NO HAY RESTRICCI칍N DE HORARIO DE OPERACI칍N

    return resultado;
  } catch (e) {
    console.error("Error en validaci칩n de fecha/hora:", e);
    resultado.fechaValida = false;
    resultado.mensaje = "Error al validar la fecha y hora. Por favor verifica el formato.";
    return resultado;
  }
}

/**
 * Parsea una fecha en espa침ol a un objeto Date
 */
function parsearFechaEspanol(fechaStr) {
  try {
    // Verificar que la fecha no est칠 vac칤a
    if (!fechaStr || typeof fechaStr !== 'string' || fechaStr.trim() === '') {
      throw new Error("Fecha vac칤a o no v치lida: " + fechaStr);
    }

    console.log("[DEBUG] Fecha recibida para parsear:", fechaStr);

    // Normalizar la fecha (quitar "de", etc. y limpiar espacios extra)
    const fechaNormalizada = fechaStr.toLowerCase()
      .trim()
      .replace(/\s+de\s+/g, " ")
      .replace(/\s+del\s+/g, " ")
      .replace(/\s+/g, " "); // Normalizar espacios m칰ltiples

    console.log("[DEBUG] Fecha normalizada:", fechaNormalizada);

    // Mapeo de meses en espa침ol a n칰meros (incluir variaciones)
    const meses = {
      "enero": 0, "febrero": 1, "marzo": 2, "abril": 3, "mayo": 4, "junio": 5,
      "julio": 6, "agosto": 7, "septiembre": 8, "octubre": 9, "noviembre": 10, "diciembre": 11,
      // Variaciones comunes
      "ene": 0, "feb": 1, "mar": 2, "abr": 3, "may": 4, "jun": 5,
      "jul": 6, "ago": 7, "sep": 8, "oct": 9, "nov": 10, "dic": 11
    };

    // Extraer d칤a, mes, a침o
    const partes = fechaNormalizada.split(/\s+/).filter(p => p.length > 0);
    console.log("[DEBUG] Partes de la fecha:", partes);

    let dia, mes, a침o;

    // Intentar diferentes formatos
    if (partes.length >= 3) {
      // Formato "15 abril 2025" o "15 de abril 2025"
      dia = parseInt(partes[0]);

      // Buscar el mes en las partes (puede estar en posici칩n 1 o 2)
      let mesEncontrado = false;
      for (let i = 1; i < partes.length - 1; i++) {
        if (meses.hasOwnProperty(partes[i])) {
          mes = meses[partes[i]];
          mesEncontrado = true;
          break;
        }
      }

      if (!mesEncontrado) {
        throw new Error("Mes no reconocido en: " + fechaStr);
      }

      a침o = parseInt(partes[partes.length - 1]);

    } else if (partes.length === 2) {
      // Formato "15 abril" (asumimos a침o actual)
      dia = parseInt(partes[0]);

      if (meses.hasOwnProperty(partes[1])) {
        mes = meses[partes[1]];
      } else {
        throw new Error("Mes no reconocido: " + partes[1]);
      }

      a침o = new Date().getFullYear();

    } else if (partes.length === 1) {
      // Intentar formato "15/04/2025" o "15-04-2025"
      if (/^\d{1,2}[\/\-]\d{1,2}[\/\-]\d{2,4}$/.test(partes[0])) {
        const partesNumero = partes[0].split(/[\/\-]/);
        dia = parseInt(partesNumero[0]);
        mes = parseInt(partesNumero[1]) - 1; // Restar 1 porque en JS los meses van de 0-11
        a침o = parseInt(partesNumero[2]);
        if (a침o < 100) a침o += 2000; // Ajustar a침os de 2 d칤gitos
      } else {
        throw new Error("Formato de fecha no reconocido: " + fechaStr);
      }
    } else {
      throw new Error("Formato de fecha no reconocido (partes insuficientes): " + fechaStr);
    }

    console.log("[DEBUG] Componentes extra칤dos - D칤a:", dia, "Mes:", mes, "A침o:", a침o);

    // Verificar que los valores sean v치lidos
    if (isNaN(dia) || mes === undefined || isNaN(mes) || isNaN(a침o)) {
      throw new Error("Componentes de fecha no v치lidos - D칤a: " + dia + ", Mes: " + mes + ", A침o: " + a침o + " en: " + fechaStr);
    }

    // Validar rangos
    if (dia < 1 || dia > 31) {
      throw new Error("D칤a fuera de rango (1-31): " + dia);
    }

    if (mes < 0 || mes > 11) {
      throw new Error("Mes fuera de rango (0-11): " + mes);
    }

    if (a침o < 1900 || a침o > 2100) {
      throw new Error("A침o fuera de rango (1900-2100): " + a침o);
    }

    // Crear el objeto Date
    const fechaResultado = new Date(a침o, mes, dia);

    // Verificar que la fecha se cre칩 correctamente
    if (isNaN(fechaResultado.getTime())) {
      throw new Error("Fecha inv치lida creada: " + fechaResultado);
    }

    console.log("[DEBUG] Fecha parseada exitosamente:", fechaResultado);
    return fechaResultado;

  } catch (e) {
    console.error("[ERROR] Error al parsear fecha '", fechaStr, "':", e.message);
    throw e;
  }
}

/**
 * Parsea una hora en formato espa침ol a un objeto Date
 */
function parsearHora(horaStr, fechaBase) {
  try {
    // Normalizar la hora
    const horaNormalizada = horaStr.toLowerCase().trim();

    // Extraer la hora y los minutos
    let hora, minutos;
    let esPM = false;

    // Verificar si es PM
    if (horaNormalizada.includes("pm") || 
        horaNormalizada.includes("p.m.") || 
        horaNormalizada.includes("tarde") ||
        horaNormalizada.includes("noche")) {
      esPM = true;
    }

    // Extraer la hora y minutos num칠ricos
    const matchHora = horaNormalizada.match(/(\d+)(?::(\d+))?/);
    if (matchHora) {
      hora = parseInt(matchHora[1]);
      minutos = matchHora[2] ? parseInt(matchHora[2]) : 0;

      // Convertir a formato 24 horas si es PM
      if (esPM && hora < 12) {
        hora += 12;
      }

      // Convertir a formato 24 horas si es AM y son las 12
      if (!esPM && hora === 12) {
        hora = 0;
      }
    } else {
      throw new Error("Formato de hora no reconocido: " + horaStr);
    }

    // Verificar que los valores sean v치lidos
    if (isNaN(hora) || isNaN(minutos)) {
      throw new Error("Formato de hora no reconocido: " + horaStr);
    }

    // Crear el objeto Date con la fecha base y la hora extra칤da
    const resultado = new Date(fechaBase);
    resultado.setHours(hora, minutos, 0, 0);

    return resultado;
  } catch (e) {
    console.error("Error al parsear hora:", e);
    throw e;
  }
}

/**
 * Verifica si dos fechas corresponden al mismo d칤a
 */
function esMismaFecha(fecha1, fecha2) {
  return fecha1.getDate() === fecha2.getDate() &&
         fecha1.getMonth() === fecha2.getMonth() &&
         fecha1.getFullYear() === fecha2.getFullYear();
}

/**
 * Verifica si la malla de la pr칩xima semana no existe
 */
function verificarMallaProximaNoExiste(mallaProximaSemana) {
  // Verificar si la malla pr칩xima semana existe y tiene datos
  if (!mallaProximaSemana || !mallaProximaSemana.datosMallaProxSemana) {
    return true;
  }

  // Verificar si contiene el mensaje de error espec칤fico en el t칤tulo
  const titulo = mallaProximaSemana.datosMallaProxSemana.titulo;
  if (titulo && (
      titulo.includes("丘멆잺") && 
      (titulo.includes("no existe") || titulo.includes("a칰n no ha sido creada"))
    )) {
    return true;
  }

  return false;
}

/**
 * Verifica si ya hubo una confirmaci칩n previa en el historial
 */
function verificarConfirmacionPrevia(historial, mensajeActual) {
  // Si no hay historial, no puede haber confirmaci칩n previa
  if (!historial) {
    return { confirmado: false };
  }

  // Patrones para detectar solicitud de confirmaci칩n en historial
  const patrones = [
    /Por favor,?\s?confirma/i,
    /쮺onfirmas/i,
    /confirma si esta/i,
    /deseas proceder/i
  ];

  // Patrones para detectar confirmaci칩n en mensaje actual
  const patronesConfirmacion = [
    /\bsi\b/i,
    /confirmo/i,
    /exacto/i,
    /\bese\b/i,
    /correcto/i,
    /afirmativo/i,
    /ok\b/i,
    /est치 bien/i
  ];

  // Buscar si hay solicitud de confirmaci칩n en historial
  let solicitudConfirmacionEncontrada = false;
  let detalles = {
    proveedor: "",
    fecha: "",
    hora: "",
    categoria: "",
    peso: 0,
    unidades: 0,
    ordenCompra: ""
  };

  // Buscar el 칰ltimo mensaje que pidi칩 confirmaci칩n
  const mensajes = historial.split(/\n+/).filter(m => m.trim());

  for (let i = mensajes.length - 1; i >= 0; i--) {
    const mensaje = mensajes[i];

    // Verificar si este mensaje pide confirmaci칩n
    if (patrones.some(patron => patron.test(mensaje))) {
      solicitudConfirmacionEncontrada = true;

      // Extraer proveedor
      const matchProveedor = mensaje.match(/proveedor\s+\*?\*?([^*\n.]+)\*?\*?/i);
      if (matchProveedor) {
        detalles.proveedor = matchProveedor[1].trim();
      }

      // Extraer fecha
      const matchFecha = mensaje.match(/(el d칤a|el|d칤a)\s+([^,]+?)(?=\s+a las|\s+con|\s+en|\s+y|$)/i);
      if (matchFecha) {
        detalles.fecha = matchFecha[2].trim();
      }

      // Extraer hora
      const matchHora = mensaje.match(/a las\s+([^,]+?)(?=\s+con|\s+en|\s+y|$)/i);
      if (matchHora) {
        detalles.hora = matchHora[1].trim();
      }

      // Extraer categor칤a
      const matchCategoria = mensaje.match(/categor칤a\s+\*?\*?([^*\n,]+)\*?\*?/i);
      if (matchCategoria) {
        detalles.categoria = matchCategoria[1].trim();
      }

      // Extraer peso
      const matchPeso = mensaje.match(/(\d+)\s+kilos/i);
      if (matchPeso) {
        detalles.peso = parseInt(matchPeso[1]);
      }

      // Extraer unidades
      const matchUnidades = mensaje.match(/(\d+)\s+unidades/i);
      if (matchUnidades) {
        detalles.unidades = parseInt(matchUnidades[1]);
      }

      // Extraer orden de compra
      const matchOrden = mensaje.match(/orden\s+(?:de compra)?\s*\*?\*?([^*\n.]+)\*?\*?/i);
      if (matchOrden) {
        detalles.ordenCompra = matchOrden[1].trim();
      }

      break;
    }
  }

  // Si encontramos una solicitud de confirmaci칩n, verificar si el mensaje actual es una confirmaci칩n
  if (solicitudConfirmacionEncontrada) {
    const esConfirmacion = patronesConfirmacion.some(patron => patron.test(mensajeActual));
    return {
      confirmado: esConfirmacion,
      proveedor: detalles.proveedor,
      fecha: detalles.fecha,
      hora: detalles.hora,
      categoria: detalles.categoria,
      peso: detalles.peso,
      unidades: detalles.unidades,
      ordenCompra: detalles.ordenCompra
    };
  }

  return { confirmado: false };
}

/**
 * Valida el proveedor contra la lista de proveedores disponibles
 */
function validarProveedor(proveedorInput, listaProveedores) {
  if (!proveedorInput || !listaProveedores || listaProveedores.length === 0) {
    return {
      coincidenciaExacta: false,
      coincidenciaParcialClara: false,
      coincidenciasParciales: []
    };
  }

  // Normalizar entrada (quitar puntos, comas, convertir a min칰sculas)
  const proveedorNormalizado = normalizarTexto(proveedorInput);

  // Si el input es demasiado corto y gen칠rico (menos de 3 caracteres), exigir m치s informaci칩n
  if (proveedorNormalizado.length < 2) {
    return {
      coincidenciaExacta: false,
      coincidenciaParcialClara: false,
      coincidenciasParciales: []
    };
  }

  console.log("Proveedor normalizado:", proveedorNormalizado);

  // Buscar coincidencia exacta (ignorando may칰sculas/min칰sculas)
  const coincidenciaExacta = listaProveedores.find(p => 
    normalizarTexto(p.proveedor) === proveedorNormalizado
  );

  if (coincidenciaExacta) {
    return {
      coincidenciaExacta: true,
      coincidenciaParcialClara: false,
      coincidenciasParciales: [],
      nombreCompleto: coincidenciaExacta.proveedor
    };
  }

  // Buscar primero coincidencias donde el input es un prefijo exacto
  const coincidenciasPrefijo = listaProveedores
    .filter(p => {
      const nombreNormalizado = normalizarTexto(p.proveedor);
      // Verificar si el nombre del proveedor comienza con el input exacto
      return nombreNormalizado.startsWith(proveedorNormalizado);
    })
    .map(p => p.proveedor);

  console.log("Coincidencias por prefijo:", coincidenciasPrefijo);

  // Si solo hay una coincidencia por prefijo, es una coincidencia clara
  if (coincidenciasPrefijo.length === 1) {
    const proveedor = listaProveedores.find(p => p.proveedor === coincidenciasPrefijo[0]);
    return {
      coincidenciaExacta: false,
      coincidenciaParcialClara: true,
      coincidenciasParciales: [],
      nombreCompleto: proveedor.proveedor
    };
  }

  // Si hay m칰ltiples coincidencias por prefijo, solicitar clarificaci칩n espec칤fica
  if (coincidenciasPrefijo.length > 1) {
    return {
      coincidenciaExacta: false,
      coincidenciaParcialClara: false,
      coincidenciasParciales: coincidenciasPrefijo
    };
  }

  // Si no hay coincidencias por prefijo, buscar coincidencias parciales m치s generales
  const coincidenciasParciales = listaProveedores
    .filter(p => {
      const nombreNormalizado = normalizarTexto(p.proveedor);
      // Verificar si el input est치 contenido en el nombre del proveedor
      return nombreNormalizado.includes(proveedorNormalizado) || 
             // O si las primeras palabras coinciden
             nombreNormalizado.split(' ').some((palabra, index, arr) => {
               if (index < 3) { // Solo considerar las primeras 3 palabras
                 return proveedorNormalizado.includes(palabra) || 
                        palabra.includes(proveedorNormalizado);
               }
               return false;
             });
    })
    .map(p => p.proveedor);

  console.log("Coincidencias parciales generales:", coincidenciasParciales);

  // Si hay solo una coincidencia parcial y es claramente identificable
  if (coincidenciasParciales.length === 1) {
    const proveedor = listaProveedores.find(p => p.proveedor === coincidenciasParciales[0]);
    const nombreNormalizado = normalizarTexto(proveedor.proveedor);

    // Calcular similitud
    const palabrasInput = proveedorNormalizado.split(' ');
    const palabrasProveedor = nombreNormalizado.split(' ');

    // Si el input tiene al menos 2 palabras y ambas est치n en el nombre del proveedor, considerarlo como claro
    const palabrasCoincidentes = palabrasInput.filter(palabra => 
      palabrasProveedor.some(p => p.includes(palabra))
    );

    const esClaramenteIdentificable = 
      // Si el input es un acr칩nimo que coincide con las iniciales del proveedor
      (proveedorNormalizado.length <= 5 && esAcronimo(proveedorNormalizado, proveedor.proveedor)) ||
      // O si hay suficiente coincidencia de palabras
      (palabrasInput.length >= 2 && palabrasCoincidentes.length >= 2) ||
      // O si el input es una parte sustancial del nombre del proveedor
      (nombreNormalizado.startsWith(proveedorNormalizado) && proveedorNormalizado.length >= 3) ||
      // Casos especiales para mejorar la coincidencia
      (proveedorNormalizado.includes("spb") && nombreNormalizado.includes("spb colombia"));

    if (esClaramenteIdentificable) {
      return {
        coincidenciaExacta: false,
        coincidenciaParcialClara: true,
        coincidenciasParciales: [],
        nombreCompleto: proveedor.proveedor
      };
    }
  }

  // En caso de m칰ltiples coincidencias o una coincidencia no clara
  return {
    coincidenciaExacta: false,
    coincidenciaParcialClara: false,
    coincidenciasParciales: coincidenciasParciales
  };
}

/**
 * Valida la categor칤a contra la lista de categor칤as disponibles
 */
function validarCategoria(categoriaInput, listaCategorias) {
  if (!categoriaInput || !listaCategorias || listaCategorias.length === 0) {
    return {
      coincidenciaExacta: false,
      coincidenciaSimilar: false,
      categoriasSimilares: []
    };
  }

  // Normalizar entrada
  const categoriaNormalizada = normalizarTexto(categoriaInput);

  // Buscar coincidencia exacta (ignorando may칰sculas/min칰sculas)
  const coincidenciaExacta = listaCategorias.find(c => 
    normalizarTexto(c.CATEGORIA) === categoriaNormalizada
  );

  if (coincidenciaExacta) {
    return {
      coincidenciaExacta: true,
      coincidenciaSimilar: false,
      categoriasSimilares: [],
      nombreCompleto: coincidenciaExacta.CATEGORIA
    };
  }

  // Caso especial para "arena" = "ARENAS"
  if (categoriaNormalizada === "arena" && listaCategorias.some(c => normalizarTexto(c.CATEGORIA) === "arenas")) {
    const categoriaArenas = listaCategorias.find(c => normalizarTexto(c.CATEGORIA) === "arenas");
    return {
      coincidenciaExacta: false,
      coincidenciaSimilar: true,
      categoriasSimilares: [],
      nombreCompleto: categoriaArenas.CATEGORIA
    };
  }

  // Buscar variantes similares (singular/plural)
  for (const categoria of listaCategorias) {
    const categoriaNombre = normalizarTexto(categoria.CATEGORIA);

    // Verificar si es una variante singular/plural
    if (
      // Si la categor칤a termina en S y sin la S coincide con el input
      (categoriaNombre.endsWith("s") && categoriaNombre.slice(0, -1) === categoriaNormalizada) ||
      // O si el input termina en S y sin la S coincide con la categor칤a
      (categoriaNormalizada.endsWith("s") && categoriaNormalizada.slice(0, -1) === categoriaNombre)
    ) {
      return {
        coincidenciaExacta: false,
        coincidenciaSimilar: true,
        categoriasSimilares: [],
        nombreCompleto: categoria.CATEGORIA
      };
    }
  }

  // Buscar coincidencias parciales
  const categoriasSimilares = listaCategorias
    .filter(c => {
      const nombreNormalizado = normalizarTexto(c.CATEGORIA);
      return nombreNormalizado.includes(categoriaNormalizada) || 
             categoriaNormalizada.includes(nombreNormalizado);
    })
    .map(c => c.CATEGORIA);

  // Si hay solo una coincidencia parcial, considerarla v치lida
  if (categoriasSimilares.length === 1) {
    return {
      coincidenciaExacta: false,
      coincidenciaSimilar: true,
      categoriasSimilares: [],
      nombreCompleto: categoriasSimilares[0]
    };
  }

  // En caso de m칰ltiples coincidencias o ninguna coincidencia
  return {
    coincidenciaExacta: false,
    coincidenciaSimilar: false,
    categoriasSimilares: categoriasSimilares
  };
}

