/**
 * Funci칩n para determinar si un a침o es bisiesto
 * Un a침o es bisiesto si es divisible por 4, excepto aquellos divisibles por 100 
 * que no son divisibles por 400
 */
function esBisiesto(year) {
  return (year % 4 === 0 && year % 100 !== 0) || (year % 400 === 0);
}

/**
 * Funci칩n para obtener el n칰mero de d칤as en un mes, considerando a침os bisiestos
 */
function getDiasMes(mes, a침o) {
  const diasPorMes = {
    1: 31, // enero
    2: esBisiesto(a침o) ? 29 : 28, // febrero - ajustado para a침o bisiesto
    3: 31, // marzo
    4: 30, // abril
    5: 31, // mayo
    6: 30, // junio
    7: 31, // julio
    8: 31, // agosto
    9: 30, // septiembre
    10: 31, // octubre
    11: 30, // noviembre
    12: 31  // diciembre
  };

  return diasPorMes[mes];
}

/**
 * Funci칩n para eliminar acentos y pasar a min칰sculas (para buscar palabras clave sin importar tildes ni may칰sculas).
 */
function normalizarTexto(texto) {
  if (typeof texto !== 'string') return "";
  // Normaliza (NFD) y elimina diacr칤ticos, luego pasa a min칰sculas.
  return texto
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, "") // quita acentos
    .toLowerCase();
}

/**
 * Dada la informaci칩n de proveedor, ordenCompra, peso, categor칤a y unidades,
 * revisa si existe la palabra 'fuera de servicio', 'cancelado' o 'reagendado'
 * en cualquiera de esos campos. De ser as칤, retorna ese estado especial;
 * de lo contrario, retorna 'ocupado'.
 * 
 * Se ignoran may칰sculas, min칰sculas y tildes.
 */
function determinarEstadoOcupado(proveedor, ordenCompra, peso, categoria, unidades) {
  const conjunto = `${proveedor} ${ordenCompra} ${peso} ${categoria} ${unidades}`;
  const texto = normalizarTexto(conjunto);

  if (texto.includes("fuera de servicio")) {
    return "fuera de servicio";
  }
  if (texto.includes("cancelado")) {
    return "cancelado";
  }
  if (texto.includes("reagendado")) {
    return "reagendado";
  }
  return "ocupado";  
}

// -------------------------------------------------------------
// 1) PROCESAR DATOS (Cabecera, Horas, Muelles, etc.)
// -------------------------------------------------------------
function procesarDatos(excelData) {
  // Verificaci칩n adicional de datos v치lidos
  if (!excelData || !Array.isArray(excelData) || excelData.length === 0) {
    return {
      titulo: "丘멆잺 La malla de la pr칩xima semana no existe en el archivo Excel o a칰n no ha sido creada.",
      a침o: "",
      semana: "",
      rangoDias: "",
      franjas: [],
      dias: {
        lunes: { muelle1: {}, muelle2: {} },
        martes: { muelle1: {}, muelle2: {} },
        mi칠rcoles: { muelle1: {}, muelle2: {} },
        jueves: { muelle1: {}, muelle2: {} },
        viernes: { muelle1: {}, muelle2: {} }
      }
    };
  }

  // Intentar mapear datos, con manejo de errores para cada item
  const datos = excelData.map(item => {
    try {
      return item.json || {};
    } catch (e) {
      return {};
    }
  });

  // A) Cabecera (T칤tulo, A침o, Rango)
  datos.forEach((dato, index) => {
    // Buscar t칤tulo
    if (dato.__EMPTY_2 === "MALLA DE RECIBO CEDI KONFIE IA") {
      mallaEstructurada.titulo = dato.__EMPTY_2;

      // En la siguiente fila puede estar a침o, semana, rango
      if (index + 1 < datos.length) {
        const datoSiguiente = datos[index + 1];
        if (datoSiguiente) {
          // A침o
          if (datoSiguiente.__EMPTY_2 && !isNaN(datoSiguiente.__EMPTY_2)) {
            mallaEstructurada.a침o = datoSiguiente.__EMPTY_2;
          }
          // Semana
          Object.entries(datoSiguiente).forEach(([k, v]) => {
            if (typeof v === 'string' && v.includes("SEMANA")) {
              mallaEstructurada.semana = v;
            }
          });
          // Rango: p.ej. "LUNES 31 AL VIERNES 4 DE ABRIL"
          Object.entries(datoSiguiente).forEach(([k, v]) => {
            if (typeof v === 'string' && v.includes("LUNES") && v.includes("VIERNES")) {
              mallaEstructurada.rangoDias = v;
            }
          });
        }
      }
    }
  });

  // Si despu칠s de procesar no encontramos t칤tulo, es que no hay malla v치lida
  if (!mallaEstructurada.titulo) {
    mallaEstructurada.titulo = "丘멆잺 La malla de la pr칩xima semana no existe en el archivo Excel o a칰n no ha sido creada.";
    return mallaEstructurada;
  }

  // B) Detectar franjas horarias (.__EMPTY = fracci칩n de d칤a)
  const franjasHorarias = [];
  datos.forEach(dato => {
    if (
      dato.__EMPTY !== undefined &&
      typeof dato.__EMPTY === 'number' &&
      dato.__EMPTY > 0 &&
      dato.__EMPTY < 1
    ) {
      const totalMin = Math.round(dato.__EMPTY * 24 * 60);
      const hh = Math.floor(totalMin / 60);
      const mm = totalMin % 60;

      let periodo = "AM";
      let hora12 = hh;
      if (hh >= 12) {
        periodo = "PM";
        hora12 = (hh === 12 ? 12 : hh - 12);
      }
      if (hh === 0) {
        hora12 = 12;
      }

      const horaStr = `${hora12}:${String(mm).padStart(2, '0')} ${periodo}`;
      franjasHorarias.push({
        hora: horaStr,
        indice: datos.indexOf(dato) // fila base
      });
    }
  });
  franjasHorarias.sort((a, b) => a.indice - b.indice);
  mallaEstructurada.franjas = franjasHorarias;

  // C) Detectar muelles (col)
  const muellesInfo = {};
  datos.forEach(dato => {
    Object.entries(dato).forEach(([key, value]) => {
      if (value === "MUELLE 01" || value === "MUELLE 02") {
        const col = parseInt(key.replace("__EMPTY_", ""), 10);
        const muelleKey = (value === "MUELLE 01") ? 'muelle1' : 'muelle2';
        if (!muellesInfo[muelleKey]) {
          muellesInfo[muelleKey] = [];
        }
        muellesInfo[muelleKey].push(col);
      }
    });
  });
  if (muellesInfo.muelle1) muellesInfo.muelle1.sort((a, b) => a - b);
  if (muellesInfo.muelle2) muellesInfo.muelle2.sort((a, b) => a - b);

  // D) Generar los 5 d칤as (lunes-viernes) con su fecha
  parsearRangoDias();

  // E) Extraer Citas
  extraerCitas(datos, muellesInfo);

  return mallaEstructurada;
}

/**
 * Parsea la cadena "LUNES 31 AL VIERNES 4 DE ABRIL"
 * (o "LUNES 31 AL VIERNES 04 DE ABRIL") y maneja el cruce de mes.
 * Ej.: si dayStart=31 y dayEnd=4, la 1춹 fecha es 31 de MARZO,
 * luego 1,2,3,4 de ABRIL, en vez de 32,33, etc.
 */
function parsearRangoDias() {
  const texto = mallaEstructurada.rangoDias;
  // Regex simple: p.ej. "31 AL ... 4 DE ABRIL"
  // Captura: dayStart, dayEnd, mesFin
  // Ojo: si dice "LUNES 31 DE MARZO AL VIERNES 4 DE ABRIL",
  // quedar치: dayStart=31, dayEnd=4, month=ABRIL (al final).
  const regex = /(\d+)\s+AL\s+\D+(\d+)\s+DE\s+(\w+)/i;
  const match = regex.exec(texto);

  // Por defecto, creamos 5 d칤as vac칤os sin fecha
  diasSemana.forEach(d => {
    mallaEstructurada.dias[d] = {
      muelle1: {},
      muelle2: {}
    };
  });

  if (!match) {
    // Sin coincidencia, dejamos los d칤as sin fecha
    return;
  }

  const dayStart = parseInt(match[1], 10); // 31
  const dayEnd   = parseInt(match[2], 10); // 4
  const finalMonthName = match[3].toLowerCase(); // "abril"

  // Convertimos a n칰mero
  const finalMonth = meses[finalMonthName] || 3; // fallback marzo
  const yearNum = parseInt(mallaEstructurada.a침o, 10) || 2025;

  // Funci칩n para obtener la fecha formateada
  function fechaFormateada(d, m, y) {
    return `${d} de ${nombreMes[m]} de ${y}`;
  }

  // Comprobamos cu치ntos d칤as tiene el mes final, considerando bisiestos
  const diasMesFinal = getDiasMes(finalMonth, yearNum);
  // y del mes previo
  const monthPrev = (finalMonth === 1) ? 12 : finalMonth - 1;
  const yearPrev = (finalMonth === 1) ? yearNum - 1 : yearNum;

  // Arreglo final con 5 fechas
  const fechasDias = [];

  if (dayStart <= dayEnd) {
    // Caso "normal": todo en el mismo mes
    let d = dayStart;
    for (let i = 0; i < 5; i++) {
      fechasDias.push({
        diaSem: diasSemana[i],
        diaNum: d,
        mesNum: finalMonth,
        yearNum: yearNum
      });
      d++;
      if (d > diasMesFinal) {
        d = 1;
        const nextMonth = finalMonth % 12 + 1;
        const nextYear = (nextMonth === 1) ? yearNum + 1 : yearNum;
        fechasDias[fechasDias.length - 1].mesNum = nextMonth;
        fechasDias[fechasDias.length - 1].yearNum = nextYear;
      }
    }
  } else {
    // Caso "cruza de mes": dayStart > dayEnd
    let d = dayStart;
    let m = monthPrev;
    let y = yearPrev;

    for (let i = 0; i < 5; i++) {
      fechasDias.push({
        diaSem: diasSemana[i],
        diaNum: d,
        mesNum: m,
        yearNum: y
      });
      d++;
      if (d > getDiasMes(m, y)) {
        d = 1;
        m = m % 12 + 1;
        if (m === 1) {
          y++;
        }
      }
    }
  }

  // Ahora volcamos esas 5 fechas en la estructura
  fechasDias.forEach(fd => {
    const { diaSem, diaNum, mesNum, yearNum } = fd;
    mallaEstructurada.dias[diaSem] = {
      fechaDia: fechaFormateada(diaNum, mesNum, yearNum),
      muelle1: {},
      muelle2: {}
    };
  });

  // Inicializamos las franjas a 'disponible'
  if (mallaEstructurada.franjas.length > 0) {
    diasSemana.forEach(d => {
      mallaEstructurada.franjas.forEach(f => {
        mallaEstructurada.dias[d].muelle1[f.hora] = {
          estado: "disponible",
          proveedor: "",
          ordenCompra: "",
          peso: "",
          categoria: "",
          unidades: ""
        };
        mallaEstructurada.dias[d].muelle2[f.hora] = {
          estado: "disponible",
          proveedor: "",
          ordenCompra: "",
          peso: "",
          categoria: "",
          unidades: ""
        };
      });
    });
  }
}

/**
 * Funci칩n auxiliar: decide a qu칠 d칤a pertenece una columna,
 * corrigiendo 2 columnas de offset (porque 'Extract from XLSX'
 * deja todo desplazado).
 */
function getDayForColumn(colNumber) {
  // Ajuste de offset +2
  const realCol = colNumber + 2;

  // Rangos:
  //  Lunes:      3..8
  //  Martes:     9..14
  //  Mi칠rcoles: 15..20
  //  Jueves:    21..26
  //  Viernes:   27..32
  if (realCol >= 3 && realCol <= 8) {
    return "lunes";
  }
  if (realCol >= 9 && realCol <= 14) {
    return "martes";
  }
  if (realCol >= 15 && realCol <= 20) {
    return "mi칠rcoles";
  }
  if (realCol >= 21 && realCol <= 26) {
    return "jueves";
  }
  if (realCol >= 27 && realCol <= 32) {
    return "viernes";
  }
  // Si nada coincide, devolvemos null
  return null;
}

/**
 * Extraer las citas para cada franja:
 * - Para cada franja, miramos 3 filas:
 *    - Fila base => proveedor
 *    - Fila base+1 => orden
 *    - Fila base+2 => peso, categor칤a, unidades
 */
function extraerCitas(datos, muellesInfo) {
  mallaEstructurada.franjas.forEach(franja => {
    const indiceBase = franja.indice;
    if (indiceBase < 0 || indiceBase + 2 >= datos.length) {
      return; // no hay suficientes filas
    }

    // Recorremos muelle1, muelle2 y sus columnas
    const asignarCita = (muelleKey, cols) => {
      cols.forEach(col => {
        const provKey = `__EMPTY_${col}`;
        const filaProv = datos[indiceBase];

        // Si en esta fila hay un proveedor o alg칰n texto
        if (
          filaProv &&
          typeof filaProv[provKey] === 'string' &&
          filaProv[provKey].length > 2
        ) {
          // Determinar el d칤a real
          const diaOk = getDayForColumn(col);
          if (!diaOk || !mallaEstructurada.dias[diaOk]) {
            return; // si no coincide con ninguno, saltamos
          }

          // Recuperar valores para proveedor, ordenCompra y dem치s
          const filaOrden = datos[indiceBase + 1];
          const filaDet = datos[indiceBase + 2];

          const proveedor = filaProv[provKey] || "";
          const ordenCompra = filaOrden && filaOrden[provKey] ? filaOrden[provKey] : "";
          const peso = filaDet && filaDet[provKey] ? filaDet[provKey] : "";
          const cat  = filaDet && filaDet[`__EMPTY_${col + 1}`] ? filaDet[`__EMPTY_${col + 1}`] : "";
          const und  = filaDet && filaDet[`__EMPTY_${col + 2}`] ? filaDet[`__EMPTY_${col + 2}`] : "";

          // Antes pon칤amos estado="ocupado" directamente
          // Ahora verificamos si el texto contiene "reagendado", "cancelado" o "fuera de servicio".
          const nuevoEstado = determinarEstadoOcupado(proveedor, ordenCompra, peso, cat, und);

          // Asignamos los datos
          mallaEstructurada.dias[diaOk][muelleKey][franja.hora].estado = nuevoEstado;
          mallaEstructurada.dias[diaOk][muelleKey][franja.hora].proveedor = proveedor;
          mallaEstructurada.dias[diaOk][muelleKey][franja.hora].ordenCompra = ordenCompra;
          mallaEstructurada.dias[diaOk][muelleKey][franja.hora].peso = peso;
          mallaEstructurada.dias[diaOk][muelleKey][franja.hora].categoria = cat;
          mallaEstructurada.dias[diaOk][muelleKey][franja.hora].unidades = und;
        }
      });
    };

    if (muellesInfo.muelle1) {
      asignarCita('muelle1', muellesInfo.muelle1);
    }
    if (muellesInfo.muelle2) {
      asignarCita('muelle2', muellesInfo.muelle2);
    }
  });
}

