    /* 4. --- Validaci칩n propiamente dicha --------------------------- */
    // Variable para almacenar qu칠 malla se utiliz칩 en la verificaci칩n
  let mallaUtilizada = null;

  const disponible = verificarDisponibilidadHoraSolicitada(
