 *  UTILIDADES 
 * 轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎넉뎷轎 */

function verificarDisponibilidadHoraSolicitada(
  fechaSolicitada, horaSolicitada, muelle, horasNecesarias,
  mallaActual, mallaProxima, callbackMalla = null
){
  try {
    // --- Normalizamos -------------------------------------------------
    if (!fechaSolicitada || !horaSolicitada || !muelle) return false;

    const fechaObj  = parsearFechaEspanol(fechaSolicitada);
    const malla     = seleccionarMallaParaFecha(fechaObj, mallaActual, mallaProxima);
    if (!malla) return false;

    // Si se proporcion칩 una funci칩n callback, llamarla con la malla seleccionada
    if (typeof callbackMalla === 'function') {
      callbackMalla(malla);
    }

    const diaSem    = obtenerDiaSemanaEspanol(fechaObj);
    const keyMuelle = normalizarMuelle(muelle);

    if (!malla.dias[diaSem] || !malla.dias[diaSem][keyMuelle]) return false;

    if (!malla.franjas || !Array.isArray(malla.franjas)) return false;
    const franjas   = malla.franjas.map(f => f.hora);
    const idxInicio = franjas.findIndex(h => limpiarHora(h) === limpiarHora(horaSolicitada));

    if (idxInicio === -1 || idxInicio + horasNecesarias > franjas.length) return false;

    for (let i = 0; i < horasNecesarias; i++) {
      const hora = franjas[idxInicio + i];
      const fr   = malla.dias[diaSem][keyMuelle][hora];
      if (!fr || (fr.estado ?? "").toLowerCase() !== "disponible") return false;
    }
    return true;
  } catch(e){ 
    console.error("Error en verificarDisponibilidadHoraSolicitada:", e); 
    return false; 
  }
}

/* ---------- Helpers de normalizaci칩n ------------------------------ */
const limpiarHora = h => h.trim().replace(/^0+(?=\d)/, "").toUpperCase();

function normalizarMuelle(txt = "") {
  return txt
    .toLowerCase()
    .normalize("NFD").replace(/[\u0300-\u036f]/g, "") // sin tildes
    .replace(/\s+/g, "");
}

/* ---- FIX: elegir malla correcta cuando el rango cruza de mes ----- */
function seleccionarMallaParaFecha(fechaObj, mallaActual, mallaProxima){
  const dentro = (malla) => {
    if (!malla) return false;
    const r = rangoDesdeDias(malla);
    if (!r) return false;

    // Simplificar comparaci칩n ignorando horas/minutos/segundos
    const fechaSimple = new Date(fechaObj.getFullYear(), fechaObj.getMonth(), fechaObj.getDate());
    const inicioSimple = new Date(r.inicio.getFullYear(), r.inicio.getMonth(), r.inicio.getDate());
    const finSimple = new Date(r.fin.getFullYear(), r.fin.getMonth(), r.fin.getDate());

    return fechaSimple >= inicioSimple && fechaSimple <= finSimple;
  };

  if (dentro(mallaActual)) return mallaActual;
  if (dentro(mallaProxima)) return mallaProxima;
  return null;
}

/* Rango basado en las fechas reales de cada d칤a (m칤n-m치x) */
function rangoDesdeDias(malla){
  if (!malla || !malla.dias) return null;
  let min=null, max=null;
  for (const d of Object.values(malla.dias)){
    if (d && d.fechaDia){
      const f = parsearFechaEspanol(d.fechaDia);
      if (!min || f < min) min=f;
      if (!max || f > max) max=f;
    }
  }
  return (min && max) ? {inicio:min, fin:max} : null;
}

// Funci칩n para calcular la duraci칩n aproximada en horas basada en las horas de inicio y fin
function calcularDuracionAproximada(horaInicio, horaFin) {
  try {
    if (!horaInicio || !horaFin) {
      return 3; // Valor por defecto si no se puede calcular
    }

    // Verificar si las horas est치n en formato AM/PM
    const esFormatoAMPM = /AM|PM/i.test(horaInicio) && /AM|PM/i.test(horaFin);

    if (esFormatoAMPM) {
      // Convertir horas de formato AM/PM a 24 horas
      const horaInicioObj = convertirHoraAMPMA24H(horaInicio);
      const horaFinObj = convertirHoraAMPMA24H(horaFin);

      // Calcular diferencia en horas
      let diferenciaHoras = horaFinObj.hora - horaInicioObj.hora;

      // Ajustar por diferencia en minutos
      if (horaFinObj.minutos < horaInicioObj.minutos) {
        diferenciaHoras--;
      }

      // Si la diferencia es negativa, asumimos que cruza la medianoche
      if (diferenciaHoras < 0) {
        diferenciaHoras += 24;
      }

      return diferenciaHoras;
    } else {
      // Formato 24 horas
      const [horasInicio] = horaInicio.split(':').map(Number);
      const [horasFin] = horaFin.split(':').map(Number);

      let diferenciaHoras = horasFin - horasInicio;
      if (diferenciaHoras < 0) {
        diferenciaHoras += 24;
      }

      return diferenciaHoras;
    }
  } catch (error) {
    console.error(`Error calculando duraci칩n:`, error);
    return 3; // Valor por defecto
  }
}

// Funci칩n auxiliar para convertir hora de formato AM/PM a formato 24 horas
function convertirHoraAMPMA24H(horaAMPM) {
  try {
    const [tiempo, periodo] = horaAMPM.split(/\s+/);
    let [horas, minutos] = tiempo.split(':').map(Number);

    // Ajustar horas para formato de 24 horas
    if (periodo.toUpperCase() === 'PM' && horas < 12) {
      horas += 12;
    } else if (periodo.toUpperCase() === 'AM' && horas === 12) {
      horas = 0;
    }

    return {
      hora: horas,
      minutos: minutos || 0
    };
  } catch (error) {
    console.error(`Error convirtiendo hora:`, error);
    return { hora: 0, minutos: 0 }; // Fallback
  }
}

// Funci칩n auxiliar para calcular la hora de fin basada en la hora de inicio y la duraci칩n
function calcularHoraFin(horaInicio, duracionHoras) {
  try {
    // Verificar si la hora de inicio tiene formato AM/PM
    const esFormatoAMPM = /AM|PM/i.test(horaInicio);

    if (esFormatoAMPM) {
      // Convertir hora formato AM/PM
      const [tiempo, periodo] = horaInicio.split(/\s+/);
      let [horas, minutos] = tiempo.split(':').map(Number);

      // Ajustar horas para formato de 24 horas
      if (periodo.toUpperCase() === 'PM' && horas < 12) {
        horas += 12;
      } else if (periodo.toUpperCase() === 'AM' && horas === 12) {
        horas = 0;
      }

      // A침adir duraci칩n
      horas += duracionHoras;

      // Convertir de vuelta a AM/PM
      let nuevoPeriodo = 'AM';
      if (horas >= 12) {
        nuevoPeriodo = 'PM';
        if (horas > 12) {
          horas -= 12;
        }
      }
      if (horas === 0) {
        horas = 12;
        nuevoPeriodo = 'AM';
      }

      // Formatear resultado
      return `${horas}:${minutos.toString().padStart(2, '0')} ${nuevoPeriodo}`;
    } else {
      // Formato 24 horas
      const [horas, minutos] = horaInicio.split(':').map(Number);
      const nuevasHoras = (horas + duracionHoras) % 24;
      return `${nuevasHoras}:${minutos.toString().padStart(2, '0')}`;
    }
  } catch (error) {
    console.error(`Error calculando hora fin:`, error);
    return ""; // Fallback
  }
}

// Convertir fecha en formato espa침ol a formato ISO (YYYY-MM-DD)
function convertirFechaAFormatoISO(fechaTexto) {
  try {
    if (!fechaTexto || typeof fechaTexto !== 'string') {
      throw new Error("Texto de fecha inv치lido");
    }

    // Formato esperado: "dd de mes de yyyy"
    const partes = fechaTexto.split(" de ");
    if (partes.length !== 3) {
      throw new Error(`Formato de fecha inv치lido: ${fechaTexto}`);
    }

    const dia = parseInt(partes[0], 10);
    if (isNaN(dia) || dia < 1 || dia > 31) {
      throw new Error(`D칤a inv치lido: ${partes[0]}`);
    }

    const mes = obtenerNumeroMes(partes[1]);
    if (mes === -1) {
      throw new Error(`Mes inv치lido: ${partes[1]}`);
    }

    const anio = parseInt(partes[2], 10);
    if (isNaN(anio) || anio < 2000 || anio > 2100) {
      throw new Error(`A침o inv치lido: ${partes[2]}`);
    }

    // Formatear como YYYY-MM-DD
    const mesStr = (mes + 1).toString().padStart(2, '0');
    const diaStr = dia.toString().padStart(2, '0');

    return `${anio}-${mesStr}-${diaStr}`;
  } catch (error) {
    console.error(`Error convirtiendo fecha: ${error.message}`);
    return ""; // Cadena vac칤a como fallback
  }
}

// Obtener n칰mero de mes a partir del nombre en espa침ol
function obtenerNumeroMes(nombreMes) {
  if (!nombreMes || typeof nombreMes !== 'string') {
    return -1;
  }

  const nombreMesLower = nombreMes.toLowerCase();
  const meses = [
    "enero", "febrero", "marzo", "abril", "mayo", "junio",
    "julio", "agosto", "septiembre", "octubre", "noviembre", "diciembre"
  ];

  return meses.indexOf(nombreMesLower);
}

// Convertir fecha en formato espa침ol a objeto Date
function parsearFechaEspanol(fechaTexto) {
  try {
    if (!fechaTexto || typeof fechaTexto !== 'string') {
      throw new Error("Texto de fecha inv치lido");
    }

    // Formato esperado: "dd de mes de yyyy"
    const partes = fechaTexto.split(" de ");
    if (partes.length !== 3) {
      throw new Error(`Formato de fecha inv치lido: ${fechaTexto}`);
    }

    const dia = parseInt(partes[0], 10);
    if (isNaN(dia) || dia < 1 || dia > 31) {
      throw new Error(`D칤a inv치lido: ${partes[0]}`);
    }

    const mes = obtenerNumeroMes(partes[1]);
    if (mes === -1) {
      throw new Error(`Mes inv치lido: ${partes[1]}`);
    }

    const anio = parseInt(partes[2], 10);
    if (isNaN(anio) || anio < 2000 || anio > 2100) {
      throw new Error(`A침o inv치lido: ${partes[2]}`);
    }

    return new Date(anio, mes, dia);
  } catch (error) {
    console.error(`Error parseando fecha: ${error.message}`);
    return new Date(); // Fecha actual como fallback
  }
}

// Obtener d칤a de la semana en espa침ol
function obtenerDiaSemanaEspanol(fecha) {
  if (!fecha || !(fecha instanceof Date)) {
    console.error("Fecha inv치lida para obtener d칤a de semana");
    return "";
  }

  const diaSemana = fecha.getDay();

  // El c칩digo original usa d칤as laborables (lunes a viernes)
  // 0 = domingo, 1 = lunes, ..., 6 = s치bado
  if (diaSemana === 0 || diaSemana === 6) {
    console.error("La fecha corresponde a un fin de semana (s치bado o domingo)");
    return "";
  }

  const diasSemana = ["domingo", "lunes", "martes", "mi칠rcoles", "jueves", "viernes", "s치bado"];
  return diasSemana[diaSemana];
}

// Parsear rango de d칤as de una malla
function parsearRangoDias(rangoTexto) {
  try {
    if (!rangoTexto || typeof rangoTexto !== 'string') {
      throw new Error("Texto de rango inv치lido");
    }

    // Formato esperado: "LUNES DD AL VIERNES DD DE MES"
    const partes = rangoTexto.toUpperCase().split(" ");

    // Buscar d칤a inicial
    let diaInicio = null;
    let indexAL = -1;

    for (let i = 0; i < partes.length; i++) {
      if (partes[i] === "AL") {
        indexAL = i;
        break;
      }

      // Intentar convertir a n칰mero
      const posibleDia = parseInt(partes[i], 10);
      if (!isNaN(posibleDia)) {
        diaInicio = posibleDia;
      }
    }

    if (diaInicio === null || indexAL === -1) {
      throw new Error("No se pudo identificar el d칤a de inicio o la palabra 'AL'");
    }

    // Buscar d칤a final
    let diaFin = null;
    let mesIndex = -1;

    for (let i = indexAL + 1; i < partes.length; i++) {
      if (partes[i] === "DE") {
        mesIndex = i;
        break;
      }

      // Intentar convertir a n칰mero
      const posibleDia = parseInt(partes[i], 10);
      if (!isNaN(posibleDia)) {
        diaFin = posibleDia;
      }
    }

    if (diaFin === null || mesIndex === -1 || mesIndex + 1 >= partes.length) {
      throw new Error("No se pudo identificar el d칤a final o el mes");
    }

    const nombreMes = partes[mesIndex + 1].toLowerCase();
    const mes = obtenerNumeroMes(nombreMes);

    if (mes === -1) {
      throw new Error(`Mes inv치lido: ${nombreMes}`);
    }

    const anio = new Date().getFullYear();

    return {
      inicio: new Date(anio, mes, diaInicio),
      fin: new Date(anio, mes, diaFin)
    };
  } catch (error) {
    console.error(`Error parseando rango: ${error.message}`);

    // Rango amplio como fallback
    const hoy = new Date();
    const finDeMes = new Date(hoy.getFullYear(), hoy.getMonth() + 1, 0);

    return {
      inicio: hoy,
      fin: finDeMes
    };
  }
}

// Devolver la funci칩n principal para n8n
return validarDisponibilidadCitaEnCronograma(items);

