  ordenCompra = "",
  peso = "",
  categoria = "",
  unidades = ""
} = datosCita;

// Obtener el d칤a de la semana a partir de la fecha
function obtenerDiaSemana(fechaStr) {
  // Extrae el d칤a, mes y a침o de la cadena
  const regex = /(\d+)\s+de\s+(\w+)\s+de\s+(\d+)/;
  const match = regex.exec(fechaStr);

  if (!match) return null;

  const dia = parseInt(match[1], 10);
  const mesStr = match[2].toLowerCase();
  const a침o = parseInt(match[3], 10);

  // Mapeo de nombres de meses a n칰meros
  const meses = {
    "enero": 0,
    "febrero": 1,
    "marzo": 2,
    "abril": 3,
    "mayo": 4,
    "junio": 5,
    "julio": 6,
    "agosto": 7,
    "septiembre": 8,
    "octubre": 9,
    "noviembre": 10,
    "diciembre": 11
  };

  const mesNum = meses[mesStr];

  if (mesNum === undefined) return null;

  // Crear objeto Date
  const fecha = new Date(a침o, mesNum, dia);

  // Obtener d칤a de la semana (0=domingo, 1=lunes, ..., 6=s치bado)
  const diaSemana = fecha.getDay();

  // Convertir a nombre en espa침ol
  const diasSemana = ["domingo", "lunes", "martes", "mi칠rcoles", "jueves", "viernes"];

  return diasSemana[diaSemana];
}

// Determinar el d칤a de la semana
const diaSemana = obtenerDiaSemana(fecha);

// Determinar el muelle (muelle1 o muelle2)
const muelleKey = muelle.toLowerCase().includes("1") ? "muelle1" : "muelle2";

// Mapeo correcto de columnas para cada d칤a y muelle seg칰n lo proporcionado
const mapeoColumnas = {
  'lunes': {
    'muelle1': 'C', 
    'muelle2': 'F' 
  },
  'martes': {
    'muelle1': 'I', 
    'muelle2': 'L'  
  },
  'mi칠rcoles': {
    'muelle1': 'O', 
    'muelle2': 'R'  
  },
  'jueves': {
    'muelle1': 'U', 
    'muelle2': 'X' 
  },
  'viernes': {
    'muelle1': 'AA', 
    'muelle2': 'AD'  
  }
};

// Verificar si tenemos mapeo para este d칤a
if (!mapeoColumnas[diaSemana]) {
  console.log(`D칤a no soportado: ${diaSemana}`);
  return {
    json: {
      success: false,
      mensaje: `D칤a no soportado: ${diaSemana}`,
      error: "DIA_NO_SOPORTADO"
    }
  };
}

// Obtener la columna base para el d칤a y muelle espec칤ficos
const columnaBase = mapeoColumnas[diaSemana][muelleKey];
if (!columnaBase) {
  console.log(`Configuraci칩n no soportada: d칤a=${diaSemana}, muelle=${muelleKey}`);
  return {
