      error: "CONFIGURACION_NO_SOPORTADA"
    }
  };
}

// Calcular la siguiente columna en Excel
function siguienteColumna(columna) {
  if (columna.length === 1) {
    // Para columnas de una letra (A-Z)
    return String.fromCharCode(columna.charCodeAt(0) + 1);
  } else {
    // Para columnas de dos letras (AA-ZZ)
    const primerChar = columna.charAt(0);
    const segundoChar = columna.charAt(1);

    if (segundoChar === 'Z') {
      // Si es 'Z', pasar a la siguiente letra para el primer car치cter
      return String.fromCharCode(primerChar.charCodeAt(0) + 1) + 'A';
    } else {
      // Incrementar solo el segundo car치cter
      return primerChar + String.fromCharCode(segundoChar.charCodeAt(0) + 1);
    }
  }
}

// Funci칩n para comparar horas de forma flexible
function horasIguales(h1, h2) {
  const normalizar = (h) =>
    h
      .toString()
      .trim()
      .toLowerCase()
      .replace(/\./g, '')            // quita puntos (por si viene "a.m.")
      .replace(/\s+/g, '')           // quita espacios
      .replace(/^0+/, '');           // quita ceros a la izquierda
  return normalizar(h1) === normalizar(h2);
}

// Ordenar franjas por hora
function obtenerFranjasOrdenadas(franjas) {
  return [...franjas].sort((a, b) => {
    // Convertir horas a formato comparable (n칰mero de minutos desde medianoche)
    function horaAMinutos(hora) {
      const match = hora.match(/(\d+):(\d+)\s*([AP]M)/i);
      if (!match) return 0;

      let horas = parseInt(match[1], 10);
      const minutos = parseInt(match[2], 10);
      const periodo = match[3].toUpperCase();

      // Convertir a formato 24 horas
      if (periodo === 'PM' && horas !== 12) {
        horas += 12;
      } else if (periodo === 'AM' && horas === 12) {
        horas = 0;
      }

      return horas * 60 + minutos;
    }

    return horaAMinutos(a.hora) - horaAMinutos(b.hora);
  });
}

// Obtener la posici칩n de la franja en el array ordenado
function obtenerPosicionFranja(hora, franjas) {
  const franjasOrdenadas = obtenerFranjasOrdenadas(franjas);
  return franjasOrdenadas.findIndex(f => horasIguales(f.hora, hora));
}

// Funci칩n corregida para calcular la fila Excel basada en la posici칩n de la franja
function calcularFilaExcel(hora, franjas) {
  const posicion = obtenerPosicionFranja(hora, franjas);

  if (posicion === -1) {
    console.log(`No se encontr칩 la posici칩n para la hora ${hora}`);
    return null;
  }

  // Mapeo de posiciones a filas base en Excel
  // Basado en las observaciones del Excel:
  const FILA_PRIMERA_FRANJA = 12;  // 6:00 AM
  const INCREMENTO_ESTANDAR = 3;   // Incremento est치ndar entre franjas

  // Caso especial para 9:00 AM
  if (posicion === 3) {
    return 21;  // Fila exacta para 9:00 AM
  } else if (posicion === 4) {
    return 24;  // Fila exacta para 10:00 AM
  } else if (posicion === 5) {
    return 27;  // Fila exacta para 11:00 AM
  } else if (posicion < 3) {
    // Para posiciones antes de 9:00 AM, usamos el incremento est치ndar
    return FILA_PRIMERA_FRANJA + (posicion * INCREMENTO_ESTANDAR);
  } else {
    // Para posiciones despu칠s de 11:00 AM, calculamos a partir de 11:00 AM
    return 27 + ((posicion - 5) * INCREMENTO_ESTANDAR);
  }
}

// Funci칩n para encontrar la franja horaria siguiente
function encontrarSiguienteFranja(horaActual, franjas) {
  const franjasOrdenadas = obtenerFranjasOrdenadas(franjas);
  const posicionActual = franjasOrdenadas.findIndex(f => horasIguales(f.hora, horaActual));

  if (posicionActual === -1 || posicionActual >= franjasOrdenadas.length - 1) {
