// Si el input viene del nodo anterior de limpiar celda, extraer el dato original
const datoOriginal = input._metadata?.datoOriginal || input;

// Verificar que tenemos la informaci칩n necesaria
if (!datoOriginal || !datoOriginal.celdaOriginal) {
  console.log('Informaci칩n insuficiente para reintentar la inserci칩n');
  return {
    json: {
      error: "INFORMACION_INSUFICIENTE",
      mensaje: "No hay suficiente informaci칩n para reintentar la inserci칩n"
    }
  };
}

// Obtener la celda correcta donde insertar y el valor a insertar
const celdaCorrecta = datoOriginal.celdaOriginal;

// Intentar obtener el valor correcto a insertar
let valorAInsertar;

// Primero, verificar si tenemos el valor de la respuesta original
if (datoOriginal.valorInsertado !== undefined) {
  valorAInsertar = datoOriginal.valorInsertado;
} 
// Si no, intentar obtenerlo del body de la respuesta
else if (datoOriginal.respuestaOriginal?.body?.values?.[0]?.[0] !== undefined) {
  valorAInsertar = datoOriginal.respuestaOriginal.body.values[0][0];
} 
// Como 칰ltimo recurso, usar un valor vac칤o
else {
  valorAInsertar = "";
  console.log('No se pudo determinar el valor a insertar, usando valor vac칤o');
}

// Determinar el tipo de valor y formatearlo adecuadamente
let valorFormateado;
if (typeof valorAInsertar === 'number') {
  valorFormateado = [[valorAInsertar]]; // Mantener el tipo num칠rico
} else {
  valorFormateado = [[valorAInsertar.toString()]]; // Convertir a string para otros tipos
}

// Obtener la hoja de Excel - primero del dato original
let hojaExcel = datoOriginal.nombreHoja || ""; 

// Si no est치 disponible en el objeto principal, intentar obtenerlo de otros lugares
if (!hojaExcel || hojaExcel === "") {
  // Verificar si est치 en el _metadata
  hojaExcel = input._metadata?.hoja || "";

  // Como 칰ltimo recurso, extraer de la direcci칩n en la respuesta
  if ((!hojaExcel || hojaExcel === "") && datoOriginal.respuestaOriginal?.body?.address) {
    const direccionCompleta = datoOriginal.respuestaOriginal.body.address;
    hojaExcel = direccionCompleta.split('!')[0].replace(/'/g, '');
  }
}

console.log(`Reintentando inserci칩n en celda ${celdaCorrecta} de la hoja ${hojaExcel} con valor "${valorAInsertar}"`);

// Crear la solicitud para reintentar la inserci칩n
const requestBody = {
  requests: [
    {
      id: `Reintento_${celdaCorrecta}_${Date.now()}`,
      method: "PATCH",
      url: `/sites/${siteId}/drives/${driveId}/items/${itemId}/workbook/worksheets/${encodeURIComponent(hojaExcel)}/range(address='${celdaCorrecta}')`,
      body: {
        values: valorFormateado
      },
      headers: {
        "Content-Type": "application/json"
      }
    }
  ],
  _metadata: {
    accion: "reintentar",
    celda: celdaCorrecta,
    hoja: hojaExcel,
    valor: valorAInsertar,
    datoOriginal: datoOriginal,
    intentos: (datoOriginal._metadata?.intentos || 0) + 1
  }
};

// Retornar el request para reintentar la inserci칩n
return {
  json: requestBody
};

// C칩digo para verificar si la inserci칩n fue exitosa (sin acciones correctivas)
const input = $input.first().json;

// Verificar que tenemos la estructura esperada en la respuesta
if (!input || !input.responses || !Array.isArray(input.responses) || input.responses.length === 0) {
  console.log('Estructura de datos de respuesta no v치lida');
  return {
    json: {
      error: "RESPUESTA_INVALIDA",
      mensaje: "La estructura de datos de la respuesta no es v치lida",
      exito: false
    }
  };
}

// Obtener la primera respuesta
const response = input.responses[0];

// Extraer informaci칩n de la solicitud original del ID
// El formato del ID es: "CELDA_INDICE_TIMESTAMP"
const idPartes = response.id.split('_');
const celdaOriginal = idPartes[0];
const indice = idPartes[1];

// Verificar que la respuesta tiene un cuerpo
if (!response.body || !response.body.address) {
  console.log(`Error: La respuesta no contiene informaci칩n sobre la direcci칩n de celda`);
  return {
    json: {
      error: "RESPUESTA_SIN_DIRECCION",
      mensaje: "La respuesta no incluye informaci칩n sobre la direcci칩n de celda",
      requestId: response.id,
      status: response.status,
      celdaOriginal: celdaOriginal,
      exito: false
    }
  };
}

// Extraer la direcci칩n de celda de la respuesta
let direccionRespuesta = "";
if (response.body && response.body.address) {
  const partesDireccion = response.body.address.split('!');
  if (partesDireccion.length > 1) {
    direccionRespuesta = partesDireccion[1];
  }
}

// Obtener el valor insertado de la respuesta
const valorInsertado = response.body.values && response.body.values[0] && response.body.values[0][0];

// Verificar si la inserci칩n se realiz칩 en la celda correcta
const insercionCorrecta = direccionRespuesta === celdaOriginal;

// Verificar el c칩digo de estado HTTP
const estadoExitoso = response.status >= 200 && response.status < 300;

// Crear objeto para almacenar el resultado de la verificaci칩n
const resultado = {
  exito: insercionCorrecta && estadoExitoso,
  celdaOriginal: celdaOriginal,
  celdaInsertada: direccionRespuesta,
  valorInsertado: valorInsertado,
  statusCode: response.status,
