// Funci칩n principal que procesa los datos de entrada
function procesarDatosReprogramacion(datos) {
  try {
    // Estructura de respuesta final (siguiendo el formato del prompt original)
    const respuesta = {
      todoCorrecto: false,
      datosPreviamenteConfirmados: false,
      idCita: datos.idCita || "",
      ordenCompra: datos.ordenCompra || "",
      nuevaFecha: datos.nuevaFecha || "",
      nuevaHora: datos.nuevaHora || "",
