      mensajeSistema: "",
      rechazoTemporal: false,
      motivoRechazo: "",
      intentaModificarDatos: false
    };

    console.log("Datos iniciales en respuesta:", JSON.stringify(respuesta, null, 2));

    // 1. Verificar si hay campos faltantes
    if (respuesta.camposFaltantes && respuesta.camposFaltantes.length > 0) {
      respuesta.todoCorrecto = false;
      respuesta.mensajeSistema = generarMensajeCamposFaltantes(respuesta.camposFaltantes);
      return respuesta;
    }

    // 2. Validar las fechas y reglas temporales
    const validacionTemporal = validarReglasTemporales(respuesta.nuevaFecha, datos.fechaActual, datos.horaActual);
    console.log("Resultado de validaci칩n temporal:", JSON.stringify(validacionTemporal, null, 2));

    if (validacionTemporal.esValido === false) {
      respuesta.todoCorrecto = false;
      respuesta.rechazoTemporal = true;
      respuesta.motivoRechazo = validacionTemporal.motivoRechazo;
      respuesta.mensajeSistema = validacionTemporal.mensaje;
      return respuesta;
    }

    // 3. Si todo est치 correcto, preparar mensaje de confirmaci칩n
    respuesta.todoCorrecto = true;
    respuesta.mensajeSistema = `Entendido. Est치s solicitando reprogramar la cita 游댔 ${respuesta.idCita} con orden de compra ${respuesta.ordenCompra} para el ${respuesta.nuevaFecha} a las ${respuesta.nuevaHora}. Verificar칠 la disponibilidad para este horario.`;

    return respuesta;
  } catch (error) {
    console.error("Error al procesar datos de reprogramaci칩n:", error);
    return {
      todoCorrecto: false,
      datosPreviamenteConfirmados: false,
      idCita: datos.idCita || "",
      ordenCompra: datos.ordenCompra || "",
