function obtenerIdFranja(item, idx) {
  // Si existe 'indice', 칰salo; de lo contrario agrupa cada 5 칤tems
  if (item.json && item.json.indice !== undefined && item.json.indice !== null) {
    return String(item.json.indice).trim();
  }
  // +1 para que las franjas queden 1-basadas
  return String(Math.floor(idx / 5) + 1);
}

// 3) Agrupar 칤tems por franja
const franjas = {};
items.forEach((item, idx) => {
  const idFranja = obtenerIdFranja(item, idx);
  if (!franjas[idFranja]) {
    franjas[idFranja] = { id: idFranja, items: [] };
  }
  franjas[idFranja].items.push(item.json);
});

// 4) Analizar cada franja y construir el detalle
const detallesPorFranja = Object.values(franjas).map(franja => {
  const totalOps   = franja.items.length;
  const exitosas   = franja.items.filter(i =>
    i.exito === true &&
    i.statusCode === 200 &&
    i.falloEstado === false
  ).length;

  const fallidas   = totalOps - exitosas;
  const exitoTotal = fallidas === 0;

  // Celdas con error para un diagn칩stico claro
  const celdasFallidas = franja.items
    .filter(i => !(i.exito && i.statusCode === 200 && !i.falloEstado))
    .map(i => ({
      celdaOriginal   : i.celdaOriginal,
      celdaInsertada  : i.celdaInsertada,
      statusCode      : i.statusCode,
      falloEstado     : i.falloEstado,
      celdasDiferentes: i.celdasDiferentes,
      mensaje         : 'Inserci칩n fallida'
    }));

  return {
    idFranja                : franja.id,
    exitoFranja             : exitoTotal,
    operacionesTotales      : totalOps,
    operacionesExitosas     : exitosas,
    operacionesFallidas     : fallidas,
    porcentajeExitosas      : ((exitosas / totalOps) * 100).toFixed(2) + '%',
    celdasFallidas
  };
});

// 5) Resumen global
const operacionesTotales   = items.length;
const operacionesExitosas  = detallesPorFranja.reduce((sum, f) => sum + f.operacionesExitosas, 0);
const operacionesFallidas  = operacionesTotales - operacionesExitosas;

const franjasTotales   = detallesPorFranja.length;
const franjasExitosas  = detallesPorFranja.filter(f => f.exitoFranja).length;
const franjasFallidas  = franjasTotales - franjasExitosas;


const resumen = {
  estadoGeneral              : franjasFallidas === 0 ? '칄XITO_COMPLETO' : '칄XITO_PARCIAL',
  franjasTotales,
  franjasExitosas,
  franjasFallidas,
  porcentajeFranjasExitosas  : ((franjasExitosas / franjasTotales) * 100).toFixed(2) + '%',
  operacionesTotales,
  operacionesExitosas,
  operacionesFallidas,
  porcentajeOperacionesExitosas : ((operacionesExitosas / operacionesTotales) * 100).toFixed(2) + '%',
  detallesPorFranja
};

// 6) Devolver un 칰nico 칤tem con el resumen
return [
  {
    json: resumen
  }
];

